                (let [#_"int" max (.. this (getMaxConnections))]
                    (if (<= max 1)
                        (§ return max)
                        (§ return (int (Math/round (* (.. this (getMaxConnections)) 0.8))))
                    )
                )
            )
            (:min-broadcast-connections this)
            (finally
                (.. (:lock this) (unlock))
            )
        )
    )

    ;;;
     ; See {@link org.bitcoinj.core.PeerGroup#getMinBroadcastConnections()}.
