            (§ assoc this :nonce (.. this (readInt64)))
            (§ assoc this :has-nonce true)
            (catch ProtocolException e
                (§ assoc this :has-nonce false)
            )
        )
        (§ assoc this :length (if (:has-nonce this) 8 0))
        nil
    )

