    #_public
    (§ method #_"boolean" isEveryOwnedOutputSpent [#_"TransactionBag" __transactionBag]
        (doseq [#_"TransactionOutput" output (:outputs this)]
            (when (and (.. output (isAvailableForSpending)) (.. output (isMine __transactionBag)))
                (§ return false)
            )
        )

        true
    )

