    (§ method #_"int" getSigOpCount []
        (let [#_"int" __sigOps 0]
            (doseq [#_"TransactionInput" input (:inputs this)]
                (§ ass __sigOps (+ __sigOps (Script'getSigOpCount (.. input (getScriptBytes)))))
            )
            (doseq [#_"TransactionOutput" output (:outputs this)]
