    #_public
    (§ method #_"Date" estimateLockTime [#_"AbstractBlockChain" chain]
        (if (< (:lock-time this) Transaction'LOCKTIME_THRESHOLD) (.. chain (estimateBlockTime (int (.. this (getLockTime))))) (Date. (* (.. this (getLockTime)) 1000)))
    )

