        (and (nil? (:priv this)) (or (.. super (isEncrypted)) (and (some? (:parent this)) (.. (:parent this) (isEncrypted)))))
    )

    ;;;
     ; Returns this keys {@link org.bitcoinj.crypto.KeyCrypter} <b>or</b> the keycrypter of its parent key.
     ;;
    #_override
    #_nilable
    #_public
