        (when (and (some? (:key-crypter this)) (not (.. (:key-crypter this) (equals __keyCrypter))))
            (throw (KeyCrypterException. "The keyCrypter being used to decrypt the key is different to the one that was used to encrypt it"))
        )

        (let [#_"BigInteger" __privKey (.. this (findOrDeriveEncryptedPrivateKey __keyCrypter, __aesKey))
              #_"DeterministicKey" key (DeterministicKey. (:child-number-path this), (:chain-code this), __privKey, (:parent this))]
            (when (not (Arrays/equals (.. key (getPubKey)), (.. this (getPubKey))))
                (throw (KeyCrypterException. "Provided AES key is wrong"))
            )

            (when (nil? (:parent this))
                (.. key (setCreationTimeSeconds (.. this (getCreationTimeSeconds))))
            )
            key
        )
    )

    #_override
    #_public
    #_throws #_[ "KeyCrypterException" ]
    (§ method #_"DeterministicKey" decrypt [#_"KeyParameter" __aesKey]
        (cast DeterministicKey (.. super (decrypt __aesKey)))
    )

    ;; For when a key is encrypted, either decrypt our encrypted private key bytes, or work up the tree asking parents
    ;; to decrypt and re-derive.
    #_private
    (§ method- #_"BigInteger" findOrDeriveEncryptedPrivateKey [#_"KeyCrypter" __keyCrypter, #_"KeyParameter" __aesKey]
        (when (some? (:encrypted-private-key this))
            (§ return (BigInteger. 1, (.. __keyCrypter (decrypt (:encrypted-private-key this), __aesKey))))
        )

        ;; Otherwise we don't have it, but maybe we can figure it out from our parents.  Walk up the tree looking for
        ;; the first key that has some encrypted private key data.
        (let [#_"DeterministicKey" cursor (:parent this)]
            (while (some? cursor)
                (when (some? (:encrypted-private-key cursor))
                    (§ break )
                )
                (§ ass cursor (:parent cursor))
            )
            (when (nil? cursor)
                (throw (KeyCrypterException. "Neither this key nor its parents have an encrypted private key"))
            )

            (let [#_"byte[]" __parentalPrivateKeyBytes (.. __keyCrypter (decrypt (:encrypted-private-key cursor), __aesKey))]
                (.. this (derivePrivateKeyDownwards cursor, __parentalPrivateKeyBytes))
            )
        )
    )

    #_private
    (§ method- #_"DeterministicKey" findParentWithPrivKey []
        (let [#_"DeterministicKey" cursor this]
            (while (some? cursor)
                (when (some? (:priv cursor))
                    (§ break )
                )
                (§ ass cursor (:parent cursor))
            )
            cursor
        )
    )

    #_nilable
    #_private
    (§ method- #_"BigInteger" findOrDerivePrivateKey []
        (let [#_"DeterministicKey" cursor (.. this (findParentWithPrivKey))]
            (when (nil? cursor)
                (§ return nil)
            )

            (.. this (derivePrivateKeyDownwards cursor, (.. (:priv cursor) (toByteArray))))
        )
    )

    #_private
    (§ method- #_"BigInteger" derivePrivateKeyDownwards [#_"DeterministicKey" cursor, #_"byte[]" __parentalPrivateKeyBytes]
        (let [#_"DeterministicKey" __downCursor (DeterministicKey. (:child-number-path cursor), (:chain-code cursor), (:pub cursor), (BigInteger. 1, __parentalPrivateKeyBytes), (:parent cursor))]
            ;; Now we have to rederive the keys along the path back to ourselves.  That path can be found by just truncating
            ;; our path with the length of the parents path.
            (let [#_"ImmutableList<ChildNumber>" path (.. (:child-number-path this) (subList (.. cursor (getPath) (size)), (.. (:child-number-path this) (size))))]
                (doseq [#_"ChildNumber" num path]
                    (§ ass __downCursor (HDKeyDerivation'deriveChildKey __downCursor, num))
                )
                ;; downCursor is now the same key as us, but with private key bytes.
                ;; If it's not, it means we tried decrypting with an invalid password and earlier checks e.g. for padding didn't
                ;; catch it.
                (when (not (.. (:pub __downCursor) (equals (:pub this))))
                    (throw (KeyCrypterException. "Could not decrypt bytes"))
                )

                (Preconditions/checkNotNull (:priv __downCursor))
            )
        )
    )

    ;;;
     ; Derives a child at the given index using hardened derivation.  Note: <code>index</code>
     ; is not the "i" value.  If you want the softened derivation, then use instead
     ; <code>HDKeyDerivation.deriveChildKey(this, new ChildNumber(child, false))</code>.
     ;;
    #_public
    (§ method #_"DeterministicKey" derive [#_"int" child]
        (HDKeyDerivation'deriveChildKey this, (ChildNumber. child, true))
    )

    ;;;
     ; Returns the private key of this deterministic key.  Even if this object isn't storing the private key,
     ; it can be re-derived by walking up to the parents if necessary and this is what will happen.
     ; @throws java.lang.IllegalStateException if the parents are encrypted or a watching chain.
     ;;
    #_override
    #_public
    (§ method #_"BigInteger" getPrivKey []
        (let [#_"BigInteger" key (.. this (findOrDerivePrivateKey))]
            (Preconditions/checkState (some? key), "Private key bytes not available")
            key
        )
    )

    #_public
    (§ method #_"byte[]" serializePublic [#_"NetworkParameters" params]
        (.. this (serialize params, true))
    )

    #_public
    (§ method #_"byte[]" serializePrivate [#_"NetworkParameters" params]
        (.. this (serialize params, false))
    )

    #_private
    (§ method- #_"byte[]" serialize [#_"NetworkParameters" params, #_"boolean" pub]
        (let [#_"ByteBuffer" ser (ByteBuffer/allocate 78)]
            (.. ser (putInt (if pub (.. params (getBip32HeaderPub)) (.. params (getBip32HeaderPriv)))))
            (.. ser (put (byte (.. this (getDepth)))))
            (.. ser (putInt (.. this (getParentFingerprint))))
            (.. ser (putInt (.. this (getChildNumber) (i))))
            (.. ser (put (.. this (getChainCode))))
            (.. ser (put (if pub (.. this (getPubKey)) (.. this (getPrivKeyBytes33)))))
            (Preconditions/checkState (= (.. ser (position)) 78))
            (.. ser (array))
        )
    )

    #_public
    (§ method #_"String" serializePubB58 [#_"NetworkParameters" params]
        (DeterministicKey'toBase58 (.. this (serialize params, true)))
    )

    #_public
    (§ method #_"String" serializePrivB58 [#_"NetworkParameters" params]
        (DeterministicKey'toBase58 (.. this (serialize params, false)))
    )

    #_static
    (§ defn #_"String" DeterministicKey'toBase58 [#_"byte[]" ser]
        (Base58'encode (DeterministicKey'addChecksum ser))
    )

    ;;; Deserialize a base-58-encoded HD Key with no parent. ;;
    #_public
    #_static
    (§ defn #_"DeterministicKey" DeterministicKey'deserializeB58 [#_"String" base58, #_"NetworkParameters" params]
        (DeterministicKey'deserializeB58 nil, base58, params)
    )

    ;;;
     ; Deserialize a base-58-encoded HD Key.
     ; @param parent The parent node in the given key's deterministic hierarchy.
     ; @throws IllegalArgumentException if the base58 encoded key could not be parsed.
     ;;
    #_public
    #_static
    (§ defn #_"DeterministicKey" DeterministicKey'deserializeB58 [#_nilable #_"DeterministicKey" parent, #_"String" base58, #_"NetworkParameters" params]
        (DeterministicKey'deserialize params, (Base58'decodeChecked base58), parent)
    )

    ;;;
     ; Deserialize an HD Key with no parent.
     ;;
    #_public
    #_static
    (§ defn #_"DeterministicKey" DeterministicKey'deserialize [#_"NetworkParameters" params, #_"byte[]" __serializedKey]
        (DeterministicKey'deserialize params, __serializedKey, nil)
    )

    ;;;
     ; Deserialize an HD Key.
     ; @param parent The parent node in the given key's deterministic hierarchy.
     ;;
    #_public
    #_static
    (§ defn #_"DeterministicKey" DeterministicKey'deserialize [#_"NetworkParameters" params, #_"byte[]" __serializedKey, #_nilable #_"DeterministicKey" parent]
        (let [#_"ByteBuffer" buffer (ByteBuffer/wrap __serializedKey)
              #_"int" header (.. buffer (getInt))]
            (when (and (not= header (.. params (getBip32HeaderPriv))) (not= header (.. params (getBip32HeaderPub))))
                (throw (IllegalArgumentException. (str "Unknown header bytes: " (.. (DeterministicKey'toBase58 __serializedKey) (substring 0, 4)))))
            )

            (let [#_"boolean" pub (= header (.. params (getBip32HeaderPub)))
                  #_"int" depth (& (.. buffer (get)) 0xff)] ;; convert signed byte to positive int since depth cannot be negative
                (let [#_"int" __parentFingerprint (.. buffer (getInt))
                      #_"int" i (.. buffer (getInt))
                      #_"ChildNumber" __childNumber (ChildNumber. i)
                      #_"ImmutableList<ChildNumber>" path]
                    (cond (some? parent)
                        (do
                            (when (= __parentFingerprint 0)
                                (throw (IllegalArgumentException. "Parent was provided but this key doesn't have one"))
                            )
                            (when (not= (.. parent (getFingerprint)) __parentFingerprint)
                                (throw (IllegalArgumentException. "Parent fingerprints don't match"))
                            )
                            (§ ass path (HDUtils'append (.. parent (getPath)), __childNumber))
                            (when (not= (.. path (size)) depth)
                                (throw (IllegalArgumentException. "Depth does not match"))
                            )
                        )
                        :else
                        (do
                            ;; We have been given a key that is not a root key, yet we lack the object representing the parent.
                            ;; This can happen when deserializing an account key for a watching wallet.  In this case, we assume
                            ;; that the client wants to conceal the key's position in the hierarchy.  The path is truncated at
                            ;; the parent's node.
                            (§ ass path (if (<= 1 depth) (ImmutableList/of __childNumber) (ImmutableList/of)))
                        )
                    )
                    (let [#_"byte[]" __chainCode (byte-array 32)]
                        (.. buffer (get __chainCode))
                        (let [#_"byte[]" data (byte-array 33)]
                            (.. buffer (get data))
                            (Preconditions/checkArgument (not (.. buffer (hasRemaining))), "Found unexpected data in key")
                            (if pub
                                (§ return (DeterministicKey. path, __chainCode, (LazyECPoint. (.. ECKey'CURVE (getCurve)), data), parent, depth, __parentFingerprint))
                                (§ return (DeterministicKey. path, __chainCode, (BigInteger. 1, data), parent, depth, __parentFingerprint))
                            )
                        )
                    )
                )
            )
        )
    )

    ;;;
     ; The creation time of a deterministic key is equal to that of its parent, unless this key is the root of a tree
     ; in which case the time is stored alongside the key as per normal, see {@link org.bitcoinj.core.ECKey#getCreationTimeSeconds()}.
     ;;
    #_override
    #_public
    (§ method #_"long" getCreationTimeSeconds []
        (if (some? (:parent this))
            (§ return (.. (:parent this) (getCreationTimeSeconds)))
            (§ return (.. super (getCreationTimeSeconds)))
        )
    )

    ;;;
     ; The creation time of a deterministic key is equal to that of its parent, unless this key is the root of a tree.
     ; Thus, setting the creation time on a leaf is forbidden.
     ;;
    #_override
    #_public
    (§ method #_"void" setCreationTimeSeconds [#_"long" __newCreationTimeSeconds]
        (if (some? (:parent this))
            (throw (IllegalStateException. "Creation time can only be set on root keys."))
            (.. super (setCreationTimeSeconds __newCreationTimeSeconds))
        )
        nil
    )

    ;;;
     ; Verifies equality of all fields but NOT the parent pointer, thus the same key derived in two separate heirarchy
     ; objects will equal each other.
     ;;
    #_override
    #_public
    (§ method #_"boolean" equals [#_"Object" o]
        (when (= this o)
            (§ return true)
        )
        (when (or (nil? o) (not= (getClass) (.. o (getClass))))
            (§ return false)
        )
        (let [#_"DeterministicKey" other (cast DeterministicKey o)]
            (and (.. super (equals other)) (Arrays/equals (:chain-code this), (:chain-code other)) (Objects/equal (:child-number-path this), (:child-number-path other)))
        )
    )

    #_override
    #_public
    (§ method #_"int" hashCode []
        (Objects/hashCode (.. super (hashCode)), (Arrays/hashCode (:chain-code this)), (:child-number-path this))
    )

    #_override
    #_public
    (§ method #_"String" toString []
        (let [#_"MoreObjects.ToStringHelper" helper (.. (MoreObjects/toStringHelper this) (omitNullValues))]
            (.. helper (add "pub", (.. Utils'HEX (encode (.. (:pub this) (getEncoded))))))
            (.. helper (add "chainCode", (.. Utils'HEX (encode (:chain-code this)))))
            (.. helper (add "path", (.. this (getPathAsString))))
            (when (< 0 (:creation-time-seconds this))
                (.. helper (add "creationTimeSeconds", (:creation-time-seconds this)))
            )
            (.. helper (add "isEncrypted", (.. this (isEncrypted))))
            (.. helper (add "isPubKeyOnly", (.. this (isPubKeyOnly))))
            (.. helper (toString))
        )
    )

    #_override
    #_public
    (§ method #_"void" formatKeyWithAddress [#_"boolean" __includePrivateKeys, #_"StringBuilder" sb, #_"NetworkParameters" params]
        (let [#_"Address" address (.. this (toAddress params))]
            (.. sb (append "  addr:") (append address))
            (.. sb (append "  hash160:") (append (.. Utils'HEX (encode (.. this (getPubKeyHash))))))
            (.. sb (append "  (") (append (.. this (getPathAsString))) (append ")\n"))
            (when __includePrivateKeys
                (.. sb (append "  ") (append (.. this (toStringWithPrivate params))) (append "\n"))
            )
            nil
        )
    )
)

;;;
 ; Provides a uniform way to access something that can be optionally encrypted with a
 ; {@link org.bitcoinj.crypto.KeyCrypter}, yielding an {@link org.bitcoinj.crypto.EncryptedData},
 ; and which can have a creation time associated with it.
 ;;
#_public
(§ interface EncryptableItem
    ;;; Returns whether the item is encrypted or not.  If it is, then {@link #getSecretBytes()} will return null. ;;
    (§ method #_"boolean" isEncrypted [])

    ;;; Returns the raw bytes of the item, if not encrypted, or null if encrypted or the secret is missing. ;;
    #_nilable
    (§ method #_"byte[]" getSecretBytes [])

    ;;; Returns the initialization vector and encrypted secret bytes, or null if not encrypted. ;;
    #_nilable
    (§ method #_"EncryptedData" getEncryptedData [])

    ;;; Returns an enum constant describing what algorithm was used to encrypt the key or UNENCRYPTED. ;;
    (§ method #_"Protos.Wallet.EncryptionType" getEncryptionType [])

    ;;; Returns the time in seconds since the UNIX epoch at which this encryptable item was first created/derived. ;;
    (§ method #_"long" getCreationTimeSeconds [])
)

;;;
 ; <p>An instance of EncryptedData is a holder for an initialization vector and encrypted bytes.
 ; It is typically used to hold encrypted private key bytes.</p>
 ;
 ; <p>The initialisation vector is random data that is used to initialise the AES block cipher when
 ; the private key bytes were encrypted.  You need these for decryption.</p>
 ;;
#_public
(§ class EncryptedData
    #_public
    (§ field #_"byte[]" :initialisation-vector)
    #_public
    (§ field #_"byte[]" :encrypted-bytes)

    #_public
    (§ constructor EncryptedData [#_"byte[]" __initialisationVector, #_"byte[]" __encryptedBytes]
        (§ assoc this :initialisation-vector (Arrays/copyOf __initialisationVector, (alength __initialisationVector)))
        (§ assoc this :encrypted-bytes (Arrays/copyOf __encryptedBytes, (alength __encryptedBytes)))
        this
    )

    #_override
    #_public
    (§ method #_"boolean" equals [#_"Object" o]
        (when (= this o)
            (§ return true)
        )
        (when (or (nil? o) (not= (getClass) (.. o (getClass))))
            (§ return false)
        )
        (let [#_"EncryptedData" other (cast EncryptedData o)]
            (and (Arrays/equals (:encrypted-bytes this), (:encrypted-bytes other)) (Arrays/equals (:initialisation-vector this), (:initialisation-vector other)))
        )
    )

    #_override
    #_public
    (§ method #_"int" hashCode []
        (Objects/hashCode (Arrays/hashCode (:encrypted-bytes this)), (Arrays/hashCode (:initialisation-vector this)))
    )

    #_override
    #_public
    (§ method #_"String" toString []
        (str "EncryptedData [initialisationVector=" (Arrays/toString (:initialisation-vector this)) ", encryptedPrivateKey=" (Arrays/toString (:encrypted-bytes this)) "]")
    )
)

#_public
(§ class HDDerivationException (§ extends RuntimeException)
    #_public
    (§ constructor HDDerivationException [#_"String" message]
        (§ super message)
        this
    )
)

;;;
 ; Implementation of the <a href="https://github.com/bitcoin/bips/blob/master/bip-0032.mediawiki">BIP 32</a>
 ; deterministic wallet child key generation algorithm.
 ;;
#_public
(§ class HDKeyDerivation
    ;; Some arbitrary random number.  Doesn't matter what it is.
    #_private
    #_static
    (def- #_"BigInteger" HDKeyDerivation'RAND_INT (BigInteger. 256, (SecureRandom.)))

    #_private
    (§ constructor- HDKeyDerivation []
        this
    )

    ;;;
     ; Child derivation may fail (although with extremely low probability); in such case it is re-attempted.
     ; This is the maximum number of re-attempts (to avoid an infinite loop in case of bugs etc.)
     ;;
    #_public
    #_static
    (def #_"int" HDKeyDerivation'MAX_CHILD_DERIVATION_ATTEMPTS 100)

    ;;;
     ; Generates a new deterministic key from the given seed, which can be any arbitrary byte array.
     ; However resist the temptation to use a string as the seed - any key derived from a password
     ; is likely to be weak and easily broken by attackers (this is not theoretical, people have had
     ; money stolen that way).  This method checks that the given seed is at least 64 bits long.
     ;
     ; @throws HDDerivationException if generated master key is invalid (private key 0 or >= n).
     ; @throws IllegalArgumentException if the seed is less than 8 bytes and could be brute forced.
     ;;
    #_public
    #_static
    #_throws #_[ "HDDerivationException" ]
    (§ defn #_"DeterministicKey" HDKeyDerivation'createMasterPrivateKey [#_"byte[]" seed]
        (Preconditions/checkArgument (< 8 (alength seed)), "Seed is too short and could be brute forced")

        ;; Calculate I = HMAC-SHA512(key="Bitcoin seed", msg=S).
        (let [#_"byte[]" i (HDUtils'hmacSha512 (HDUtils'createHmacSha512Digest (.. "Bitcoin seed" (getBytes))), seed)]
            ;; Split I into two 32-byte sequences, Il and Ir.
            ;; Use Il as master secret key, and Ir as master chain code.
            (Preconditions/checkState (= (alength i) 64), (alength i))

            (let [#_"byte[]" il (Arrays/copyOfRange i, 0, 32)
                  #_"byte[]" ir (Arrays/copyOfRange i, 32, 64)]
                (Arrays/fill i, (byte 0))
                (let [#_"DeterministicKey" __masterPrivKey (HDKeyDerivation'createMasterPrivKeyFromBytes il, ir)]
                    (Arrays/fill il, (byte 0))
                    (Arrays/fill ir, (byte 0))

                    ;; Child deterministic keys will chain up to their parents to find the keys.
                    (.. __masterPrivKey (setCreationTimeSeconds (Utils'currentTimeSeconds)))
                    __masterPrivKey
                )
            )
        )
    )

    ;;;
     ; @throws HDDerivationException if privKeyBytes is invalid (0 or >= n).
     ;;
    #_public
    #_static
    #_throws #_[ "HDDerivationException" ]
    (§ defn #_"DeterministicKey" HDKeyDerivation'createMasterPrivKeyFromBytes [#_"byte[]" __privKeyBytes, #_"byte[]" __chainCode]
        (let [#_"BigInteger" priv (BigInteger. 1, __privKeyBytes)]
            (HDKeyDerivation'assertNonZero priv, "Generated master key is invalid.")
            (HDKeyDerivation'assertLessThanN priv, "Generated master key is invalid.")
            (DeterministicKey. (ImmutableList/of #_"ImmutableList<ChildNumber>"), __chainCode, priv, nil)
        )
    )

    #_public
    #_static
    (§ defn #_"DeterministicKey" HDKeyDerivation'createMasterPubKeyFromBytes [#_"byte[]" __pubKeyBytes, #_"byte[]" __chainCode]
        (DeterministicKey. (ImmutableList/of #_"ImmutableList<ChildNumber>"), __chainCode, (LazyECPoint. (.. ECKey'CURVE (getCurve)), __pubKeyBytes), nil, nil)
    )

    ;;;
     ; Derives a key given the "extended" child number, i.e. the 0x80000000 bit of the value that you
     ; pass for <code>childNumber</code> will determine whether to use hardened derivation or not.
     ; Consider whether your code would benefit from the clarity of the equivalent, but explicit, form
     ; of this method that takes a <code>ChildNumber</code> rather than an <code>int</code>, for example:
     ; <code>deriveChildKey(parent, new ChildNumber(childNumber, true))</code>
     ; where the value of the hardened bit of <code>childNumber</code> is zero.
     ;;
    #_public
    #_static
    (§ defn #_"DeterministicKey" HDKeyDerivation'deriveChildKey [#_"DeterministicKey" parent, #_"int" __childNumber]
        (HDKeyDerivation'deriveChildKey parent, (ChildNumber. __childNumber))
    )

    ;;;
     ; Derives a key of the "extended" child number, i.e. with the 0x80000000 bit specifying whether
     ; to use hardened derivation or not.  If derivation fails, tries a next child.
     ;;
    #_public
    #_static
    (§ defn #_"DeterministicKey" HDKeyDerivation'deriveThisOrNextChildKey [#_"DeterministicKey" parent, #_"int" __childNumber]
        (let [#_"ChildNumber" child (ChildNumber. __childNumber)
              #_"boolean" __isHardened (.. child (isHardened))]
            (loop-when-recur [#_"int" i 0] (< i HDKeyDerivation'MAX_CHILD_DERIVATION_ATTEMPTS) [(inc i)]
                (try
                    (§ ass child (ChildNumber. (+ (.. child (num)) i), __isHardened))
                    (§ return (HDKeyDerivation'deriveChildKey parent, child))
                    (catch HDDerivationException _
                    )
                )
            )
            (throw (HDDerivationException. "Maximum number of child derivation attempts reached, this is probably an indication of a bug."))
        )
    )

    ;;;
     ; @throws HDDerivationException if private derivation is attempted for a public-only parent key, or
     ; if the resulting derived key is invalid (e.g. private key == 0).
     ;;
    #_public
    #_static
    #_throws #_[ "HDDerivationException" ]
    (§ defn #_"DeterministicKey" HDKeyDerivation'deriveChildKey [#_"DeterministicKey" parent, #_"ChildNumber" __childNumber]
        (cond (not (.. parent (hasPrivKey)))
            (do
                (let [#_"RawKeyBytes" __rawKey (HDKeyDerivation'deriveChildKeyBytesFromPublic parent, __childNumber, :PublicDerivationMode'NORMAL)]
                    (§ return (DeterministicKey. (HDUtils'append (.. parent (getPath)), __childNumber), (:chain-code __rawKey), (LazyECPoint. (.. ECKey'CURVE (getCurve)), (:key-bytes __rawKey)), nil, parent))
                )
            )
            :else
            (do
                (let [#_"RawKeyBytes" __rawKey (HDKeyDerivation'deriveChildKeyBytesFromPrivate parent, __childNumber)]
                    (§ return (DeterministicKey. (HDUtils'append (.. parent (getPath)), __childNumber), (:chain-code __rawKey), (BigInteger. 1, (:key-bytes __rawKey)), parent))
                )
            )
        )
    )

    #_public
    #_static
    #_throws #_[ "HDDerivationException" ]
    (§ defn #_"RawKeyBytes" HDKeyDerivation'deriveChildKeyBytesFromPrivate [#_"DeterministicKey" parent, #_"ChildNumber" __childNumber]
        (Preconditions/checkArgument (.. parent (hasPrivKey)), "Parent key must have private key bytes for this method.")

        (let [#_"byte[]" __parentPublicKey (.. parent (getPubKeyPoint) (getEncoded true))]
            (Preconditions/checkState (= (alength __parentPublicKey) 33), (str "Parent pubkey must be 33 bytes, but is " (alength __parentPublicKey)))

            (let [#_"ByteBuffer" data (ByteBuffer/allocate 37)]
                (.. data (put (if (.. __childNumber (isHardened)) (.. parent (getPrivKeyBytes33)) __parentPublicKey)))
                (.. data (putInt (.. __childNumber (i))))

                (let [#_"byte[]" i (HDUtils'hmacSha512 (.. parent (getChainCode)), (.. data (array)))]
                    (Preconditions/checkState (= (alength i) 64), (alength i))

                    (let [#_"byte[]" il (Arrays/copyOfRange i, 0, 32)
                          #_"byte[]" __chainCode (Arrays/copyOfRange i, 32, 64)
                          #_"BigInteger" __ilInt (BigInteger. 1, il)]
                        (HDKeyDerivation'assertLessThanN __ilInt, "Illegal derived key: I_L >= n")

                        (let [#_"BigInteger" priv (.. parent (getPrivKey))
                              #_"BigInteger" ki (.. priv (add __ilInt) (mod (.. ECKey'CURVE (getN))))]
                            (HDKeyDerivation'assertNonZero ki, "Illegal derived key: derived private key equals 0.")

                            (RawKeyBytes. (.. ki (toByteArray)), __chainCode)
                        )
                    )
                )
            )
        )
    )

    #_public
    #_static
    #_enum
    (def PublicDerivationMode'values
    #{
        :PublicDerivationMode'NORMAL
        :PublicDerivationMode'WITH_INVERSION
    })

    #_public
    #_static
    #_throws #_[ "HDDerivationException" ]
    (§ defn #_"RawKeyBytes" HDKeyDerivation'deriveChildKeyBytesFromPublic [#_"DeterministicKey" parent, #_"ChildNumber" __childNumber, #_"PublicDerivationMode" mode]
        (Preconditions/checkArgument (not (.. __childNumber (isHardened))), "Can't use private derivation with public keys only.")

        (let [#_"byte[]" __parentPublicKey (.. parent (getPubKeyPoint) (getEncoded true))]
            (Preconditions/checkState (= (alength __parentPublicKey) 33), (str "Parent pubkey must be 33 bytes, but is " (alength __parentPublicKey)))

            (let [#_"ByteBuffer" data (ByteBuffer/allocate 37)]
                (.. data (put __parentPublicKey))
                (.. data (putInt (.. __childNumber (i))))

                (let [#_"byte[]" i (HDUtils'hmacSha512 (.. parent (getChainCode)), (.. data (array)))]
                    (Preconditions/checkState (= (alength i) 64), (alength i))

                    (let [#_"byte[]" il (Arrays/copyOfRange i, 0, 32)
                          #_"byte[]" __chainCode (Arrays/copyOfRange i, 32, 64)
                          #_"BigInteger" __ilInt (BigInteger. 1, il)]
                        (HDKeyDerivation'assertLessThanN __ilInt, "Illegal derived key: I_L >= n")

                        (let [#_"BigInteger" __N (.. ECKey'CURVE (getN))
                              #_"ECPoint" __Ki]
                            (condp = mode
                                :PublicDerivationMode'NORMAL
                                    (§ ass __Ki (.. (ECKey'publicPointFromPrivate __ilInt) (add (.. parent (getPubKeyPoint)))))
                                :PublicDerivationMode'WITH_INVERSION
                                    (§ do
                                        ;; This trick comes from Gregory Maxwell.  Check the homomorphic properties of our curve hold.  The
                                        ;; below calculations should be redundant and give the same result as NORMAL but if the precalculated
                                        ;; tables have taken a bit flip will yield a different answer.  This mode is used when vending a key
                                        ;; to perform a last-ditch sanity check trying to catch bad RAM.
                                        (§ ass __Ki (ECKey'publicPointFromPrivate (.. __ilInt (add HDKeyDerivation'RAND_INT) (mod __N))))
                                        (let [#_"BigInteger" __additiveInverse (.. HDKeyDerivation'RAND_INT (negate) (mod __N))]
                                            (§ ass __Ki (Ki/add (ECKey'publicPointFromPrivate __additiveInverse)))
                                            (§ ass __Ki (Ki/add (.. parent (getPubKeyPoint))))
                                        )
                                    )
                                (throw (AssertionError.))
                            )

                            (HDKeyDerivation'assertNonInfinity __Ki, "Illegal derived key: derived public key equals infinity.")
                            (RawKeyBytes. (Ki/getEncoded true), __chainCode)
                        )
                    )
                )
            )
        )
    )

    #_private
    #_static
    (§ defn- #_"void" HDKeyDerivation'assertNonZero [#_"BigInteger" integer, #_"String" __errorMessage]
        (when (.. integer (equals BigInteger/ZERO))
            (throw (HDDerivationException. __errorMessage))
        )
        nil
    )

    #_private
    #_static
    (§ defn- #_"void" HDKeyDerivation'assertNonInfinity [#_"ECPoint" point, #_"String" __errorMessage]
        (when (.. point (equals (.. ECKey'CURVE (getCurve) (getInfinity))))
            (throw (HDDerivationException. __errorMessage))
        )
        nil
    )

    #_private
    #_static
    (§ defn- #_"void" HDKeyDerivation'assertLessThanN [#_"BigInteger" integer, #_"String" __errorMessage]
        (when (> (.. integer (compareTo (.. ECKey'CURVE (getN)))) 0)
            (throw (HDDerivationException. __errorMessage))
        )
        nil
    )

    #_public
    #_static
    (§ class RawKeyBytes
        #_public
        (§ field #_"byte[]" :key-bytes)
        #_public
        (§ field #_"byte[]" :chain-code)

        #_public
        (§ constructor RawKeyBytes [#_"byte[]" __keyBytes, #_"byte[]" __chainCode]
            (§ assoc this :key-bytes __keyBytes)
            (§ assoc this :chain-code __chainCode)
            this
        )
    )
)

;;;
 ; Static utilities used in BIP 32 Hierarchical Deterministic Wallets (HDW).
 ;;
#_public
(§ class HDUtils
    #_private
    #_static
    (def- #_"Joiner" HDUtils'PATH_JOINER (Joiner/on "/"))

    #_static
    (§ defn #_"HMac" HDUtils'createHmacSha512Digest [#_"byte[]" key]
        (let [#_"SHA512Digest" digest (SHA512Digest.)
              #_"HMac" __hMac (HMac. digest)]
            (.. __hMac (init (KeyParameter. key)))
            __hMac
        )
    )

    #_static
    (§ defn #_"byte[]" HDUtils'hmacSha512 [#_"HMac" __hmacSha512, #_"byte[]" input]
        (.. __hmacSha512 (reset))
        (.. __hmacSha512 (update input, 0, (alength input)))
        (let [#_"byte[]" out (byte-array 64)]
            (.. __hmacSha512 (doFinal out, 0))
            out
        )
    )

    #_public
    #_static
    (§ defn #_"byte[]" HDUtils'hmacSha512 [#_"byte[]" key, #_"byte[]" data]
        (HDUtils'hmacSha512 (HDUtils'createHmacSha512Digest key), data)
    )

    #_static
    (§ defn #_"byte[]" HDUtils'toCompressed [#_"byte[]" __uncompressedPoint]
        (.. ECKey'CURVE (getCurve) (decodePoint __uncompressedPoint) (getEncoded true))
    )

    #_static
    (§ defn #_"byte[]" HDUtils'longTo4ByteArray [#_"long" n]
        (let [#_"byte[]" bytes (Arrays/copyOfRange (.. (ByteBuffer/allocate 8) (putLong n) (array)), 4, 8)]
            (assert (= (alength bytes) 4) (alength bytes))
            bytes
        )
    )

    ;;; Append a derivation level to an existing path. ;;
    #_public
    #_static
    (§ defn #_"ImmutableList<ChildNumber>" HDUtils'append [#_"List<ChildNumber>" path, #_"ChildNumber" __childNumber]
        (.. (ImmutableList/builder #_"ImmutableList<ChildNumber>") (addAll path) (add __childNumber) (build))
    )

    ;;; Concatenate two derivation paths. ;;
    #_public
    #_static
    (§ defn #_"ImmutableList<ChildNumber>" HDUtils'concat [#_"List<ChildNumber>" path, #_"List<ChildNumber>" path2]
        (.. (ImmutableList/builder #_"ImmutableList<ChildNumber>") (addAll path) (addAll path2) (build))
    )

    ;;; Convert to a string path, starting with "M/". ;;
    #_public
    #_static
    (§ defn #_"String" HDUtils'formatPath [#_"List<ChildNumber>" path]
        (.. HDUtils'PATH_JOINER (join (Iterables/concat (Collections/singleton "M"), path)))
    )

    ;;;
     ; The path is a human-friendly representation of the deterministic path.  For example:
     ;
     ; "44H / 0H / 0H / 1 / 1"
     ;
     ; Where a letter "H" means hardened key.  Spaces are ignored.
     ;;
    #_public
    #_static
    (§ defn #_"List<ChildNumber>" HDUtils'parsePath [#_non-nil #_"String" path]
        (let [#_"List<ChildNumber>" nodes (ArrayList. #_"<>")]

            (doseq [#_"String" s (.. path (replace "M", "") (split "/"))]
                (let [s (.. s (replaceAll " ", ""))]
                    (when (not= (.. s (length)) 0)
                        (let [#_"boolean" hard? (.. s (endsWith "H")) s (if hard? (.. s (substring 0, (dec (.. s (length))))) s)]
                            (.. nodes (add (ChildNumber. (Integer/parseInt s), hard?)))
                        )
                    )
                )
            )

            nodes
        )
    )
)

;;;
 ; <p>A KeyCrypter can be used to encrypt and decrypt a message.  The sequence of events to encrypt and then decrypt
 ; a message are as follows:</p>
 ;
 ; <p>(1) Ask the user for a password.  deriveKey() is then called to create an KeyParameter.  This contains the AES
 ; key that will be used for encryption.</p>
 ; <p>(2) Encrypt the message using encrypt(), providing the message bytes and the KeyParameter from (1).  This returns
 ; an EncryptedData which contains the encryptedPrivateKey bytes and an initialisation vector.</p>
 ; <p>(3) To decrypt an EncryptedData, repeat step (1) to get a KeyParameter, then call decrypt().</p>
 ;
 ; <p>There can be different algorithms used for encryption/ decryption so the getUnderstoodEncryptionType is used
 ; to determine whether any given KeyCrypter can understand the type of encrypted data you have.</p>
 ;;
#_public
(§ interface KeyCrypter
    ;;;
     ; Return the EncryptionType enum value which denotes the type of encryption/ decryption that this KeyCrypter
     ; can understand.
     ;;
    (§ method #_"Protos.Wallet.EncryptionType" getUnderstoodEncryptionType [])

    ;;;
     ; Create a KeyParameter (which typically contains an AES key).
     ; @param password
     ; @return the KeyParameter which typically contains the AES key to use for encrypting and decrypting.
     ; @throws KeyCrypterException
     ;;
    #_throws #_[ "KeyCrypterException" ]
    (§ method #_"KeyParameter" deriveKey [#_"CharSequence" password])

    ;;;
     ; Decrypt the provided encrypted bytes, converting them into unencrypted bytes.
     ;
     ; @throws KeyCrypterException if decryption was unsuccessful.
     ;;
    #_throws #_[ "KeyCrypterException" ]
    (§ method #_"byte[]" decrypt [#_"EncryptedData" __encryptedBytesToDecode, #_"KeyParameter" __aesKey])

    ;;;
     ; Encrypt the supplied bytes, converting them into ciphertext.
     ;
     ; @return an encryptedPrivateKey containing the encrypted bytes and an initialisation vector.
     ; @throws KeyCrypterException if encryption was unsuccessful.
     ;;
    #_throws #_[ "KeyCrypterException" ]
    (§ method #_"EncryptedData" encrypt [#_"byte[]" __plainBytes, #_"KeyParameter" __aesKey])
)

;;;
 ; <p>Exception to provide the following:</p>
 ; <ul>
 ; <li>Provision of encryption / decryption exception.</li>
 ; </ul>
 ; <p>This base exception acts as a general failure mode not attributable to a specific cause (other than
 ; that reported in the exception message).  Since this is in English, it may not be worth reporting directly
 ; to the user other than as part of a "general failure to parse" response.</p>
 ;;
#_public
(§ class KeyCrypterException (§ extends RuntimeException)
    #_public
    (§ constructor KeyCrypterException [#_"String" s]
        (§ super s)
        this
    )

    #_public
    (§ constructor KeyCrypterException [#_"String" s, #_"Throwable" throwable]
        (§ super s, throwable)
        this
    )
)

;;;
 ; <p>This class encrypts and decrypts byte arrays and strings using scrypt as the
 ; key derivation function and AES for the encryption.</p>
 ;
 ; <p>You can use this class to:</p>
 ;
 ; <p>1. Using a user password, create an AES key that can encrypt and decrypt your private keys.
 ; To convert the password to the AES key, scrypt is used.  This is an algorithm resistant
 ; to brute force attacks.  You can use the ScryptParameters to tune how difficult you
 ; want this to be generation to be.</p>
 ;
 ; <p>2. Using the AES Key generated above, you then can encrypt and decrypt any bytes using
 ; the AES symmetric cipher.  Eight bytes of salt is used to prevent dictionary attacks.</p>
 ;;
#_public
(§ class KeyCrypterScrypt (§ implements KeyCrypter)
    #_private
    #_static
    (def- #_"Logger" KeyCrypterScrypt'log (LoggerFactory/getLogger KeyCrypterScrypt))

    ;;;
     ; Key length in bytes.
     ;;
    #_public
    #_static
    (def #_"int" KeyCrypterScrypt'KEY_LENGTH 32) ;; = 256 bits.

    ;;;
     ; The size of an AES block in bytes.
     ; This is also the length of the initialisation vector.
     ;;
    #_public
    #_static
    (def #_"int" KeyCrypterScrypt'BLOCK_LENGTH 16) ;; = 128 bits.

    ;;;
     ; The length of the salt used.
     ;;
    #_public
    #_static
    (def #_"int" KeyCrypterScrypt'SALT_LENGTH 8)

    #_private
    #_static
    (def- #_"SecureRandom" KeyCrypterScrypt'SECURE_RANDOM (SecureRandom.))

    ;;; Returns SALT_LENGTH (8) bytes of random data. ;;
    #_public
    #_static
    (§ defn #_"byte[]" KeyCrypterScrypt'randomSalt []
        (let [#_"byte[]" salt (byte-array KeyCrypterScrypt'SALT_LENGTH)]
            (.. KeyCrypterScrypt'SECURE_RANDOM (nextBytes salt))
            salt
        )
    )

    ;; Scrypt parameters.
    #_private
    (§ field- #_"Protos.ScryptParameters" :scrypt-parameters)

    ;;;
     ; Encryption/Decryption using default parameters and a random salt.
     ;;
    #_public
    (§ constructor KeyCrypterScrypt []
        (let [#_"Protos.ScryptParameters.Builder" builder (.. (Protos.ScryptParameters/newBuilder) (setSalt (ByteString/copyFrom (KeyCrypterScrypt'randomSalt))))]
            (§ assoc this :scrypt-parameters (.. builder (build)))
            this
        )
    )

    ;;;
     ; Encryption/Decryption using custom number of iterations parameters and a random salt.
     ; As of August 2016, a useful value for mobile devices is 4096 (derivation takes about 1 second).
     ;
     ; @param iterations Number of scrypt iterations.
     ;;
    #_public
    (§ constructor KeyCrypterScrypt [#_"int" iterations]
        (let [#_"Protos.ScryptParameters.Builder" builder (.. (Protos.ScryptParameters/newBuilder) (setSalt (ByteString/copyFrom (KeyCrypterScrypt'randomSalt))) (setN iterations))]
            (§ assoc this :scrypt-parameters (.. builder (build)))
            this
        )
    )

    ;;;
     ; Encryption/ Decryption using specified Scrypt parameters.
     ;
     ; @param scryptParameters ScryptParameters to use.
     ; @throws NullPointerException if the scryptParameters or any of its N, R or P is null.
     ;;
    #_public
    (§ constructor KeyCrypterScrypt [#_"Protos.ScryptParameters" __scryptParameters]
        (§ assoc this :scrypt-parameters (Preconditions/checkNotNull __scryptParameters))

        ;; Check there is a non-empty salt.  Some early MultiBit wallets has a missing salt, so it is not a hard fail.
        (when (or (nil? (.. __scryptParameters (getSalt))) (nil? (.. __scryptParameters (getSalt) (toByteArray))) (= (alength (.. __scryptParameters (getSalt) (toByteArray))) 0))
            (.. KeyCrypterScrypt'log (warn "You are using a ScryptParameters with no salt. Your encryption may be vulnerable to a dictionary attack."))
        )
        this
    )

    ;;;
     ; Generate AES key.
     ;
     ; This is a very slow operation compared to encrypt/ decrypt so it is normally worth caching the result.
     ;
     ; @param password The password to use in key generation.
     ; @return the KeyParameter containing the created AES key.
     ; @throws KeyCrypterException
     ;;
    #_override
    #_public
    #_throws #_[ "KeyCrypterException" ]
    (§ method #_"KeyParameter" deriveKey [#_"CharSequence" password]
        (let [#_"byte[]" __passwordBytes nil]
            (try
                (§ ass __passwordBytes (KeyCrypterScrypt'convertToByteArray password))
                (let [#_"byte[]" salt (byte-array 0)]
                    (cond (some? (.. (:scrypt-parameters this) (getSalt)))
                        (do
                            (§ ass salt (.. (:scrypt-parameters this) (getSalt) (toByteArray)))
                        )
                        :else
                        (do
                            ;; Warn the user that they are not using a salt.  Some early MultiBit wallets had a blank salt.
                            (.. KeyCrypterScrypt'log (warn "You are using a ScryptParameters with no salt. Your encryption may be vulnerable to a dictionary attack."))
                        )
                    )

                    (let [#_"Stopwatch" watch (Stopwatch/createStarted)
                          #_"byte[]" __keyBytes (SCrypt/scrypt __passwordBytes, salt, (int (.. (:scrypt-parameters this) (getN))), (.. (:scrypt-parameters this) (getR)), (.. (:scrypt-parameters this) (getP)), KeyCrypterScrypt'KEY_LENGTH)]
                        (.. watch (stop))
                        (.. KeyCrypterScrypt'log (info "Deriving key took {} for {} scrypt iterations.", watch, (.. (:scrypt-parameters this) (getN))))
                        (§ return (KeyParameter. __keyBytes))
                    )
                )
                (catch Exception e
                    (throw (KeyCrypterException. "Could not generate key from password and salt.", e))
                )
                (finally
                    ;; Zero the password bytes.
                    (when (some? __passwordBytes)
                        (java.util.Arrays/fill __passwordBytes, (byte 0))
                    )
                )
            )
        )
    )

    ;;;
     ; Password based encryption using AES - CBC 256 bits.
     ;;
    #_override
    #_public
    #_throws #_[ "KeyCrypterException" ]
    (§ method #_"EncryptedData" encrypt [#_"byte[]" __plainBytes, #_"KeyParameter" __aesKey]
        (Preconditions/checkNotNull __plainBytes)
        (Preconditions/checkNotNull __aesKey)

        (try
            ;; Generate iv - each encryption call has a different iv.
            (let [#_"byte[]" iv (byte-array KeyCrypterScrypt'BLOCK_LENGTH)]
                (.. KeyCrypterScrypt'SECURE_RANDOM (nextBytes iv))

                (let [#_"ParametersWithIV" __keyWithIv (ParametersWithIV. __aesKey, iv)]

                    ;; Encrypt using AES.
                    (let [#_"BufferedBlockCipher" cipher (PaddedBufferedBlockCipher. (CBCBlockCipher. (AESFastEngine.)))]
                        (.. cipher (init true, __keyWithIv))
                        (let [#_"byte[]" __encryptedBytes (byte-array (.. cipher (getOutputSize (alength __plainBytes))))
                              #_"int" length1 (.. cipher (processBytes __plainBytes, 0, (alength __plainBytes), __encryptedBytes, 0))
                              #_"int" length2 (.. cipher (doFinal __encryptedBytes, length1))]

                            (§ return (EncryptedData. iv, (Arrays/copyOf __encryptedBytes, (+ length1 length2))))
                        )
                    )
                )
            )
            (catch Exception e
                (throw (KeyCrypterException. "Could not encrypt bytes.", e))
            )
        )
    )

    ;;;
     ; Decrypt bytes previously encrypted with this class.
     ;
     ; @param dataToDecrypt The data to decrypt.
     ; @param aesKey The AES key to use for decryption.
     ; @return the decrypted bytes.
     ; @throws KeyCrypterException if bytes could not be decrypted.
     ;;
    #_override
    #_public
    #_throws #_[ "KeyCrypterException" ]
    (§ method #_"byte[]" decrypt [#_"EncryptedData" __dataToDecrypt, #_"KeyParameter" __aesKey]
        (Preconditions/checkNotNull __dataToDecrypt)
        (Preconditions/checkNotNull __aesKey)

        (try
            (let [#_"ParametersWithIV" __keyWithIv (ParametersWithIV. (KeyParameter. (.. __aesKey (getKey))), (:initialisation-vector __dataToDecrypt))]

                ;; Decrypt the message.
                (let [#_"BufferedBlockCipher" cipher (PaddedBufferedBlockCipher. (CBCBlockCipher. (AESFastEngine.)))]
                    (.. cipher (init false, __keyWithIv))

                    (let [#_"byte[]" __cipherBytes (:encrypted-bytes __dataToDecrypt)
                          #_"byte[]" __decryptedBytes (byte-array (.. cipher (getOutputSize (alength __cipherBytes))))
                          #_"int" length1 (.. cipher (processBytes __cipherBytes, 0, (alength __cipherBytes), __decryptedBytes, 0))
                          #_"int" length2 (.. cipher (doFinal __decryptedBytes, length1))]

                        (§ return (Arrays/copyOf __decryptedBytes, (+ length1 length2)))
                    )
                )
            )
            (catch Exception e
                (throw (KeyCrypterException. "Could not decrypt bytes", e))
            )
        )
    )

    ;;;
     ; Convert a CharSequence (which are UTF16) into a byte array.
     ;
     ; Note: a String.getBytes() is not used to avoid creating a String of the password in the JVM.
     ;;
    #_private
    #_static
    (§ defn- #_"byte[]" KeyCrypterScrypt'convertToByteArray [#_"CharSequence" __charSequence]
        (Preconditions/checkNotNull __charSequence)

        (let [#_"byte[]" __byteArray (byte-array (<< (.. __charSequence (length)) 1))]
            (loop-when-recur [#_"int" i 0] (< i (.. __charSequence (length))) [(inc i)]
                (let [#_"int" __bytePosition (<< i 1)]
                    (aset __byteArray __bytePosition (byte (>> (& (.. __charSequence (charAt i)) 0xff00) 8)))
                    (aset __byteArray (inc __bytePosition) (byte (& (.. __charSequence (charAt i)) 0x00ff)))
                )
            )
            __byteArray
        )
    )

    #_public
    (§ method #_"Protos.ScryptParameters" getScryptParameters []
        (:scrypt-parameters this)
    )

    ;;;
     ; Return the EncryptionType enum value which denotes the type of encryption/ decryption that this KeyCrypter
     ; can understand.
     ;;
    #_override
    #_public
    (§ method #_"Protos.Wallet.EncryptionType" getUnderstoodEncryptionType []
        Protos.Wallet.EncryptionType/ENCRYPTED_SCRYPT_AES
    )

    #_override
    #_public
    (§ method #_"String" toString []
        (str "AES-" (* KeyCrypterScrypt'KEY_LENGTH 8) "-CBC, Scrypt (N: " (.. (:scrypt-parameters this) (getN)) ")")
    )

    #_override
    #_public
    (§ method #_"int" hashCode []
        (Objects/hashCode (:scrypt-parameters this))
    )

    #_override
    #_public
    (§ method #_"boolean" equals [#_"Object" o]
        (when (= this o)
            (§ return true)
        )
        (when (or (nil? o) (not= (getClass) (.. o (getClass))))
            (§ return false)
        )
        (Objects/equal (:scrypt-parameters this), (:scrypt-parameters (cast KeyCrypterScrypt o)))
    )
)

;;;
 ; A wrapper around ECPoint that delays decoding of the point for as long as possible.  This is useful because point
 ; encode/decode in Bouncy Castle is quite slow especially on Dalvik, as it often involves decompression/recompression.
 ;;
#_public
(§ class LazyECPoint
    ;; If curve is set, bits is also set.  If curve is unset, point is set and bits is unset.  Point can be set along
    ;; with curve and bits when the cached form has been accessed and thus must have been converted.
    #_private
    (§ field- #_"ECCurve" :curve)
    #_private
    (§ field- #_"byte[]" :bits)

    ;; This field is effectively final - once set it won't change again.  However it can be set after construction.
    #_nilable
    #_private
    (§ field- #_"ECPoint" :point)

    #_public
    (§ constructor LazyECPoint [#_"ECCurve" curve, #_"byte[]" bits]
        (§ assoc this :curve curve)
        (§ assoc this :bits bits)
        this
    )

    #_public
    (§ constructor LazyECPoint [#_"ECPoint" point]
        (§ assoc this :point (Preconditions/checkNotNull point))
        (§ assoc this :curve nil)
        (§ assoc this :bits nil)
        this
    )

    #_public
    (§ method #_"ECPoint" get []
        (when (nil? (:point this))
            (§ assoc this :point (.. (:curve this) (decodePoint (:bits this))))
        )
        (:point this)
    )

    ;; Delegated methods.

    #_public
    (§ method #_"ECPoint" getDetachedPoint []
        (.. this (get) (getDetachedPoint))
    )

    #_public
    (§ method #_"byte[]" getEncoded []
        (if (some? (:bits this))
            (§ return (Arrays/copyOf (:bits this), (alength (:bits this))))
            (§ return (.. this (get) (getEncoded)))
        )
    )

    #_public
    (§ method #_"boolean" isInfinity []
        (.. this (get) (isInfinity))
    )

    #_public
    (§ method #_"ECPoint" timesPow2 [#_"int" e]
        (.. this (get) (timesPow2 e))
    )

    #_public
    (§ method #_"ECFieldElement" getYCoord []
        (.. this (get) (getYCoord))
    )

    #_public
    (§ method #_"ECFieldElement[]" getZCoords []
        (.. this (get) (getZCoords))
    )

    #_public
    (§ method #_"boolean" isNormalized []
        (.. this (get) (isNormalized))
    )

    #_public
    (§ method #_"boolean" isCompressed []
        (if (some? (:bits this)) (any = (aget (:bits this) 0) 2 3) (.. this (get) (isCompressed)))
    )

    #_public
    (§ method #_"ECPoint" multiply [#_"BigInteger" k]
        (.. this (get) (multiply k))
    )

    #_public
    (§ method #_"ECPoint" subtract [#_"ECPoint" b]
        (.. this (get) (subtract b))
    )

    #_public
    (§ method #_"boolean" isValid []
        (.. this (get) (isValid))
    )

    #_public
    (§ method #_"ECPoint" scaleY [#_"ECFieldElement" scale]
        (.. this (get) (scaleY scale))
    )

    #_public
    (§ method #_"ECFieldElement" getXCoord []
        (.. this (get) (getXCoord))
    )

    #_public
    (§ method #_"ECPoint" scaleX [#_"ECFieldElement" scale]
        (.. this (get) (scaleX scale))
    )

    #_public
    (§ method #_"boolean" equals [#_"ECPoint" other]
        (.. this (get) (equals other))
    )

    #_public
    (§ method #_"ECPoint" negate []
        (.. this (get) (negate))
    )

    #_public
    (§ method #_"ECPoint" threeTimes []
        (.. this (get) (threeTimes))
    )

    #_public
    (§ method #_"ECFieldElement" getZCoord [#_"int" index]
        (.. this (get) (getZCoord index))
    )

    #_public
    (§ method #_"byte[]" getEncoded [#_"boolean" compressed]
        (if (and (= compressed (.. this (isCompressed))) (some? (:bits this)))
            (§ return (Arrays/copyOf (:bits this), (alength (:bits this))))
            (§ return (.. this (get) (getEncoded compressed)))
        )
    )

    #_public
    (§ method #_"ECPoint" add [#_"ECPoint" b]
        (.. this (get) (add b))
    )

    #_public
    (§ method #_"ECPoint" twicePlus [#_"ECPoint" b]
        (.. this (get) (twicePlus b))
    )

    #_public
    (§ method #_"ECCurve" getCurve []
        (.. this (get) (getCurve))
    )

    #_public
    (§ method #_"ECPoint" normalize []
        (.. this (get) (normalize))
    )

    #_public
    (§ method #_"ECFieldElement" getY []
        (.. this (normalize) (getYCoord))
    )

    #_public
    (§ method #_"ECPoint" twice []
        (.. this (get) (twice))
    )

    #_public
    (§ method #_"ECFieldElement" getAffineYCoord []
        (.. this (get) (getAffineYCoord))
    )

    #_public
    (§ method #_"ECFieldElement" getAffineXCoord []
        (.. this (get) (getAffineXCoord))
