    #_public
    (§ method #_"void" setLookaheadSize [#_"int" __lookaheadSize]
        (§ assoc this :lookahead-size __lookaheadSize)
        (doseq [#_"DeterministicKeyChain" chain (:chains this)]
            (.. chain (setLookaheadSize __lookaheadSize))
        )
        nil
    )

    ;;;
