     ; See {@link DeterministicKeyChain#setLookaheadSize(int)} for more information.
     ;;
    #_public
    (§ method #_"int" getLookaheadSize []
        (if (= (:lookahead-size this) -1) (.. this (getActiveKeyChain) (getLookaheadSize)) (:lookahead-size this))
    )

