    #_public
    (§ method #_"BloomFilter" getBloomFilter [#_"int" size, #_"double" __falsePositiveRate, #_"long" __nTweak]
        (let [#_"BloomFilter" filter (BloomFilter. size, __falsePositiveRate, __nTweak)]
            (when (< 0 (.. (:basic this) (numKeys)))
