    )

    #_private
    #_static
    (§ defn- #_"ECKey" (§ fn ECKey/extractKeyFromASN1) [#_"byte[]" __asn1privkey])
    (§ block
        ;; To understand this code, see the definition of the ASN.1 format for EC private keys in the OpenSSL source
        ;; code in ec_asn1.c:
        ;;
        ;; ASN1_SEQUENCE(EC_PRIVATEKEY) = {
        ;;   ASN1_SIMPLE(EC_PRIVATEKEY, version, LONG),
        ;;   ASN1_SIMPLE(EC_PRIVATEKEY, privateKey, ASN1_OCTET_STRING),
        ;;   ASN1_EXP_OPT(EC_PRIVATEKEY, parameters, ECPKPARAMETERS, 0),
        ;;   ASN1_EXP_OPT(EC_PRIVATEKEY, publicKey, ASN1_BIT_STRING, 1)
        ;; } ASN1_SEQUENCE_END(EC_PRIVATEKEY)
        (§ try )
        (§ block
            (§ let #_"ASN1InputStream" __decoder (§ new #_"ASN1InputStream" __asn1privkey))
            (§ block
                (§ let #_"DLSequence" __seq (§ cast #_"DLSequence" (.. __decoder (readObject))))
                (§ block
                    (Preconditions/checkArgument (nil? (.. __decoder (readObject))), "Input contains extra bytes")
                    (.. __decoder (close))

                    (Preconditions/checkArgument (== (.. __seq (size)) 4), "Input does not appear to be an ASN.1 OpenSSL EC private key")

                    (Preconditions/checkArgument (.. (§ cast #_"ASN1Integer" (.. __seq (getObjectAt 0))) (getValue) (equals BigInteger/ONE)), "Input is of wrong version")

                    (§ let #_"byte[]" __privbits (.. (§ cast #_"ASN1OctetString" (.. __seq (getObjectAt 1))) (getOctets)))
                    (§ block
                        (§ let #_"BigInteger" __privkey (§ new #_"BigInteger" 1, __privbits))
                        (§ block

                            (§ let #_"ASN1TaggedObject" __pubkey (§ cast #_"ASN1TaggedObject" (.. __seq (getObjectAt 3))))
                            (§ block
                                (Preconditions/checkArgument (== (.. __pubkey (getTagNo)) 1), "Input has 'publicKey' with bad tag number")
                                (§ let #_"byte[]" __pubbits (.. (§ cast #_"DERBitString" (.. __pubkey (getObject))) (getBytes)))
                                (§ block
                                    (Preconditions/checkArgument (or (== (.. __pubbits length) 33) (== (.. __pubbits length) 65)), "Input has 'publicKey' with invalid length")
                                    (§ let #_"int" __encoding (& 0xff (§ ai __pubbits 0)))
                                    (§ block
                                        ;; Only allow compressed(2,3) and uncompressed(4), not infinity(0) or hybrid(6,7).
                                        (Preconditions/checkArgument (<= 2 __encoding 4), "Input has 'publicKey' with invalid encoding")

                                        ;; Now sanity check to ensure the pubkey bytes match the privkey.
                                        (§ let #_"boolean" __compressed (== (.. __pubbits length) 33))
                                        (§ block
                                            (§ let #_"ECKey" __key (§ new #_"ECKey" __privkey, nil, __compressed))
                                            (§ block
                                                (§ if (not (Arrays/equals (.. __key (getPubKey)), __pubbits)))
                                                (§ block
                                                    (§ throw (§ new #_"IllegalArgumentException" "Public key in ASN.1 structure does not match private key."))
                                                )
                                                (§ return __key)
                                            )
                                        )
                                    )
                                )
                            )
                        )
                    )
                )
            )
        )
        (§ catch #_"IOException" __e)
        (§ block
            (§ throw (§ new #_"RuntimeException" __e)) ;; Cannot happen, reading from memory stream.
        )
    )

