        (§ if (and (some? (.. this keyCrypter)) (not (.. this keyCrypter (equals __keyCrypter)))))
        (§ block
            (§ throw (§ new #_"KeyCrypterException" "The keyCrypter being used to decrypt the key is different to the one that was used to encrypt it"))
        )

        (Preconditions/checkState (some? encryptedPrivateKey), "This key is not encrypted")

        (§ let #_"byte[]" __unencryptedPrivateKey (.. __keyCrypter (decrypt encryptedPrivateKey, __aesKey)))
        (§ block
            (§ let #_"ECKey" __key (ECKey/fromPrivate __unencryptedPrivateKey))
