     ;;
    #_public
    (§ method #_"ECKey" (§ fn decrypt) [#_"KeyParameter" __aesKey])
        (§ throws #_"KeyCrypterException")
    (§ block
        (§ let #_"KeyCrypter" __crypter (.. this (getKeyCrypter)))
        (§ block
            (§ if (nil? __crypter))
            (§ block
                (§ throw (§ new #_"KeyCrypterException" "No key crypter available"))
            )

            (.. this (decrypt __crypter, __aesKey))
        )
    )

    ;;;
     ; Creates decrypted private key if needed.
     ;;
    #_public
    (§ method #_"ECKey" (§ fn maybeDecrypt) [#_nilable #_"KeyParameter" __aesKey])
        (§ throws #_"KeyCrypterException")
    (§ block
        (if (and (.. this (isEncrypted)) (some? __aesKey)) (.. this (decrypt __aesKey)) this)
    )

    ;;;
     ; <p>Check that it is possible to decrypt the key with the keyCrypter and that the original key is returned.</p>
     ;
     ; <p>Because it is a critical failure if the private keys cannot be decrypted successfully (resulting of loss of
     ; all bitcoins controlled by the private key) you can use this method to check when you *encrypt* a wallet that
     ; it can definitely be decrypted successfully.</p>
     ;
     ; <p>See {@link Wallet#encrypt(KeyCrypter keyCrypter, KeyParameter aesKey)} for example usage.</p>
     ;
     ; @return true if the encrypted key can be decrypted back to the original key successfully.
     ;;
    #_public
    #_static
    (§ defn #_"boolean" (§ fn ECKey/encryptionIsReversible) [#_"ECKey" __originalKey, #_"ECKey" __encryptedKey, #_"KeyCrypter" __keyCrypter, #_"KeyParameter" __aesKey])
    (§ block
        (§ try )
        (§ block
            (§ let #_"ECKey" __rebornUnencryptedKey (.. __encryptedKey (decrypt __keyCrypter, __aesKey)))
            (§ block
                (§ let #_"byte[]" __originalPrivateKeyBytes (.. __originalKey (getPrivKeyBytes)))
                (§ block
                    (§ let #_"byte[]" __rebornKeyBytes (.. __rebornUnencryptedKey (getPrivKeyBytes)))
                    (§ block
                        (§ if (not (Arrays/equals __originalPrivateKeyBytes, __rebornKeyBytes)))
                        (§ block
                            (.. ECKey/log (error "The check that encryption could be reversed failed for {}", __originalKey))
                            (§ return false)
                        )
                        (§ return true)
                    )
                )
            )
        )
        (§ catch #_"KeyCrypterException" __kce)
        (§ block
            (.. ECKey/log (error (.. __kce (getMessage))))
            (§ return false)
        )
    )

    ;;;
     ; Indicates whether the private key is encrypted (true) or not (false).
     ; A private key is deemed to be encrypted when there is both a KeyCrypter and the encryptedPrivateKey is non-zero.
     ;;
    #_override
    #_public
    (§ method #_"boolean" (§ fn isEncrypted) [])
    (§ block
        (and (some? keyCrypter) (some? encryptedPrivateKey) (< 0 (.. encryptedPrivateKey encryptedBytes length)))
    )

    #_nilable
    #_override
    #_public
    (§ method #_"Protos.Wallet.EncryptionType" (§ fn getEncryptionType) [])
    (§ block
        (if (some? keyCrypter) (.. keyCrypter (getUnderstoodEncryptionType)) Protos/Wallet/EncryptionType/UNENCRYPTED)
    )

    ;;;
     ; A wrapper for {@link #getPrivKeyBytes()} that returns null if the private key bytes are missing or would have
     ; to be derived (for the HD key case).
     ;;
    #_override
    #_nilable
    #_public
    (§ method #_"byte[]" (§ fn getSecretBytes) [])
    (§ block
        (§ if (.. this (hasPrivKey)))
        (§ block
            (§ return (.. this (getPrivKeyBytes)))
        )

        nil
    )

    ;;; An alias for {@link #getEncryptedPrivateKey()}. ;;
    #_nilable
    #_override
    #_public
    (§ method #_"EncryptedData" (§ fn getEncryptedData) [])
    (§ block
        (.. this (getEncryptedPrivateKey))
    )

    ;;;
     ; Returns the the encrypted private key bytes and initialisation vector for this ECKey, or null if the ECKey
     ; is not encrypted.
     ;;
    #_nilable
    #_public
    (§ method #_"EncryptedData" (§ fn getEncryptedPrivateKey) [])
    (§ block
        encryptedPrivateKey
    )

    ;;;
     ; Returns the KeyCrypter that was used to encrypt to encrypt this ECKey.  You need this to decrypt the ECKey.
     ;;
    #_nilable
    #_public
    (§ method #_"KeyCrypter" (§ fn getKeyCrypter) [])
    (§ block
        keyCrypter
    )

    #_public
    #_static
    (§ class #_"MissingPrivateKeyException" (§ extends #_"RuntimeException")
    )

    #_public
    #_static
    (§ class #_"KeyIsEncryptedException" (§ extends #_"MissingPrivateKeyException")
    )

    #_override
    #_public
    (§ method #_"boolean" (§ fn equals) [#_"Object" __o])
    (§ block
        (§ if (== this __o))
        (§ block
            (§ return true)
        )
        (§ if (or (nil? __o) (not (§ insta __o #_"ECKey"))))
        (§ block
            (§ return false)
        )
        (§ let #_"ECKey" __other (§ cast #_"ECKey" __o))
        (§ block
            (and (Objects/equal (.. this priv), (.. __other priv)) (Objects/equal (.. this pub), (.. __other pub)) (Objects/equal (.. this creationTimeSeconds), (.. __other creationTimeSeconds)) (Objects/equal (.. this keyCrypter), (.. __other keyCrypter)) (Objects/equal (.. this encryptedPrivateKey), (.. __other encryptedPrivateKey)))
        )
    )

    #_override
    #_public
    (§ method #_"int" (§ fn hashCode) [])
    (§ block
        (.. pub (hashCode))
    )

    #_override
    #_public
    (§ method #_"String" (§ fn toString) [])
    (§ block
        (.. this (toString false, nil))
    )

    ;;;
     ; Produce a string rendering of the ECKey INCLUDING the private key.
     ; Unless you absolutely need the private key it is better for security reasons to just use {@link #toString()}.
     ;;
    #_public
    (§ method #_"String" (§ fn toStringWithPrivate) [#_"NetworkParameters" __params])
    (§ block
        (.. this (toString true, __params))
    )

    #_public
    (§ method #_"String" (§ fn getPrivateKeyAsHex) [])
    (§ block
        (.. Utils/HEX (encode (.. this (getPrivKeyBytes))))
    )

    #_public
    (§ method #_"String" (§ fn getPublicKeyAsHex) [])
    (§ block
        (.. Utils/HEX (encode (.. pub (getEncoded))))
    )

    #_private
    (§ method- #_"String" (§ fn toString) [#_"boolean" __includePrivate, #_"NetworkParameters" __params])
    (§ block
        (§ let #_"MoreObjects.ToStringHelper" __helper (.. (MoreObjects/toStringHelper this) (omitNullValues)))
        (§ block
            (.. __helper (add "pub HEX", (.. this (getPublicKeyAsHex))))
            (§ if __includePrivate)
            (§ block
                (§ try )
                (§ block
                    (.. __helper (add "priv HEX", (.. this (getPrivateKeyAsHex))))
                )
                (§ catch #_"IllegalStateException" (§ name _))
                (§ block
                    ;; TODO: Make hasPrivKey() work for deterministic keys and fix this.
                )
                (§ catch #_"Exception" __e)
                (§ block
                    (§ let #_"String" __message (.. __e (getMessage)))
                    (§ block
                        (.. __helper (add "priv EXCEPTION", (str (.. __e (getClass) (getName)) (if (some? __message) (str ": " __message) ""))))
                    )
                )
            )
            (§ if (< 0 creationTimeSeconds))
            (§ block
                (.. __helper (add "creationTimeSeconds", creationTimeSeconds))
            )
            (.. __helper (add "keyCrypter", keyCrypter))
            (§ if __includePrivate)
            (§ block
                (.. __helper (add "encryptedPrivateKey", encryptedPrivateKey))
            )
            (.. __helper (add "isEncrypted", (.. this (isEncrypted))))
            (.. __helper (add "isPubKeyOnly", (.. this (isPubKeyOnly))))
            (.. __helper (toString))
        )
    )

    #_public
    (§ method #_"void" (§ fn formatKeyWithAddress) [#_"boolean" __includePrivateKeys, #_"StringBuilder" __sb, #_"NetworkParameters" __params])
    (§ block
        (§ let #_"Address" __address (.. this (toAddress __params)))
        (§ block
            (.. __sb (append "  addr:"))
            (.. __sb (append (.. __address (toString))))
            (.. __sb (append "  hash160:"))
            (.. __sb (append (.. Utils/HEX (encode (.. this (getPubKeyHash))))))
            (§ if (< 0 creationTimeSeconds))
            (§ block
                (.. __sb (append "  creationTimeSeconds:") (append creationTimeSeconds))
            )
            (.. __sb (append "\n"))
            (§ if __includePrivateKeys)
            (§ block
                (.. __sb (append "  "))
                (.. __sb (append (.. this (toStringWithPrivate __params))))
                (.. __sb (append "\n"))
            )
            nil
        )
    )
)

#_(ns org.bitcoinj.core #_"EmptyMessage"
    (:import [java.io IOException OutputStream]))

