    #_private
    (§ method- #_"String" (§ fn getScriptAddress) [#_nilable #_"Script" __script])
    (§ block
        (§ let #_"String" __address "")
        (§ block
            (§ try )
            (§ block
                (§ if (some? __script))
                (§ block
                    (§ ass __address (.. __script (getToAddress params, true) (toString)))
                )
            )
            (§ catch #_"Exception" (§ name _))
            (§ block
            )
            __address
        )
