    #_override
    #_protected
    (§ method #_"TransactionOutputChanges" (§ fn connectTransactions) [#_"int" __height, #_"Block" __block])
        (§ throws #_"VerificationException", #_"BlockStoreException")
    (§ block
        (Preconditions/checkState (.. lock (isHeldByCurrentThread)))

        (§ if (== (.. __block transactions) nil))
        (§ block
            (§ throw (§ new #_"RuntimeException" "connectTransactions called with Block that didn't have transactions!"))
        )
        (§ if (not (.. params (passesCheckpoint __height, (.. __block (getHash))))))
        (§ block
            (§ throw (§ new #_"VerificationException" (str "Block failed checkpoint lockin at " __height)))
        )

        (.. blockStore (beginDatabaseBatchWrite))

        (§ let #_"LinkedList<UTXO>" __txOutsSpent (§ new #_"LinkedList<>"))
        (§ block
            (§ let #_"LinkedList<UTXO>" __txOutsCreated (§ new #_"LinkedList<>"))
            (§ block
                (§ let #_"long" __sigOps 0)
                (§ block

                    (§ if (.. scriptVerificationExecutor (isShutdown)))
                    (§ block
                        (§ ass (§ name scriptVerificationExecutor) (Executors/newFixedThreadPool (.. (Runtime/getRuntime) (availableProcessors))))
                    )

                    (§ let #_"List<Future<VerificationException>>" __listScriptVerificationResults (§ new #_"ArrayList<>" (.. __block transactions (size))))
