                                (§ block
                                    (§ let #_"Set<VerifyFlag>" __verifyFlags (.. params (getTransactionVerificationFlags __block, __tx, (.. this (getVersionTally)), __height)))
                                    (§ block
                                        (§ let #_"Sha256Hash" __hash (.. __tx (getHash)))
                                        (§ block
                                            ;; If we already have unspent outputs for this hash, we saw the tx already.
                                            ;; Either the block is being added twice (bug) or the block is a BIP30 violator.
                                            (§ if (.. blockStore (hasUnspentOutputs __hash, (.. __tx (getOutputs) (size)))))
                                            (§ block
                                                (§ throw (§ new #_"VerificationException" "Block failed BIP30 test!"))
                                            )
