                                            (§ if (.. __verifyFlags (contains VerifyFlag/P2SH)))
                                            (§ block
                                                (§ ass __sigOps (+ __sigOps (.. __tx (getSigOpCount))))
                                            )
                                        )
                                    )
                                )
                            )
                            (§ let #_"Coin" __totalFees Coin/ZERO)
                            (§ block
                                (§ let #_"Coin" __coinbaseValue nil)
                                (§ block
                                    (§ for (§ var Transaction tx) :for (.. __block transactions))
                                    (§ block
                                        (§ let #_"boolean" __isCoinBase (.. tx (isCoinBase)))
                                        (§ block
                                            (§ let #_"Coin" __valueIn Coin/ZERO)
                                            (§ block
                                                (§ let #_"Coin" __valueOut Coin/ZERO)
                                                (§ block
                                                    (§ let #_"List<Script>" __prevOutScripts (§ new #_"LinkedList<>"))
                                                    (§ block
                                                        (§ let #_"Set<VerifyFlag>" __verifyFlags (.. params (getTransactionVerificationFlags __block, tx, (.. this (getVersionTally)), __height)))
                                                        (§ block
                                                            (§ if (not __isCoinBase))
                                                            (§ block
                                                                ;; For each input of the transaction remove the corresponding output from the set of unspent outputs.
                                                                (§ for (§ var #_"int" __index 0) :for (< __index (.. tx (getInputs) (size))) :for (§ recur (inc __index)))
                                                                (§ block
                                                                    (§ let #_"TransactionInput" __in (.. tx (getInputs) (get __index)))
                                                                    (§ block
                                                                        (§ let #_"UTXO" __prevOut (.. blockStore (getTransactionOutput (.. __in (getOutpoint) (getHash)), (.. __in (getOutpoint) (getIndex)))))
                                                                        (§ block
                                                                            (§ if (nil? __prevOut))
                                                                            (§ block
                                                                                (§ throw (§ new #_"VerificationException" "Attempted to spend a non-existent or already spent output!"))
