                                                                            ;; The assumption is there will ~never be re-orgs deeper than the spendable coinbase chain depth.
                                                                            (§ if (.. __prevOut (isCoinbase)))
                                                                            (§ block
                                                                                (§ if (< (- __height (.. __prevOut (getHeight))) (.. params (getSpendableCoinbaseDepth))))
                                                                                (§ block
                                                                                    (§ throw (§ new #_"VerificationException" (str "Tried to spend coinbase at depth " (- __height (.. __prevOut (getHeight))))))
                                                                                )
                                                                            )
                                                                            ;; TODO: Check we're not spending the genesis transaction here. Bitcoin Core won't allow it.
