                                                                            (§ block
                                                                                (§ if (.. __prevOut (getScript) (isPayToScriptHash)))
                                                                                (§ block
                                                                                    (§ ass __sigOps (+ __sigOps (Script/getP2SHSigOpCount (.. __in (getScriptBytes)))))
                                                                                )
                                                                                (§ if (< Block/MAX_BLOCK_SIGOPS __sigOps))
                                                                                (§ block
                                                                                    (§ throw (§ new #_"VerificationException" "Too many P2SH SigOps in block"))
                                                                                )
                                                                            )

