                                                                    (§ let #_"Script" __script (.. this (getScript (.. __out (getScriptBytes)))))
                                                                    (§ block
                                                                        (§ let #_"UTXO" __newOut (§ new #_"UTXO" __hash, (.. __out (getIndex)), (.. __out (getValue)), __height, __isCoinBase, __script, (.. this (getScriptAddress __script))))
                                                                        (§ block
                                                                            (.. blockStore (addUnspentTransactionOutput __newOut))
                                                                            (.. __txOutsCreated (add __newOut))
                                                                        )
                                                                    )
                                                                )
                                                                ;; All values were already checked for being non-negative (as it is verified in Transaction.verify()),
                                                                ;; but we check again here just for defence in depth.  Transactions with zero output value are OK.
                                                                (§ if (or (< (.. __valueOut (signum)) 0) (< 0 (.. __valueOut (compareTo (.. params (getMaxMoney)))))))
                                                                (§ block
                                                                    (§ throw (§ new #_"VerificationException" "Transaction output value out of range"))
                                                                )

                                                                (§ if __isCoinBase)
                                                                (§ block
                                                                    (§ ass __coinbaseValue __valueOut)
                                                                )
                                                                (§ else )
                                                                (§ block
                                                                    (§ if (or (< (.. __valueIn (compareTo __valueOut)) 0) (< 0 (.. __valueIn (compareTo (.. params (getMaxMoney)))))))
                                                                    (§ block
                                                                        (§ throw (§ new #_"VerificationException" "Transaction input value out of range"))
                                                                    )
                                                                    (§ ass __totalFees (.. __totalFees (add (.. __valueIn (subtract __valueOut)))))
                                                                )

                                                                (§ if (and (not __isCoinBase) runScripts))
                                                                (§ block
                                                                    ;; Because correctlySpends modifies transactions, this must come after we are done with tx.
                                                                    (§ let #_"FutureTask<VerificationException>" __future (§ new #_"FutureTask<>" (§ new #_"Verifier" tx, __prevOutScripts, __verifyFlags)))
                                                                    (§ block
                                                                        (.. scriptVerificationExecutor (execute __future))
                                                                        (.. __listScriptVerificationResults (add __future))
                                                                    )
                                                                )
                                                            )
                                                        )
                                                    )
                                                )
                                            )
                                        )
                                    )
                                    (§ if (or (< 0 (.. __totalFees (compareTo (.. params (getMaxMoney))))) (< (.. __block (getBlockInflation __height) (add __totalFees) (compareTo __coinbaseValue)) 0)))
                                    (§ block
                                        (§ throw (§ new #_"VerificationException" "Transaction fees out of range"))
                                    )

                                    (§ for (§ var #_"Future<VerificationException>" __future) :for __listScriptVerificationResults)
                                    (§ block
                                        (§ let #_"VerificationException" __e)
                                        (§ block
                                            (§ try )
                                            (§ block
                                                (§ ass __e (.. __future (get)))
                                            )
                                            (§ catch #_"InterruptedException" __ie)
                                            (§ block
                                                (§ throw (§ new #_"RuntimeException" __ie)) ;; Shouldn't happen.
                                            )
                                            (§ catch #_"ExecutionException" __ee)
                                            (§ block
                                                (.. FullPrunedBlockChain/log (error (str "Script.correctlySpends threw a non-normal exception: " (.. __ee (getCause)))))
                                                (§ throw (§ new #_"VerificationException" "Bug in Script.correctlySpends, likely script malformed in some new and interesting way.", __ee))
                                            )
                                            (§ if (some? __e))
                                            (§ block
                                                (§ throw __e)
                                            )
                                        )
                                    )
                                )
                            )
                        )
                        (§ catch #_"VerificationException" __e)
                        (§ block
                            (.. scriptVerificationExecutor (shutdownNow))
                            (.. blockStore (abortDatabaseBatchWrite))
                            (§ throw __e)
                        )
                        (§ catch #_"BlockStoreException" __e)
                        (§ block
                            (.. scriptVerificationExecutor (shutdownNow))
                            (.. blockStore (abortDatabaseBatchWrite))
                            (§ throw __e)
                        )

                        (§ new #_"TransactionOutputChanges" __txOutsCreated, __txOutsSpent)
