            (§ throw (§ new #_"VerificationException" (str "Block failed checkpoint lockin at " (.. __newBlock (getHeight)))))
        )

        (.. blockStore (beginDatabaseBatchWrite))
        (§ let #_"StoredUndoableBlock" __block (.. blockStore (getUndoBlock (.. __newBlock (getHeader) (getHash)))))
        (§ block
            (§ if (nil? __block))
            (§ block
                ;; We're trying to re-org too deep and the data needed has been deleted.
                (.. blockStore (abortDatabaseBatchWrite))
                (§ throw (§ new #_"PrunedException" (.. __newBlock (getHeader) (getHash))))
            )

            (§ let #_"TransactionOutputChanges" __txOutChanges)
            (§ block
                (§ try )
                (§ block
                    (§ let #_"List<Transaction>" __transactions (.. __block (getTransactions)))
