                        (§ if (some? __transactions))
                        (§ block
                            (§ let #_"LinkedList<UTXO>" __txOutsSpent (§ new #_"LinkedList<>"))
                            (§ block
                                (§ let #_"LinkedList<UTXO>" __txOutsCreated (§ new #_"LinkedList<>"))
                                (§ block
                                    (§ let #_"long" __sigOps 0)
                                    (§ block

                                        (§ if (not (.. params (isCheckpoint (.. __newBlock (getHeight))))))
                                        (§ block
                                            (§ for (§ var #_"Transaction" __tx) :for __transactions)
                                            (§ block
                                                (§ let #_"Sha256Hash" __hash (.. __tx (getHash)))
                                                (§ block
                                                    (§ if (.. blockStore (hasUnspentOutputs __hash, (.. __tx (getOutputs) (size)))))
                                                    (§ block
                                                        (§ throw (§ new #_"VerificationException" "Block failed BIP30 test!"))
                                                    )
                                                )
                                            )
                                        )

