                                            (§ let #_"Coin" __coinbaseValue nil)
                                            (§ block

                                                (§ if (.. scriptVerificationExecutor (isShutdown)))
                                                (§ block
                                                    (§ ass (§ name scriptVerificationExecutor) (Executors/newFixedThreadPool (.. (Runtime/getRuntime) (availableProcessors))))
                                                )

                                                (§ let #_"List<Future<VerificationException>>" __listScriptVerificationResults (§ new #_"ArrayList<>" (.. __transactions (size))))
                                                (§ block
                                                    (§ for (§ var Transaction tx) :for transactions)
