                                                            )
                                                        )
                                                    )
                                                )
                                            )
                                        )
                                    )
                                )
                            )
                        )
                        (§ else )
                        (§ block
                            (§ ass __txOutChanges (.. __block (getTxOutChanges)))
                            (§ if (not (.. params (isCheckpoint (.. __newBlock (getHeight))))))
                            (§ block
                                (§ for (§ var #_"UTXO" __out) :for (.. txOutChanges txOutsCreated))
                                (§ block
                                    (§ let #_"Sha256Hash" __hash (.. __out (getHash)))
