                )
                (§ catch #_"BlockStoreException" __e)
                (§ block
                    (.. scriptVerificationExecutor (shutdownNow))
                    (.. blockStore (abortDatabaseBatchWrite))
                    (§ throw __e)
                )
                txOutChanges
            )
        )
    )

    ;;;
     ; This is broken for blocks that do not pass BIP30, so all BIP30-failing blocks which are allowed to fail BIP30
     ; must be checkpointed.
     ;;
    #_override
    #_protected
    (§ method #_"void" (§ fn disconnectTransactions) [#_"StoredBlock" __oldBlock])
        (§ throws #_"PrunedException", #_"BlockStoreException")
    (§ block
        (Preconditions/checkState (.. lock (isHeldByCurrentThread)))
        (.. blockStore (beginDatabaseBatchWrite))
        (§ try )
        (§ block
            (§ let #_"StoredUndoableBlock" __undoBlock (.. blockStore (getUndoBlock (.. __oldBlock (getHeader) (getHash)))))
            (§ block
                (§ if (nil? __undoBlock))
                (§ block
                    (§ throw (§ new #_"PrunedException" (.. __oldBlock (getHeader) (getHash))))
                )

                (§ let #_"TransactionOutputChanges" __txOutChanges (.. __undoBlock (getTxOutChanges)))
                (§ block
                    (§ for (§ var #_"UTXO" __out) :for (.. __txOutChanges txOutsSpent))
                    (§ block
                        (.. blockStore (addUnspentTransactionOutput __out))
                    )
                    (§ for (§ var #_"UTXO" __out) :for (.. __txOutChanges txOutsCreated))
                    (§ block
                        (.. blockStore (removeUnspentTransactionOutput __out))
                    )
                )
            )
        )
        (§ catch #_"PrunedException" __e)
        (§ block
            (.. blockStore (abortDatabaseBatchWrite))
            (§ throw __e)
        )
        (§ catch #_"BlockStoreException" __e)
        (§ block
            (.. blockStore (abortDatabaseBatchWrite))
            (§ throw __e)
        )
        nil
    )

    #_override
    #_protected
    (§ method #_"void" (§ fn doSetChainHead) [#_"StoredBlock" __chainHead])
        (§ throws #_"BlockStoreException")
    (§ block
        (Preconditions/checkState (.. lock (isHeldByCurrentThread)))
        (.. blockStore (setVerifiedChainHead __chainHead))
        (.. blockStore (commitDatabaseBatchWrite))
        nil
    )

    #_override
    #_protected
    (§ method #_"void" (§ fn notSettingChainHead) [])
        (§ throws #_"BlockStoreException")
    (§ block
        (.. blockStore (abortDatabaseBatchWrite))
        nil
    )

    #_override
    #_protected
    (§ method #_"StoredBlock" (§ fn getStoredBlockInCurrentScope) [#_"Sha256Hash" __hash])
        (§ throws #_"BlockStoreException")
    (§ block
        (Preconditions/checkState (.. lock (isHeldByCurrentThread)))
        (.. blockStore (getOnceUndoableStoredBlock __hash))
    )
)

