#_(ns org.bitcoinj.core #_"GetBlocksMessage"
    (:import [java.io IOException OutputStream]
             [java.util ArrayList List]))

;;;
 ; <p>Represents the "getblocks" P2P network message, which requests the hashes of the parts of the block chain we're missing.
 ; Those blocks can then be downloaded with a {@link GetDataMessage}.</p>
 ;
 ; <p>Instances of this class are not safe for use by multiple threads.</p>
 ;;
#_public
(§ class #_"GetBlocksMessage" (§ extends #_"Message")
    #_protected
    (§ field #_"long" (§ name version))
    #_protected
    (§ field #_"List<Sha256Hash>" (§ name locator))
    #_protected
    (§ field #_"Sha256Hash" (§ name stopHash))

    #_public
    (§ constructor #_"GetBlocksMessage" [#_"NetworkParameters" __params, #_"List<Sha256Hash>" __locator, #_"Sha256Hash" __stopHash])
    (§ block
        (§ super __params)

        (§ ass (§ name (.. this version)) protocolVersion)
        (§ ass (§ name (.. this locator)) __locator)
        (§ ass (§ name (.. this stopHash)) __stopHash)
        this
    )

    #_public
    (§ constructor #_"GetBlocksMessage" [#_"NetworkParameters" __params, #_"byte[]" __payload])
        (§ throws #_"ProtocolException")
    (§ block
        (§ super __params, __payload, 0)
        this
    )

    #_override
    #_protected
    (§ method #_"void" (§ fn parse) [])
        (§ throws #_"ProtocolException")
    (§ block
        (§ ass (§ name cursor) offset)
        (§ ass (§ name version) (.. this (readUint32)))
        (§ let #_"int" __startCount (§ cast #_"int" (.. this (readVarInt))))
        (§ block
            (§ if (< 500 __startCount))
            (§ block
                (§ throw (§ new #_"ProtocolException" (str "Number of locators cannot be > 500, received: " __startCount)))
            )

            (§ ass (§ name length) (+ (- cursor offset) (* (inc __startCount) 32)))
            (§ ass (§ name locator) (§ new #_"ArrayList<>" __startCount))
            (§ for (§ var #_"int" __i 0) :for (< __i __startCount) :for (§ recur (inc __i)))
            (§ block
                (.. locator (add (.. this (readHash))))
            )
            (§ ass (§ name stopHash) (.. this (readHash)))
            nil
        )
    )

    #_public
    (§ method #_"List<Sha256Hash>" (§ fn getLocator) [])
    (§ block
        locator
    )

    #_public
    (§ method #_"Sha256Hash" (§ fn getStopHash) [])
    (§ block
        stopHash
    )

    #_override
    #_public
    (§ method #_"String" (§ fn toString) [])
    (§ block
        (str "getblocks: " (.. Utils/SPACE_JOINER (join locator)))
    )

    #_override
    #_protected
    (§ method #_"void" (§ fn bitcoinSerializeToStream) [#_"OutputStream" __stream])
        (§ throws #_"IOException")
    (§ block
        ;; Version, for some reason.
        (Utils/uint32ToByteStreamLE (.. params (getProtocolVersionNum NetworkParameters/ProtocolVersion/CURRENT)), __stream)
        ;; Then a vector of block hashes.  This is actually a "block locator", a set of block
        ;; identifiers that spans the entire chain with exponentially increasing gaps between
        ;; them, until we end up at the genesis block.  See CBlockLocator::Set().
        (.. __stream (write (.. (§ new #_"VarInt" (.. locator (size))) (encode))))
        (§ for (§ var #_"Sha256Hash" __hash) :for locator)
        (§ block
            ;; Have to reverse as wire format is little endian.
            (.. __stream (write (.. __hash (getReversedBytes))))
        )
        ;; Next, a block ID to stop at.
        (.. __stream (write (.. stopHash (getReversedBytes))))
        nil
    )

    #_override
    #_public
    (§ method #_"boolean" (§ fn equals) [#_"Object" __o])
    (§ block
        (§ if (== this __o))
        (§ block
            (§ return true)
        )
        (§ if (or (nil? __o) (!= (getClass) (.. __o (getClass)))))
        (§ block
            (§ return false)
        )
        (§ let #_"GetBlocksMessage" __other (§ cast #_"GetBlocksMessage" __o))
        (§ block
            (and (== version (.. __other version)) (.. stopHash (equals (.. __other stopHash))) (== (.. locator (size)) (.. __other locator (size))) (.. locator (containsAll (.. __other locator)))) ;; ignores locator ordering
        )
    )

    #_override
    #_public
    (§ method #_"int" (§ fn hashCode) [])
    (§ block
        (§ let #_"int" __hashCode (bit-xor (§ cast #_"int" version) (.. "getblocks" (hashCode)) (.. stopHash (hashCode))))
        (§ block
            (§ for (§ var #_"Sha256Hash" __l) :for locator)
            (§ block
                (§ ass __hashCode (bit-xor __hashCode (.. __l (hashCode)))) ;; ignores locator ordering
            )
            __hashCode
        )
    )
)

