        (§ super __params, __locator, __stopHash)
        this
    )

    #_public
    (§ constructor #_"GetHeadersMessage" [#_"NetworkParameters" __params, #_"byte[]" __payload])
        (§ throws #_"ProtocolException")
    (§ block
        (§ super __params, __payload)
        this
    )

    #_override
    #_public
    (§ method #_"String" (§ fn toString) [])
    (§ block
        (str "getheaders: " (.. Utils/SPACE_JOINER (join locator)))
    )

    ;;;
     ; Compares two getheaders messages.  Note that even though they are structurally identical a GetHeadersMessage
     ; will not compare equal to a GetBlocksMessage containing the same data.
     ;;
    #_override
    #_public
    (§ method #_"boolean" (§ fn equals) [#_"Object" __o])
    (§ block
        (§ if (== this __o))
        (§ block
            (§ return true)
        )
        (§ if (or (nil? __o) (!= (getClass) (.. __o (getClass)))))
        (§ block
            (§ return false)
        )
        (§ let #_"GetHeadersMessage" __other (§ cast #_"GetHeadersMessage" __o))
        (§ block
            (and (== version (.. __other version)) (.. stopHash (equals (.. __other stopHash))) (== (.. locator (size)) (.. __other locator (size))) (.. locator (containsAll (.. __other locator)))) ;; ignores locator ordering
        )
    )

    #_override
    #_public
    (§ method #_"int" (§ fn hashCode) [])
    (§ block
        (§ let #_"int" __hashCode (bit-xor (§ cast #_"int" version) (.. "getheaders" (hashCode)) (.. stopHash (hashCode))))
        (§ block
            (§ for (§ var #_"Sha256Hash" __l) :for locator)
            (§ block
                (§ ass __hashCode (bit-xor __hashCode (.. __l (hashCode)))) ;; ignores locator ordering
            )
            __hashCode
        )
    )
)

#_(ns org.bitcoinj.core #_"HeadersMessage"
    (:import [java.io IOException OutputStream]
             [java.util ArrayList Arrays List])
    (:import [org.slf4j Logger LoggerFactory]))

;;;
 ; <p>A protocol message that contains a repeated series of block headers, sent in response to the "getheaders" command.
 ; This is useful when you want to traverse the chain but know you don't care about the block contents, for example,
 ; because you have a freshly created wallet with no keys.</p>
 ;
 ; <p>Instances of this class are not safe for use by multiple threads.</p>
 ;;
#_public
(§ class #_"HeadersMessage" (§ extends #_"Message")
    #_private
    #_static
    (§ def- #_"Logger" (§ name HeadersMessage/log) (LoggerFactory/getLogger (§ klass #_"HeadersMessage")))

    ;; The main client will never send us more than this number of headers.
    #_public
    #_static
    (§ def #_"int" (§ name HeadersMessage/MAX_HEADERS) 2000)

    #_private
    (§ field- #_"List<Block>" (§ name blockHeaders))

    #_public
    (§ constructor #_"HeadersMessage" [#_"NetworkParameters" __params, #_"byte[]" __payload])
        (§ throws #_"ProtocolException")
    (§ block
        (§ super __params, __payload, 0)
        this
    )

    #_public
    (§ constructor #_"HeadersMessage" [#_"NetworkParameters" __params, #_"Block..." __headers])
        (§ throws #_"ProtocolException")
    (§ block
        (§ super __params)
        (§ ass (§ name blockHeaders) (Arrays/asList __headers))
        this
    )

    #_public
    (§ constructor #_"HeadersMessage" [#_"NetworkParameters" __params, #_"List<Block>" __headers])
        (§ throws #_"ProtocolException")
    (§ block
        (§ super __params)
        (§ ass (§ name blockHeaders) __headers)
        this
    )

    #_override
    #_public
    (§ method #_"void" (§ fn bitcoinSerializeToStream) [#_"OutputStream" __stream])
        (§ throws #_"IOException")
    (§ block
        (.. __stream (write (.. (§ new #_"VarInt" (.. blockHeaders (size))) (encode))))
        (§ for (§ var #_"Block" __header) :for blockHeaders)
        (§ block
            (.. __header (cloneAsHeader) (bitcoinSerializeToStream __stream))
            (.. __stream (write 0))
        )
        nil
    )

    #_override
    #_protected
    (§ method #_"void" (§ fn parse) [])
        (§ throws #_"ProtocolException")
    (§ block
        (§ let #_"long" __numHeaders (.. this (readVarInt)))
        (§ block
            (§ if (< HeadersMessage/MAX_HEADERS __numHeaders))
            (§ block
                (§ throw (§ new #_"ProtocolException" (str "Too many headers: got " __numHeaders " which is larger than " HeadersMessage/MAX_HEADERS)))
            )

            (§ ass (§ name blockHeaders) (§ new #_"ArrayList<>"))
            (§ let #_"BitcoinSerializer" __serializer (.. this params (getSerializer true)))
            (§ block

                (§ for (§ var #_"int" __i 0) :for (< __i __numHeaders) :for (§ recur (inc __i)))
                (§ block
                    (§ let #_"Block" __newBlockHeader (.. __serializer (makeBlock payload, cursor, Message/UNKNOWN_LENGTH)))
                    (§ block
                        (§ if (.. __newBlockHeader (hasTransactions)))
                        (§ block
                            (§ throw (§ new #_"ProtocolException" "Block header does not end with a nil byte"))
                        )

                        (§ ass (§ name cursor) (+ cursor (.. __newBlockHeader optimalEncodingMessageSize)))
                        (.. blockHeaders (add __newBlockHeader))
                    )
                )

                (§ if (== length Message/UNKNOWN_LENGTH))
                (§ block
                    (§ ass (§ name length) (- cursor offset))
                )

                (§ if (.. HeadersMessage/log (isDebugEnabled)))
                (§ block
                    (§ for (§ var #_"int" __i 0) :for (< __i __numHeaders) :for (§ recur (inc __i)))
                    (§ block
                        (.. HeadersMessage/log (debug (.. this blockHeaders (get __i) (toString))))
                    )
                )
                nil
            )
        )
    )

    #_public
    (§ method #_"List<Block>" (§ fn getBlockHeaders) [])
    (§ block
        blockHeaders
    )
)

