#_(ns org.bitcoinj.core #_"InventoryMessage"
    (:import [com.google.common.base Preconditions]))

;;;
 ; <p>Represents the "inv" P2P network message.  An inv contains a list of hashes of either blocks or transactions.
 ; It's a bandwidth optimization - on receiving some data, a (fully validating) peer sends every connected peer an inv
 ; containing the hash of what it saw.  It'll only transmit the full thing if a peer asks for it with a
 ; {@link GetDataMessage}.</p>
 ;
 ; <p>Instances of this class are not safe for use by multiple threads.</p>
 ;;
#_public
(§ class #_"InventoryMessage" (§ extends #_"ListMessage")
    ;;; A hard coded constant in the protocol. ;;
    #_public
    #_static
    (§ def #_"int" (§ name InventoryMessage/MAX_INV_SIZE) 50000)

    #_public
    (§ constructor #_"InventoryMessage" [#_"NetworkParameters" __params, #_"byte[]" __bytes])
        (§ throws #_"ProtocolException")
    (§ block
        (§ super __params, __bytes)
        this
    )

    ;;;
     ; Deserializes an 'inv' message.
     ; @param params NetworkParameters object.
     ; @param payload Bitcoin protocol formatted byte array containing message content.
     ; @param serializer The serializer to use for this message.
     ; @param length The length of message if known.  Usually this is provided when deserializing of the wire
     ; as the length will be provided as part of the header.  Set to Message.UNKNOWN_LENGTH, if not known.
     ; @throws ProtocolException
     ;;
    #_public
    (§ constructor #_"InventoryMessage" [#_"NetworkParameters" __params, #_"byte[]" __payload, #_"MessageSerializer" __serializer, #_"int" __length])
        (§ throws #_"ProtocolException")
    (§ block
        (§ super __params, __payload, __serializer, __length)
        this
    )

    #_public
    (§ constructor #_"InventoryMessage" [#_"NetworkParameters" __params])
    (§ block
        (§ super __params)
        this
    )

    #_public
    (§ method #_"void" (§ fn addBlock) [#_"Block" __block])
    (§ block
        (.. this (addItem (§ new #_"InventoryItem" InventoryItem/Type/Block, (.. __block (getHash)))))
        nil
    )

    #_public
    (§ method #_"void" (§ fn addTransaction) [#_"Transaction" __tx])
    (§ block
        (.. this (addItem (§ new #_"InventoryItem" InventoryItem/Type/Transaction, (.. __tx (getHash)))))
        nil
    )

    ;;; Creates a new inv message for the given transactions. ;;
    #_public
    #_static
    (§ defn #_"InventoryMessage" (§ fn InventoryMessage/with) [#_"Transaction..." __txs])
    (§ block
        (Preconditions/checkArgument (< 0 (.. __txs length)))
        (§ let #_"InventoryMessage" __result (§ new #_"InventoryMessage" (.. (§ ai __txs 0) (getParams))))
        (§ block
            (§ for (§ var #_"Transaction" __tx) :for __txs)
            (§ block
                (.. __result (addTransaction __tx))
            )
            __result
        )
    )
)

#_(ns org.bitcoinj.core #_"ListMessage"
    (:import [java.io IOException OutputStream]
             [java.util ArrayList Collections List]))

;;;
 ; <p>Abstract superclass of classes with list based payload, i.e. InventoryMessage and GetDataMessage.</p>
 ;
 ; <p>Instances of this class are not safe for use by multiple threads.</p>
 ;;
#_public
#_abstract
(§ class #_"ListMessage" (§ extends #_"Message")
    #_public
    #_static
    (§ def #_"long" (§ name ListMessage/MAX_INVENTORY_ITEMS) 50000)

    #_private
    (§ field- #_"long" (§ name arrayLen))
    ;; For some reason the compiler complains if this is inside InventoryItem.
    #_protected
    (§ field #_"List<InventoryItem>" (§ name items))

    #_public
    (§ constructor #_"ListMessage" [#_"NetworkParameters" __params, #_"byte[]" __bytes])
        (§ throws #_"ProtocolException")
    (§ block
        (§ super __params, __bytes, 0)
        this
    )

    #_public
    (§ constructor #_"ListMessage" [#_"NetworkParameters" __params, #_"byte[]" __payload, #_"MessageSerializer" __serializer, #_"int" __length])
        (§ throws #_"ProtocolException")
    (§ block
        (§ super __params, __payload, 0, __serializer, __length)
        this
    )

    #_public
    (§ constructor #_"ListMessage" [#_"NetworkParameters" __params])
    (§ block
        (§ super __params)

        (§ ass (§ name items) (§ new #_"ArrayList<>"))
        (§ ass (§ name length) 1) ;; length of 0 varint
        this
    )

    #_public
    (§ method #_"List<InventoryItem>" (§ fn getItems) [])
    (§ block
        (Collections/unmodifiableList items)
    )

    #_public
    (§ method #_"void" (§ fn addItem) [#_"InventoryItem" __item])
    (§ block
        (.. this (unCache))
        (§ ass (§ name length) (- length (VarInt/sizeOf (.. items (size)))))
        (.. items (add __item))
        (§ ass (§ name length) (+ length (VarInt/sizeOf (.. items (size))) InventoryItem/MESSAGE_LENGTH))
        nil
    )

    #_public
    (§ method #_"void" (§ fn removeItem) [#_"int" __index])
    (§ block
        (.. this (unCache))
        (§ ass (§ name length) (- length (VarInt/sizeOf (.. items (size)))))
        (.. items (remove __index))
        (§ ass (§ name length) (+ length (VarInt/sizeOf (.. items (size))) (- InventoryItem/MESSAGE_LENGTH)))
        nil
    )

    #_override
    #_protected
    (§ method #_"void" (§ fn parse) [])
        (§ throws #_"ProtocolException")
    (§ block
        (§ ass (§ name arrayLen) (.. this (readVarInt)))
        (§ if (< ListMessage/MAX_INVENTORY_ITEMS arrayLen))
        (§ block
            (§ throw (§ new #_"ProtocolException" (str "Too many items in INV message: " arrayLen)))
        )
        (§ ass (§ name length) (§ cast #_"int" (+ (- cursor offset) (* arrayLen InventoryItem/MESSAGE_LENGTH))))

        ;; An inv is vector<CInv> where CInv is int+hash.  The int is either 1 or 2 for tx or block.
        (§ ass (§ name items) (§ new #_"ArrayList<>" (§ cast #_"int" arrayLen)))
        (§ for (§ var #_"int" __i 0) :for (< __i arrayLen) :for (§ recur (inc __i)))
        (§ block
            (§ if (< (.. payload length) (+ cursor InventoryItem/MESSAGE_LENGTH)))
            (§ block
                (§ throw (§ new #_"ProtocolException" "Ran off the end of the INV"))
            )

            (§ let #_"int" __typeCode (§ cast #_"int" (.. this (readUint32))))
            (§ block
                (§ let #_"InventoryItem.Type" __type)
                (§ block
                    ;; see ppszTypeName in net.h
                    (§ switch __typeCode)
                    (§ block
                        (§ case 0)
                        (§ block
                            (§ ass __type InventoryItem/Type/Error)
                            (§ break )
                        )
                        (§ case 1)
                        (§ block
                            (§ ass __type InventoryItem/Type/Transaction)
                            (§ break )
                        )
                        (§ case 2)
                        (§ block
                            (§ ass __type InventoryItem/Type/Block)
                            (§ break )
                        )
                        (§ case 3)
                        (§ block
                            (§ ass __type InventoryItem/Type/FilteredBlock)
                            (§ break )
                        )
                        (§ default )
                        (§ block
                            (§ throw (§ new #_"ProtocolException" (str "Unknown CInv type: " __typeCode)))
                        )
                    )
                    (.. items (add (§ new #_"InventoryItem" __type, (.. this (readHash)))))
                )
            )
        )
        (§ ass (§ name payload) nil)
        nil
    )

    #_override
    #_public
    (§ method #_"void" (§ fn bitcoinSerializeToStream) [#_"OutputStream" __stream])
        (§ throws #_"IOException")
    (§ block
        (.. __stream (write (.. (§ new #_"VarInt" (.. items (size))) (encode))))
        (§ for (§ var #_"InventoryItem" __i) :for items)
        (§ block
            ;; Write out the type code.
            (Utils/uint32ToByteStreamLE (.. __i type (ordinal)), __stream)
            ;; And now the hash.
            (.. __stream (write (.. __i hash (getReversedBytes))))
        )
        nil
    )

    #_override
    #_public
    (§ method #_"boolean" (§ fn equals) [#_"Object" __o])
    (§ block
        (§ if (== this __o))
        (§ block
            (§ return true)
        )
        (§ if (or (nil? __o) (!= (getClass) (.. __o (getClass)))))
        (§ block
            (§ return false)
        )
        (.. items (equals (.. (§ cast #_"ListMessage" __o) items)))
    )

    #_override
    #_public
    (§ method #_"int" (§ fn hashCode) [])
    (§ block
        (.. items (hashCode))
    )
)

#_(ns org.bitcoinj.core #_"MemoryPoolMessage"
    (:import [java.io IOException OutputStream]))

;;;
 ; <p>The "mempool" message asks a remote peer to announce all transactions in its memory pool, possibly restricted by
 ; any Bloom filter set on the connection.  The list of transaction hashes comes back in an inv message.  Note that
 ; this is different to the {@link TxConfidenceTable} object which doesn't try to keep track of all pending transactions,
 ; it's just a holding area for transactions that a part of the app may find interesting.  The mempool message has
 ; no fields.</p>
 ;
 ; <p>Instances of this class are not safe for use by multiple threads.</p>
 ;;
#_public
(§ class #_"MemoryPoolMessage" (§ extends #_"Message")
    #_override
    #_protected
    (§ method #_"void" (§ fn parse) [])
        (§ throws #_"ProtocolException")
    (§ block
        nil
    )

    #_override
    #_protected
    (§ method #_"void" (§ fn bitcoinSerializeToStream) [#_"OutputStream" __stream])
        (§ throws #_"IOException")
    (§ block
        nil
    )
)

#_(ns org.bitcoinj.core #_"Message"
    (:import [java.io *]
             [java.math BigInteger]
             [java.util Arrays])
    (:import [com.google.common.base Preconditions]
             [org.slf4j Logger LoggerFactory]))

;;;
 ; <p>A Message is a data structure that can be serialized/deserialized using the Bitcoin serialization format.
 ; Specific types of messages that are used both in the block chain, and on the wire, are derived from this class.</p>
 ;
 ; <p>Instances of this class are not safe for use by multiple threads.</p>
 ;;
#_public
#_abstract
(§ class #_"Message"
    #_private
    #_static
    (§ def- #_"Logger" (§ name Message/log) (LoggerFactory/getLogger (§ klass #_"Message")))

    #_public
    #_static
    (§ def #_"int" (§ name Message/MAX_SIZE) 0x02000000) ;; 32MB

    #_public
    #_static
    (§ def #_"int" (§ name Message/UNKNOWN_LENGTH) Integer/MIN_VALUE)

    ;; Useful to ensure serialize/deserialize are consistent with each other.
    #_private
    #_static
    (§ def- #_"boolean" (§ name Message/SELF_CHECK) false)

    ;; The offset is how many bytes into the provided byte array this message payload starts at.
    #_protected
    (§ field #_"int" (§ name offset))
    ;; The cursor keeps track of where we are in the byte array as we parse it.
    ;; Note that it's relative to the start of the array NOT the start of the message payload.
    #_protected
    (§ field #_"int" (§ name cursor))

    #_protected
    (§ field #_"int" (§ name length) Message/UNKNOWN_LENGTH)

    ;; The raw message payload bytes themselves.
    #_protected
    (§ field #_"byte[]" (§ name payload))

    #_protected
    (§ field #_"boolean" (§ name recached) false)
    #_protected
    (§ field #_"MessageSerializer" (§ name serializer))

    #_protected
    (§ field #_"int" (§ name protocolVersion))

    #_protected
    (§ field #_"NetworkParameters" (§ name params))

    #_protected
    (§ constructor #_"Message" [])
    (§ block
        (§ ass (§ name serializer) DummySerializer/DEFAULT)
        this
    )

    #_protected
    (§ constructor #_"Message" [#_"NetworkParameters" __params])
    (§ block
        (§ ass (§ name (.. this params)) __params)
        (§ ass (§ name serializer) (.. __params (getDefaultSerializer)))
        this
    )

    #_protected
    (§ constructor #_"Message" [#_"NetworkParameters" __params, #_"byte[]" __payload, #_"int" __offset, #_"int" __protocolVersion])
        (§ throws #_"ProtocolException")
    (§ block
        (§ this __params, __payload, __offset, __protocolVersion, (.. __params (getDefaultSerializer)), Message/UNKNOWN_LENGTH)
        this
    )

    ;;;
     ; @param params NetworkParameters object.
     ; @param payload Bitcoin protocol formatted byte array containing message content.
     ; @param offset The location of the first payload byte within the array.
     ; @param protocolVersion Bitcoin protocol version.
     ; @param serializer The serializer to use for this message.
     ; @param length The length of message payload if known.  Usually this is provided when deserializing of the wire
     ; as the length will be provided as part of the header.  Set to Message.UNKNOWN_LENGTH, if not known.
     ; @throws ProtocolException
     ;;
    #_protected
    (§ constructor #_"Message" [#_"NetworkParameters" __params, #_"byte[]" __payload, #_"int" __offset, #_"int" __protocolVersion, #_"MessageSerializer" __serializer, #_"int" __length])
        (§ throws #_"ProtocolException")
    (§ block
        (§ ass (§ name (.. this serializer)) __serializer)
        (§ ass (§ name (.. this protocolVersion)) __protocolVersion)
        (§ ass (§ name (.. this params)) __params)
        (§ ass (§ name (.. this payload)) __payload)
        (§ ass (§ name (.. this cursor)) (§ ass (§ name (.. this offset)) __offset))
        (§ ass (§ name (.. this length)) __length)

        (.. this (parse))

        (§ if (== (.. this length) Message/UNKNOWN_LENGTH))
        (§ block
            (Preconditions/checkState false, "Length field has not been set in constructor for %s after parse.", (.. (getClass) (getSimpleName)))
        )

        (§ if Message/SELF_CHECK)
        (§ block
            (.. this (selfCheck __payload, __offset))
        )

        (§ if (not (.. __serializer (isParseRetainMode))))
        (§ block
            (§ ass (§ name (.. this payload)) nil)
        )
        this
    )

    #_private
    (§ method- #_"void" (§ fn selfCheck) [#_"byte[]" __payload, #_"int" __offset])
    (§ block
        (§ if (not (§ insta this #_"VersionMessage")))
        (§ block
            (§ let #_"byte[]" __payloadBytes (§ new #_"byte[]" (§ count cursor - __offset)))
            (§ block
                (System/arraycopy __payload, __offset, __payloadBytes, 0, (- cursor __offset))
                (§ let #_"byte[]" __reserialized (.. this (bitcoinSerialize)))
                (§ block
                    (§ if (not (Arrays/equals __reserialized, __payloadBytes)))
                    (§ block
                        (§ throw (§ new #_"RuntimeException" (str "Serialization is wrong: \n" (.. Utils/HEX (encode __reserialized)) " vs \n" (.. Utils/HEX (encode __payloadBytes)))))
                    )
                )
            )
        )
        nil
    )

    #_protected
    (§ constructor #_"Message" [#_"NetworkParameters" __params, #_"byte[]" __payload, #_"int" __offset])
        (§ throws #_"ProtocolException")
    (§ block
        (§ this __params, __payload, __offset, (.. __params (getProtocolVersionNum NetworkParameters/ProtocolVersion/CURRENT)), (.. __params (getDefaultSerializer)), Message/UNKNOWN_LENGTH)
        this
    )

    #_protected
    (§ constructor #_"Message" [#_"NetworkParameters" __params, #_"byte[]" __payload, #_"int" __offset, #_"MessageSerializer" __serializer, #_"int" __length])
        (§ throws #_"ProtocolException")
    (§ block
        (§ this __params, __payload, __offset, (.. __params (getProtocolVersionNum NetworkParameters/ProtocolVersion/CURRENT)), __serializer, __length)
        this
    )

    ;; These methods handle the serialization/deserialization using the custom Bitcoin protocol.

    #_protected
    #_abstract
    (§ method #_"void" (§ fn parse) [])
        (§ throws #_"ProtocolException")

    ;;;
     ; <p>To be called before any change of internal values including any setters.  This ensures any cached byte array is
     ; removed.<p/>
     ; <p>Child messages of this object (e.g. Transactions belonging to a Block) will not have their internal byte caches
     ; invalidated unless they are also modified internally.</p>
     ;;
    #_protected
    (§ method #_"void" (§ fn unCache) [])
    (§ block
        (§ ass (§ name payload) nil)
        (§ ass (§ name recached) false)
        nil
    )

    #_protected
    (§ method #_"void" (§ fn adjustLength) [#_"int" __newArraySize, #_"int" __adjustment])
    (§ block
        (§ if (== length Message/UNKNOWN_LENGTH))
        (§ block
            (§ return nil)
        )
        ;; Our own length is now unknown if we have an unknown length adjustment.
        (§ if (== __adjustment Message/UNKNOWN_LENGTH))
        (§ block
            (§ ass (§ name length) Message/UNKNOWN_LENGTH)
            (§ return nil)
        )
        (§ ass (§ name length) (+ length __adjustment))
        ;; Check if we will need more bytes to encode the length prefix.
        (§ if (== __newArraySize 1))
        (§ block
            (§ ass (§ name length) (inc length)) ;; The assumption here is we never call adjustLength with the same arraySize as before.
        )
        (§ elseif (!= __newArraySize 0))
        (§ block
            (§ ass (§ name length) (+ length (- (VarInt/sizeOf __newArraySize) (VarInt/sizeOf (dec __newArraySize)))))
        )
        nil
    )

    ;;;
     ; used for unit testing
     ;;
    #_public
    (§ method #_"boolean" (§ fn isCached) [])
    (§ block
        (some? payload)
    )

    #_public
    (§ method #_"boolean" (§ fn isRecached) [])
    (§ block
        recached
    )

    ;;;
     ; Returns a copy of the array returned by {@link Message#unsafeBitcoinSerialize()}, which is safe to mutate.
     ; If you need extra performance and can guarantee you won't write to the array, you can use the unsafe version.
     ;
     ; @return a freshly allocated serialized byte array.
     ;;
    #_public
    (§ method #_"byte[]" (§ fn bitcoinSerialize) [])
    (§ block
        (§ let #_"byte[]" __bytes (.. this (unsafeBitcoinSerialize)))
        (§ block
            (§ let #_"byte[]" __copy (§ new #_"byte[]" (§ count (.. __bytes length))))
            (§ block
                (System/arraycopy __bytes, 0, __copy, 0, (.. __bytes length))
                __copy
            )
        )
    )

    ;;;
     ; Serialize this message to a byte array that conforms to the bitcoin wire protocol.
     ;
     ; This method may return the original byte array used to construct this message if the
     ; following conditions are met:
     ; <ol>
     ; <li>1. The message was parsed from a byte array with parseRetain = true.</li>
     ; <li>2. The message has not been modified.</li>
     ; <li>3. The array had an offset of 0 and no surplus bytes.</li>
     ; </ol>
     ;
     ; If condition 3 is not met then an copy of the relevant portion of the array will be returned.
     ; Otherwise a full serialize will occur.  For this reason you should only use this API
     ; if you can guarantee you will treat the resulting array as read only.
     ;
     ; @return a byte array owned by this object, do NOT mutate it.
     ;;
    #_public
    (§ method #_"byte[]" (§ fn unsafeBitcoinSerialize) [])
    (§ block
        ;; 1st attempt to use a cached array.
        (§ if (some? payload))
        (§ block
            ;; Cached byte array is the entire message with no extras so we can return as is and avoid an array copy.
            (§ if (and (== offset 0) (== length (.. payload length))))
            (§ block
                (§ return payload)
            )

            (§ let #_"byte[]" __buf (§ new #_"byte[]" (§ count length)))
            (§ block
                (System/arraycopy payload, offset, __buf, 0, length)
                (§ return __buf)
            )
        )

        ;; No cached array available so serialize parts by stream.
        (§ let #_"ByteArrayOutputStream" __stream (§ new #_"UnsafeByteArrayOutputStream" (if (< length 32) 32 (+ length 32))))
        (§ block
            (§ try )
            (§ block
                (.. this (bitcoinSerializeToStream __stream))
            )
            (§ catch #_"IOException" (§ name _))
            (§ block
                ;; Cannot happen, we are serializing to a memory stream.
            )

            (§ if (.. serializer (isParseRetainMode)))
            (§ block
                ;; A free set of steak knives!
                ;; If there happens to be a call to this method we gain an opportunity to recache
                ;; the byte array and in this case it contains no bytes from parent messages.
                ;; This give a dual benefit.  Releasing references to the larger byte array so that it
                ;; it is more likely to be GC'd.  And preventing double serializations.  E.g. calculating
                ;; merkle root calls this method.  It is will frequently happen prior to serializing the block
                ;; which means another call to bitcoinSerialize is coming.  If we didn't recache then internal
                ;; serialization would occur a 2nd time and every subsequent time the message is serialized.
                (§ ass (§ name payload) (.. __stream (toByteArray)))
                (§ ass (§ name cursor) (- cursor offset))
                (§ ass (§ name offset) 0)
                (§ ass (§ name recached) true)
                (§ ass (§ name length) (.. payload length))
                (§ return payload)
            )
            ;; Record length.  If this Message wasn't parsed from a byte stream it won't have length field
            ;; set (except for static length message types).  Setting it makes future streaming more efficient
            ;; because we can preallocate the ByteArrayOutputStream buffer and avoid resizing.
            (§ let #_"byte[]" __buf (.. __stream (toByteArray)))
            (§ block
                (§ ass (§ name length) (.. __buf length))
                __buf
            )
        )
    )

    ;;;
     ; Serialize this message to the provided OutputStream using the bitcoin wire format.
     ;
     ; @param stream
     ; @throws IOException
     ;;
    #_public
    (§ method #_"void" (§ fn bitcoinSerialize) [#_"OutputStream" __stream])
        (§ throws #_"IOException")
    (§ block
        ;; 1st check for cached bytes.
        (§ if (and (some? payload) (!= length Message/UNKNOWN_LENGTH)))
        (§ block
            (.. __stream (write payload, offset, length))
            (§ return nil)
        )

        (.. this (bitcoinSerializeToStream __stream))
        nil
    )

    ;;;
     ; Serializes this message to the provided stream.  If you just want the raw bytes use bitcoinSerialize().
     ;;
    #_protected
    (§ method #_"void" (§ fn bitcoinSerializeToStream) [#_"OutputStream" __stream])
        (§ throws #_"IOException")
    (§ block
        (.. Message/log (error "Error: {} class has not implemented bitcoinSerializeToStream method.  Generating message with no payload", (getClass)))
        nil
    )

    ;;;
     ; This method is a NOP for all classes except Block and Transaction.  It is only declared in Message
     ; so BitcoinSerializer can avoid 2 instanceof checks + a casting.
     ;;
    #_public
    (§ method #_"Sha256Hash" (§ fn getHash) [])
    (§ block
        (§ throw (§ new #_"UnsupportedOperationException"))
    )

    ;;;
     ; This returns a correct value by parsing the message.
     ;;
    #_public
    (§ method #_"int" (§ fn getMessageSize) [])
    (§ block
        (§ if (== length Message/UNKNOWN_LENGTH))
        (§ block
            (Preconditions/checkState false, "Length field has not been set in %s.", (.. (getClass) (getSimpleName)))
        )
        length
    )

    #_protected
    (§ method #_"long" (§ fn readUint32) [])
        (§ throws #_"ProtocolException")
    (§ block
        (§ try )
        (§ block
            (§ let #_"long" __u (Utils/readUint32 payload, cursor))
            (§ block
                (§ ass (§ name cursor) (+ cursor 4))
                (§ return __u)
            )
        )
        (§ catch #_"ArrayIndexOutOfBoundsException" __e)
        (§ block
            (§ throw (§ new #_"ProtocolException" __e))
        )
    )

    #_protected
    (§ method #_"long" (§ fn readInt64) [])
        (§ throws #_"ProtocolException")
    (§ block
        (§ try )
        (§ block
            (§ let #_"long" __u (Utils/readInt64 payload, cursor))
            (§ block
                (§ ass (§ name cursor) (+ cursor 8))
                (§ return __u)
            )
        )
        (§ catch #_"ArrayIndexOutOfBoundsException" __e)
        (§ block
            (§ throw (§ new #_"ProtocolException" __e))
        )
    )

    #_protected
    (§ method #_"BigInteger" (§ fn readUint64) [])
        (§ throws #_"ProtocolException")
    (§ block
        ;; Java does not have an unsigned 64 bit type. So scrape it off the wire then flip.
        (§ new #_"BigInteger" (Utils/reverseBytes (.. this (readBytes 8))))
    )

    #_protected
    (§ method #_"long" (§ fn readVarInt) [])
        (§ throws #_"ProtocolException")
    (§ block
        (.. this (readVarInt 0))
    )

    #_protected
    (§ method #_"long" (§ fn readVarInt) [#_"int" __offset])
        (§ throws #_"ProtocolException")
    (§ block
        (§ try )
        (§ block
            (§ let #_"VarInt" __varint (§ new #_"VarInt" payload, (+ cursor __offset)))
            (§ block
                (§ ass (§ name cursor) (+ cursor __offset (.. __varint (getOriginalSizeInBytes))))
                (§ return (.. __varint value))
            )
        )
        (§ catch #_"ArrayIndexOutOfBoundsException" __e)
        (§ block
            (§ throw (§ new #_"ProtocolException" __e))
        )
    )

    #_protected
    (§ method #_"byte[]" (§ fn readBytes) [#_"int" __length])
        (§ throws #_"ProtocolException")
    (§ block
        (§ if (< Message/MAX_SIZE __length))
        (§ block
            (§ throw (§ new #_"ProtocolException" (str "Claimed value length too large: " __length)))
        )

        (§ try )
        (§ block
            (§ let #_"byte[]" __b (§ new #_"byte[]" (§ count __length)))
            (§ block
                (System/arraycopy payload, cursor, __b, 0, __length)
                (§ ass (§ name cursor) (+ cursor __length))
                (§ return __b)
            )
        )
        (§ catch #_"IndexOutOfBoundsException" __e)
        (§ block
            (§ throw (§ new #_"ProtocolException" __e))
        )
    )

    #_protected
    (§ method #_"byte[]" (§ fn readByteArray) [])
        (§ throws #_"ProtocolException")
    (§ block
        (§ let #_"long" __len (.. this (readVarInt)))
        (§ block
            (.. this (readBytes (§ cast #_"int" __len)))
        )
    )

    #_protected
    (§ method #_"String" (§ fn readStr) [])
        (§ throws #_"ProtocolException")
    (§ block
        (§ let #_"long" __length (.. this (readVarInt)))
        (§ block
            (if (== __length 0) "" (Utils/toString (.. this (readBytes (§ cast #_"int" __length))), "UTF-8")) ;; optimization for empty strings
        )
    )

    #_protected
    (§ method #_"Sha256Hash" (§ fn readHash) [])
        (§ throws #_"ProtocolException")
    (§ block
        ;; We have to flip it around, as it's been read off the wire in little endian.
        ;; Not the most efficient way to do this but the clearest.
        (Sha256Hash/wrapReversed (.. this (readBytes 32)))
    )

    #_protected
    (§ method #_"boolean" (§ fn hasMoreBytes) [])
    (§ block
        (< cursor (.. payload length))
    )

    ;;; Network parameters this message was created with. ;;
    #_public
    (§ method #_"NetworkParameters" (§ fn getParams) [])
    (§ block
        params
    )

    ;;;
     ; Set the serializer for this message when deserialized by Java.
     ;;
    #_private
    (§ method- #_"void" (§ fn readObject) [#_"java.io.ObjectInputStream" __in])
        (§ throws #_"IOException", #_"ClassNotFoundException")
    (§ block
        (.. __in (defaultReadObject))
        (§ if (some? params))
        (§ block
            (§ ass (§ name (.. this serializer)) (.. params (getDefaultSerializer)))
        )
        nil
    )
)

#_(ns org.bitcoinj.core #_"MessageSerializer"
    (:import [java.io IOException OutputStream]
             [java.nio BufferUnderflowException ByteBuffer]))

;;;
 ; Generic interface for classes which serialize/deserialize messages.
 ; Implementing classes should be immutable.
 ;;
#_public
#_abstract
(§ class #_"MessageSerializer"
    ;;;
     ; Reads a message from the given ByteBuffer and returns it.
     ;;
    #_public
    #_abstract
    (§ method #_"Message" (§ fn deserialize) [#_"ByteBuffer" __in])
        (§ throws #_"ProtocolException", #_"IOException", #_"UnsupportedOperationException")

    ;;;
     ; Deserializes only the header in case packet meta data is needed before decoding
     ; the payload.  This method assumes you have already called seekPastMagicBytes().
     ;;
    #_public
    #_abstract
    (§ method #_"BitcoinSerializer.BitcoinPacketHeader" (§ fn deserializeHeader) [#_"ByteBuffer" __in])
        (§ throws #_"ProtocolException", #_"IOException", #_"UnsupportedOperationException")

    ;;;
     ; Deserialize payload only.  You must provide a header, typically obtained by calling
     ; {@link BitcoinSerializer#deserializeHeader}.
     ;;
    #_public
    #_abstract
    (§ method #_"Message" (§ fn deserializePayload) [#_"BitcoinSerializer.BitcoinPacketHeader" __header, #_"ByteBuffer" __in])
        (§ throws #_"ProtocolException", #_"BufferUnderflowException", #_"UnsupportedOperationException")

    ;;;
     ; Whether the serializer will produce cached mode Messages.
     ;;
    #_public
    #_abstract
    (§ method #_"boolean" (§ fn isParseRetainMode) [])

    ;;;
     ; Make an address message from the payload.  Extension point for alternative
     ; serialization format support.
     ;;
    #_public
    #_abstract
    (§ method #_"AddressMessage" (§ fn makeAddressMessage) [#_"byte[]" __payloadBytes, #_"int" __length])
        (§ throws #_"ProtocolException", #_"UnsupportedOperationException")

    ;;;
     ; Make an alert message from the payload.  Extension point for alternative
     ; serialization format support.
     ;;
    #_public
    #_abstract
    (§ method #_"Message" (§ fn makeAlertMessage) [#_"byte[]" __payloadBytes])
        (§ throws #_"ProtocolException", #_"UnsupportedOperationException")

    ;;;
     ; Make a block from the payload, using an offset of zero and the payload
     ; length as block length.
     ;;
    #_public
    (§ method #_"Block" (§ fn makeBlock) [#_"byte[]" __payloadBytes])
        (§ throws #_"ProtocolException")
    (§ block
        (.. this (makeBlock __payloadBytes, 0, (.. __payloadBytes length)))
    )

    ;;;
     ; Make a block from the payload, using an offset of zero and the provided
     ; length as block length.
     ;;
    #_public
    (§ method #_"Block" (§ fn makeBlock) [#_"byte[]" __payloadBytes, #_"int" __length])
        (§ throws #_"ProtocolException")
    (§ block
        (.. this (makeBlock __payloadBytes, 0, __length))
    )

    ;;;
     ; Make a block from the payload, using an offset of zero and the provided
     ; length as block length.  Extension point for alternative
     ; serialization format support.
     ;;
    #_public
    #_abstract
    (§ method #_"Block" (§ fn makeBlock) [#_"byte[]" __payloadBytes, #_"int" __offset, #_"int" __length])
        (§ throws #_"ProtocolException", #_"UnsupportedOperationException")

    ;;;
     ; Make an filter message from the payload.  Extension point for alternative
     ; serialization format support.
     ;;
    #_public
    #_abstract
    (§ method #_"Message" (§ fn makeBloomFilter) [#_"byte[]" __payloadBytes])
        (§ throws #_"ProtocolException", #_"UnsupportedOperationException")

    ;;;
     ; Make a filtered block from the payload.  Extension point for alternative
     ; serialization format support.
     ;;
    #_public
    #_abstract
    (§ method #_"FilteredBlock" (§ fn makeFilteredBlock) [#_"byte[]" __payloadBytes])
        (§ throws #_"ProtocolException", #_"UnsupportedOperationException")

    ;;;
     ; Make an inventory message from the payload.  Extension point for alternative
     ; serialization format support.
     ;;
    #_public
    #_abstract
    (§ method #_"InventoryMessage" (§ fn makeInventoryMessage) [#_"byte[]" __payloadBytes, #_"int" __length])
        (§ throws #_"ProtocolException", #_"UnsupportedOperationException")

    ;;;
     ; Make a transaction from the payload.  Extension point for alternative
     ; serialization format support.
     ;
     ; @throws UnsupportedOperationException if this serializer/deserializer
     ; does not support deserialization.  This can occur either because it's a dummy
     ; serializer (i.e. for messages with no network parameters), or because
     ; it does not support deserializing transactions.
     ;;
    #_public
    #_abstract
    (§ method #_"Transaction" (§ fn makeTransaction) [#_"byte[]" __payloadBytes, #_"int" __offset, #_"int" __length, #_"byte[]" __hash])
        (§ throws #_"ProtocolException", #_"UnsupportedOperationException")

    ;;;
     ; Make a transaction from the payload.  Extension point for alternative
     ; serialization format support.
     ;
     ; @throws UnsupportedOperationException if this serializer/deserializer
     ; does not support deserialization.  This can occur either because it's a dummy
     ; serializer (i.e. for messages with no network parameters), or because
     ; it does not support deserializing transactions.
     ;;
    #_public
    (§ method #_"Transaction" (§ fn makeTransaction) [#_"byte[]" __payloadBytes])
        (§ throws #_"ProtocolException", #_"UnsupportedOperationException")
    (§ block
        (.. this (makeTransaction __payloadBytes, 0))
    )

    ;;;
     ; Make a transaction from the payload.  Extension point for alternative
     ; serialization format support.
     ;
     ; @throws UnsupportedOperationException if this serializer/deserializer
     ; does not support deserialization.  This can occur either because it's a dummy
     ; serializer (i.e. for messages with no network parameters), or because
     ; it does not support deserializing transactions.
     ;;
    #_public
    (§ method #_"Transaction" (§ fn makeTransaction) [#_"byte[]" __payloadBytes, #_"int" __offset])
        (§ throws #_"ProtocolException")
    (§ block
        (.. this (makeTransaction __payloadBytes, __offset, (.. __payloadBytes length), nil))
    )

    #_public
    #_abstract
    (§ method #_"void" (§ fn seekPastMagicBytes) [#_"ByteBuffer" __in])
        (§ throws #_"BufferUnderflowException")

    ;;;
     ; Writes message to to the output stream.
     ;
     ; @throws UnsupportedOperationException if this serializer/deserializer
     ; does not support serialization.  This can occur either because it's a dummy
     ; serializer (i.e. for messages with no network parameters), or because
     ; it does not support serializing the given message.
     ;;
    #_public
    #_abstract
    (§ method #_"void" (§ fn serialize) [#_"String" __name, #_"byte[]" __message, #_"OutputStream" __out])
        (§ throws #_"IOException", #_"UnsupportedOperationException")

    ;;;
     ; Writes message to to the output stream.
     ;
     ; @throws UnsupportedOperationException if this serializer/deserializer
     ; does not support serialization.  This can occur either because it's a dummy
     ; serializer (i.e. for messages with no network parameters), or because
     ; it does not support serializing the given message.
     ;;
    #_public
    #_abstract
    (§ method #_"void" (§ fn serialize) [#_"Message" __message, #_"OutputStream" __out])
        (§ throws #_"IOException", #_"UnsupportedOperationException")
)

#_(ns org.bitcoinj.core #_"Monetary"
    (:import [java.io Serializable]))

;;;
 ; Classes implementing this interface represent a monetary value, such as a Bitcoin or fiat amount.
 ;;
#_public
(§ interface #_"Monetary" (§ extends #_"Serializable")
    ;;;
     ; Returns the absolute value of exponent of the value of a "smallest unit" in scientific notation.
     ; For Bitcoin, a satoshi is worth 1E-8 so this would be 8.
     ;;
    (§ method #_"int" (§ fn smallestUnitExponent) [])

    ;;;
     ; Returns the number of "smallest units" of this monetary value.
     ; For Bitcoin, this would be the number of satoshis.
     ;;
    (§ method #_"long" (§ fn getValue) [])

    (§ method #_"int" (§ fn signum) [])
)

#_(ns org.bitcoinj.core #_"NetworkParameters"
    (:import [java.io *]
             [java.math *]
             [java.util *])
    (:import [com.google.common.base Objects])
   (:require [org.bitcoinj.core Block Coin StoredBlock VerificationException]
             [org.bitcoinj.net.discovery *]
             [org.bitcoinj.params *]
             [org.bitcoinj.script *]
             [org.bitcoinj.store BlockStore BlockStoreException]
             [org.bitcoinj.utils MonetaryFormat VersionTally]))

;;;
 ; <p>NetworkParameters contains the data needed for working with an instantiation of a Bitcoin chain.</p>
 ;
 ; <p>This is an abstract class, concrete instantiations can be found in the params package.  There are four:
 ; one for the main network ({@link MainNetParams}), one for the public test network, and two others that are
 ; intended for unit testing and local app development purposes.  Although this class contains some aliases for
 ; them, you are encouraged to call the static get() methods on each specific params class directly.</p>
 ;;
#_public
#_abstract
(§ class #_"NetworkParameters"
    ;;;
     ; The alert signing key originally owned by Satoshi, and now passed on to Gavin along with a few others.
     ;;
    #_public
    #_static
    (§ def #_"byte[]" (§ name NetworkParameters/SATOSHI_KEY) (.. Utils/HEX (decode "04fc9702847840aaf195de8442ebecedf5b095cdbb9bc716bda9110971b28a49e0ead8564ff0db22209e0374782c093bb899692d524e9d6a6956e7c5ecbcd68284")))

    ;;; The string returned by getId() for the main, production network where people trade things. ;;
    #_public
    #_static
    (§ def #_"String" (§ name NetworkParameters/ID_MAINNET) "org.bitcoin.production")
    ;;; The string returned by getId() for the testnet. ;;
    #_public
    #_static
    (§ def #_"String" (§ name NetworkParameters/ID_TESTNET) "org.bitcoin.test")
    ;;; Unit test network. ;;
    #_public
    #_static
    (§ def #_"String" (§ name NetworkParameters/ID_UNITTESTNET) "org.bitcoinj.unittest")

    ;;; The string used by the payment protocol to represent the main net. ;;
    #_public
    #_static
    (§ def #_"String" (§ name NetworkParameters/PAYMENT_PROTOCOL_ID_MAINNET) "main")
    ;;; The string used by the payment protocol to represent the test net. ;;
    #_public
    #_static
    (§ def #_"String" (§ name NetworkParameters/PAYMENT_PROTOCOL_ID_TESTNET) "test")
    ;;; The string used by the payment protocol to represent unit testing (note that this is non-standard). ;;
    #_public
    #_static
    (§ def #_"String" (§ name NetworkParameters/PAYMENT_PROTOCOL_ID_UNIT_TESTS) "unittest")

    ;; TODO: Seed nodes should be here as well.

    #_protected
    (§ field #_"Block" (§ name genesisBlock))
    #_protected
    (§ field #_"BigInteger" (§ name maxTarget))
    #_protected
    (§ field #_"int" (§ name port))
    #_protected
    (§ field #_"long" (§ name packetMagic)) ;; Indicates message origin network and is used to seek to the next message when stream state is unknown.
    #_protected
    (§ field #_"int" (§ name addressHeader))
    #_protected
    (§ field #_"int" (§ name p2shHeader))
    #_protected
    (§ field #_"int" (§ name interval))
    #_protected
    (§ field #_"int" (§ name targetTimespan))
    #_protected
    (§ field #_"byte[]" (§ name alertSigningKey))
    #_protected
    (§ field #_"int" (§ name bip32HeaderPub))
    #_protected
    (§ field #_"int" (§ name bip32HeaderPriv))

    ;;; Used to check majorities for block version upgrade. ;;
    #_protected
    (§ field #_"int" (§ name majorityEnforceBlockUpgrade))
    #_protected
    (§ field #_"int" (§ name majorityRejectBlockOutdated))
    #_protected
    (§ field #_"int" (§ name majorityWindow))

    ;;;
     ; See getId().  This may be null for old deserialized wallets.  In that case we derive it heuristically
     ; by looking at the port number.
     ;;
    #_protected
    (§ field #_"String" (§ name id))

    ;;;
     ; The depth of blocks required for a coinbase transaction to be spendable.
     ;;
    #_protected
    (§ field #_"int" (§ name spendableCoinbaseDepth))
    #_protected
    (§ field #_"int" (§ name subsidyDecreaseBlockCount))

    #_protected
    (§ field #_"int[]" (§ name acceptableAddressCodes))
    #_protected
    (§ field #_"String[]" (§ name dnsSeeds))
    #_protected
    (§ field #_"int[]" (§ name addrSeeds))
    #_protected
    (§ field #_"Map<Integer, Sha256Hash>" (§ name checkpoints) (§ new #_"HashMap<>"))
    #_protected
    #_transient
    (§ field #_"MessageSerializer" (§ name defaultSerializer))

    #_protected
    (§ constructor #_"NetworkParameters" [])
    (§ block
        (§ ass (§ name alertSigningKey) NetworkParameters/SATOSHI_KEY)
        (§ ass (§ name genesisBlock) (NetworkParameters/createGenesis this))
        this
    )

    #_private
    #_static
    (§ defn- #_"Block" (§ fn NetworkParameters/createGenesis) [#_"NetworkParameters" __n])
    (§ block
        (§ let #_"Block" __genesisBlock (§ new #_"Block" __n, Block/BLOCK_VERSION_GENESIS))
        (§ block
            (§ let #_"Transaction" __t (§ new #_"Transaction" __n))
            (§ block
                (§ try )
                (§ block
                    ;; A script containing the difficulty bits and the following message: "The Times 03/Jan/2009 Chancellor on brink of second bailout for banks".
                    (§ let #_"byte[]" __bytes (.. Utils/HEX (decode "04ffff001d0104455468652054696d65732030332f4a616e2f32303039204368616e63656c6c6f72206f6e206272696e6b206f66207365636f6e64206261696c6f757420666f722062616e6b73")))
                    (§ block
                        (.. __t (addInput (§ new #_"TransactionInput" __n, __t, __bytes)))
                        (§ let #_"ByteArrayOutputStream" __scriptPubKeyBytes (§ new #_"ByteArrayOutputStream"))
                        (§ block
                            (Script/writeBytes __scriptPubKeyBytes, (.. Utils/HEX (decode "04678afdb0fe5548271967f1a67130b7105cd6a828e03909a67962e0ea1f61deb649f6bc3f4cef38c4f35504e51ec112de5c384df7ba0b8d578a4c702b6bf11d5f")))
                            (.. __scriptPubKeyBytes (write ScriptOpCodes/OP_CHECKSIG))
                            (.. __t (addOutput (§ new #_"TransactionOutput" __n, __t, Coin/FIFTY_COINS, (.. __scriptPubKeyBytes (toByteArray)))))
                        )
                    )
                )
                (§ catch #_"Exception" __e)
                (§ block
                    (§ throw (§ new #_"RuntimeException" __e)) ;; Cannot happen.
                )
                (.. __genesisBlock (addTransaction __t))
                __genesisBlock
            )
        )
    )

    #_public
    #_static
    (§ def #_"int" (§ name NetworkParameters/TARGET_TIMESPAN) (* 14 24 60 60)) ;; 2 weeks per difficulty cycle, on average.
    #_public
    #_static
    (§ def #_"int" (§ name NetworkParameters/TARGET_SPACING) (* 10 60)) ;; 10 minutes per block.
    #_public
    #_static
    (§ def #_"int" (§ name NetworkParameters/INTERVAL) (/ NetworkParameters/TARGET_TIMESPAN NetworkParameters/TARGET_SPACING))

    ;;;
     ; Blocks with a timestamp after this should enforce BIP 16, aka "Pay to script hash".  This BIP changed
     ; the network rules in a soft-forking manner, that is, blocks that don't follow the rules are accepted
     ; but not mined upon and thus will be quickly re-orged out as long as the majority are enforcing the rule.
     ;;
    #_public
    #_static
    (§ def #_"int" (§ name NetworkParameters/BIP16_ENFORCE_TIME) 1333238400)

    ;;;
     ; The maximum number of coins to be generated.
     ;;
    #_public
    #_static
    (§ def #_"long" (§ name NetworkParameters/MAX_COINS) 21000000)

    ;;;
     ; The maximum money to be generated.
     ;;
    #_public
    #_static
    (§ def #_"Coin" (§ name NetworkParameters/MAX_MONEY) (.. Coin/COIN (multiply NetworkParameters/MAX_COINS)))

    ;;;
     ; A Java package style string acting as unique ID for these parameters.
     ;;
    #_public
    (§ method #_"String" (§ fn getId) [])
    (§ block
        id
    )

    #_public
    #_abstract
    (§ method #_"String" (§ fn getPaymentProtocolId) [])

    #_override
    #_public
    (§ method #_"boolean" (§ fn equals) [#_"Object" __o])
    (§ block
        (§ if (== this __o))
        (§ block
            (§ return true)
        )
        (§ if (or (nil? __o) (!= (getClass) (.. __o (getClass)))))
        (§ block
            (§ return false)
        )
        (.. this (getId) (equals (.. (§ cast #_"NetworkParameters" __o) (getId))))
    )

    #_override
    #_public
    (§ method #_"int" (§ fn hashCode) [])
    (§ block
        (Objects/hashCode (.. this (getId)))
    )

    ;;; Returns the network parameters for the given string ID or NULL if not recognized. ;;
    #_nilable
    #_public
    #_static
    (§ defn #_"NetworkParameters" (§ fn NetworkParameters/fromID) [#_"String" __id])
    (§ block
        (§ if (.. __id (equals NetworkParameters/ID_MAINNET)))
        (§ block
            (§ return (MainNetParams/get))
        )
        (§ if (.. __id (equals NetworkParameters/ID_TESTNET)))
        (§ block
            (§ return (TestNet3Params/get))
        )
        (§ if (.. __id (equals NetworkParameters/ID_UNITTESTNET)))
        (§ block
            (§ return (UnitTestParams/get))
        )
        nil
    )

    ;;; Returns the network parameters for the given string paymentProtocolID or NULL if not recognized. ;;
    #_nilable
    #_public
    #_static
    (§ defn #_"NetworkParameters" (§ fn NetworkParameters/fromPmtProtocolID) [#_"String" __pmtProtocolId])
    (§ block
        (§ if (.. __pmtProtocolId (equals NetworkParameters/PAYMENT_PROTOCOL_ID_MAINNET)))
        (§ block
            (§ return (MainNetParams/get))
        )
        (§ if (.. __pmtProtocolId (equals NetworkParameters/PAYMENT_PROTOCOL_ID_TESTNET)))
        (§ block
            (§ return (TestNet3Params/get))
        )
        (§ if (.. __pmtProtocolId (equals NetworkParameters/PAYMENT_PROTOCOL_ID_UNIT_TESTS)))
        (§ block
            (§ return (UnitTestParams/get))
        )
        nil
    )

    #_public
    (§ method #_"int" (§ fn getSpendableCoinbaseDepth) [])
    (§ block
        spendableCoinbaseDepth
    )

    ;;;
     ; Throws an exception if the block's difficulty is not correct.
     ;
     ; @throws VerificationException if the block's difficulty is not correct.
     ;;
    #_public
    #_abstract
    (§ method #_"void" (§ fn checkDifficultyTransitions) [#_"StoredBlock" __storedPrev, #_"Block" __next, #_"BlockStore" __blockStore])
        (§ throws #_"VerificationException", #_"BlockStoreException")

    ;;;
     ; Returns true if the block height is either not a checkpoint, or is a checkpoint and the hash matches.
     ;;
    #_public
    (§ method #_"boolean" (§ fn passesCheckpoint) [#_"int" __height, #_"Sha256Hash" __hash])
    (§ block
        (§ let #_"Sha256Hash" __checkpointHash (.. checkpoints (get __height)))
        (§ block
            (or (nil? __checkpointHash) (.. __checkpointHash (equals __hash)))
        )
    )

    ;;;
     ; Returns true if the given height has a recorded checkpoint.
     ;;
    #_public
    (§ method #_"boolean" (§ fn isCheckpoint) [#_"int" __height])
    (§ block
        (§ let #_"Sha256Hash" __checkpointHash (.. checkpoints (get __height)))
        (§ block
            (some? __checkpointHash)
        )
    )

    #_public
    (§ method #_"int" (§ fn getSubsidyDecreaseBlockCount) [])
    (§ block
        subsidyDecreaseBlockCount
    )

    ;;; Returns DNS names that when resolved, give IP addresses of active peers. ;;
    #_public
    (§ method #_"String[]" (§ fn getDnsSeeds) [])
    (§ block
        dnsSeeds
    )

    ;;; Returns IP address of active peers. ;;
    #_public
    (§ method #_"int[]" (§ fn getAddrSeeds) [])
    (§ block
        addrSeeds
    )

    ;;;
     ; <p>Genesis block for this chain.</p>
     ;
     ; <p>The first block in every chain is a well known constant shared between all Bitcoin implemenetations.
     ; For a block to be valid, it must be eventually possible to work backwards to the genesis block by following
     ; the prevBlockHash pointers in the block headers.</p>
     ;
     ; <p>The genesis blocks for both test and main networks contain the timestamp of when they were created,
     ; and a message in the coinbase transaction.  It says,
     ; <i>"The Times 03/Jan/2009 Chancellor on brink of second bailout for banks"</i>.</p>
     ;;
    #_public
    (§ method #_"Block" (§ fn getGenesisBlock) [])
    (§ block
        genesisBlock
    )

    ;;; Default TCP port on which to connect to nodes. ;;
    #_public
    (§ method #_"int" (§ fn getPort) [])
    (§ block
        port
    )

    ;;; The header bytes that identify the start of a packet on this network. ;;
    #_public
    (§ method #_"long" (§ fn getPacketMagic) [])
    (§ block
        packetMagic
    )

    ;;;
     ; First byte of a base58 encoded address.  See {@link org.bitcoinj.core.Address}.
     ; This is the same as acceptableAddressCodes[0] and is the one used for "normal" addresses.
     ; Other types of address may be encountered with version codes found in the acceptableAddressCodes array.
     ;;
    #_public
    (§ method #_"int" (§ fn getAddressHeader) [])
    (§ block
        addressHeader
    )

    ;;;
     ; First byte of a base58 encoded P2SH address.  P2SH addresses are defined as part of BIP0013.
     ;;
    #_public
    (§ method #_"int" (§ fn getP2SHHeader) [])
    (§ block
        p2shHeader
    )

    ;;;
     ; How much time in seconds is supposed to pass between "interval" blocks.  If the actual elapsed time is
     ; significantly different from this value, the network difficulty formula will produce a different value.
     ; Both test and main Bitcoin networks use 2 weeks (1209600 seconds).
     ;;
    #_public
    (§ method #_"int" (§ fn getTargetTimespan) [])
    (§ block
        targetTimespan
    )

    ;;;
     ; The version codes that prefix addresses which are acceptable on this network.  Although Satoshi intended these
     ; to be used for "versioning", in fact they are today used to discriminate what kind of data is contained in the
     ; address and to prevent accidentally sending coins across chains which would destroy them.
     ;;
    #_public
    (§ method #_"int[]" (§ fn getAcceptableAddressCodes) [])
    (§ block
        acceptableAddressCodes
    )

    ;;;
     ; If we are running in testnet-in-a-box mode, we allow connections to nodes with 0 non-genesis blocks.
     ;;
    #_public
    (§ method #_"boolean" (§ fn allowEmptyPeerChain) [])
    (§ block
        true
    )

    ;;; How many blocks pass between difficulty adjustment periods.  Bitcoin standardises this to be 2016. ;;
    #_public
    (§ method #_"int" (§ fn getInterval) [])
    (§ block
        interval
    )

    ;;; Maximum target represents the easiest allowable proof of work. ;;
    #_public
    (§ method #_"BigInteger" (§ fn getMaxTarget) [])
    (§ block
        maxTarget
    )

    ;;;
     ; The key used to sign {@link org.bitcoinj.core.AlertMessage}s.
     ; You can use {@link org.bitcoinj.core.ECKey#verify(byte[], byte[], byte[])} to verify signatures using it.
     ;;
    #_public
    (§ method #_"byte[]" (§ fn getAlertSigningKey) [])
    (§ block
        alertSigningKey
    )

    ;;; Returns the 4 byte header for BIP32 (HD) wallet - public key part. ;;
    #_public
    (§ method #_"int" (§ fn getBip32HeaderPub) [])
    (§ block
        bip32HeaderPub
    )

    ;;; Returns the 4 byte header for BIP32 (HD) wallet - private key part. ;;
    #_public
    (§ method #_"int" (§ fn getBip32HeaderPriv) [])
    (§ block
        bip32HeaderPriv
    )

    ;;;
     ; Returns the number of coins that will be produced in total, on this network.
     ; Where not applicable, a very large number of coins is returned
     ; instead (i.e. the main coin issue for Dogecoin).
     ;;
    #_public
    #_abstract
    (§ method #_"Coin" (§ fn getMaxMoney) [])

    ;;;
     ; Any standard (i.e. pay-to-address) output smaller than this value will
     ; most likely be rejected by the network.
     ;;
    #_public
    #_abstract
    (§ method #_"Coin" (§ fn getMinNonDustOutput) [])

    ;;;
     ; The monetary object for this currency.
     ;;
    #_public
    #_abstract
    (§ method #_"MonetaryFormat" (§ fn getMonetaryFormat) [])

    ;;;
     ; Scheme part for URIs, for example "bitcoin".
     ;;
    #_public
    #_abstract
    (§ method #_"String" (§ fn getUriScheme) [])

    ;;;
     ; Returns whether this network has a maximum number of coins (finite supply) or not.
     ; Always returns true for Bitcoin, but exists to be overriden for other networks.
     ;;
    #_public
    #_abstract
    (§ method #_"boolean" (§ fn hasMaxMoney) [])

    ;;;
     ; Return the default serializer for this network.  This is a shared serializer.
     ;;
    #_public
    (§ method #_"MessageSerializer" (§ fn getDefaultSerializer) [])
    (§ block
        ;; Construct a default serializer if we don't have one.
        (§ if (== (.. this defaultSerializer) nil))
        (§ block
            ;; Don't grab a lock unless we absolutely need it.
            (§ sync this)
            (§ block
                ;; Now we have a lock, double check there's still no serializer and create one if so.
                (§ if (== (.. this defaultSerializer) nil))
                (§ block
                    ;; As the serializers are intended to be immutable, creating
                    ;; two due to a race condition should not be a problem, however
                    ;; to be safe we ensure only one exists for each network.
                    (§ ass (§ name (.. this defaultSerializer)) (.. this (getSerializer false)))
                )
            )
        )
        defaultSerializer
    )

    ;;;
     ; Construct and return a custom serializer.
     ;;
    #_public
    #_abstract
    (§ method #_"BitcoinSerializer" (§ fn getSerializer) [#_"boolean" __parseRetain])

    ;;;
     ; The number of blocks in the last {@link getMajorityWindow()} blocks
     ; at which to trigger a notice to the user to upgrade their client, where
     ; the client does not understand those blocks.
     ;;
    #_public
    (§ method #_"int" (§ fn getMajorityEnforceBlockUpgrade) [])
    (§ block
        majorityEnforceBlockUpgrade
    )

    ;;;
     ; The number of blocks in the last {@link getMajorityWindow()} blocks
     ; at which to enforce the requirement that all new blocks are of the
     ; newer type (i.e. outdated blocks are rejected).
     ;;
    #_public
    (§ method #_"int" (§ fn getMajorityRejectBlockOutdated) [])
    (§ block
        majorityRejectBlockOutdated
    )

    ;;;
     ; The sampling window from which the version numbers of blocks are taken
     ; in order to determine if a new block version is now the majority.
     ;;
    #_public
    (§ method #_"int" (§ fn getMajorityWindow) [])
    (§ block
        majorityWindow
    )

    ;;;
     ; The flags indicating which block validation tests should be applied to
     ; the given block.  Enables support for alternative blockchains which enable
     ; tests based on different criteria.
     ;
     ; @param block Block to determine flags for.
     ; @param height Height of the block, if known, null otherwise.  Returned
     ; tests should be a safe subset if block height is unknown.
     ;;
    #_public
    (§ method #_"EnumSet<Block.VerifyFlag>" (§ fn getBlockVerificationFlags) [#_"Block" __block, #_"VersionTally" __tally, #_"Integer" __height])
    (§ block
        (§ let #_"EnumSet<Block.VerifyFlag>" __flags (EnumSet/noneOf (§ klass #_"Block.VerifyFlag")))
        (§ block

            (§ if (.. __block (isBIP34)))
            (§ block
                (§ let #_"Integer" __count (.. __tally (getCountAtOrAbove Block/BLOCK_VERSION_BIP34)))
                (§ block
                    (§ if (and (some? __count) (<= (.. this (getMajorityEnforceBlockUpgrade)) __count)))
                    (§ block
                        (.. __flags (add Block/VerifyFlag/HEIGHT_IN_COINBASE))
                    )
                )
            )

            __flags
        )
    )

    ;;;
     ; The flags indicating which script validation tests should be applied to
     ; the given transaction.  Enables support for alternative blockchains which enable
     ; tests based on different criteria.
     ;
     ; @param block Block the transaction belongs to.
     ; @param transaction to determine flags for.
     ; @param height Height of the block, if known, null otherwise.  Returned
     ; tests should be a safe subset if block height is unknown.
     ;;
    #_public
    (§ method #_"EnumSet<Script.VerifyFlag>" (§ fn getTransactionVerificationFlags) [#_"Block" __block, #_"Transaction" __transaction, #_"VersionTally" __tally, #_"Integer" __height])
    (§ block
        (§ let #_"EnumSet<Script.VerifyFlag>" __verifyFlags (EnumSet/noneOf (§ klass #_"Script.VerifyFlag")))
        (§ block
            (§ if (<= NetworkParameters/BIP16_ENFORCE_TIME (.. __block (getTimeSeconds))))
            (§ block
                (.. __verifyFlags (add Script/VerifyFlag/P2SH))
            )

            ;; Start enforcing CHECKLOCKTIMEVERIFY (BIP65) for block.nVersion=4 blocks,
            ;; when 75% of the network has been upgraded:
            (§ if (and (<= Block/BLOCK_VERSION_BIP65 (.. __block (getVersion))) (< (.. this (getMajorityEnforceBlockUpgrade)) (.. __tally (getCountAtOrAbove Block/BLOCK_VERSION_BIP65)))))
            (§ block
                (.. __verifyFlags (add Script/VerifyFlag/CHECKLOCKTIMEVERIFY))
            )

            __verifyFlags
        )
    )

    #_public
    #_abstract
    (§ method #_"int" (§ fn getProtocolVersionNum) [#_"ProtocolVersion" __version])

    #_public
    #_static
    (§ enum #_"ProtocolVersion"
        (§ item (MINIMUM 70000))
        (§ item (PONG 60001))
        (§ item (BLOOM_FILTER 70000))
        (§ item (CURRENT 70001))

        #_private
        (§ field- #_"int" (§ name bitcoinProtocol))

        (§ constructor #_"ProtocolVersion" [#_"int" __bitcoinProtocol])
        (§ block
            (§ ass (§ name (.. this bitcoinProtocol)) __bitcoinProtocol)
            this
        )

        #_public
        (§ method #_"int" (§ fn getBitcoinProtocolVersion) [])
        (§ block
            bitcoinProtocol
        )
    )
)

#_(ns org.bitcoinj.core #_"NotFoundMessage"
    (:import [java.util ArrayList List]))

;;;
 ; <p>Sent by a peer when a getdata request doesn't find the requested data in the mempool.  It has the same format
 ; as an inventory message and lists the hashes of the missing items.</p>
 ;
 ; <p>Instances of this class are not safe for use by multiple threads.</p>
 ;;
#_public
(§ class #_"NotFoundMessage" (§ extends #_"InventoryMessage")
    #_public
    #_static
    (§ def #_"int" (§ name NotFoundMessage/MIN_PROTOCOL_VERSION) 70001)

    #_public
    (§ constructor #_"NotFoundMessage" [#_"NetworkParameters" __params])
    (§ block
        (§ super __params)
        this
    )

    #_public
    (§ constructor #_"NotFoundMessage" [#_"NetworkParameters" __params, #_"byte[]" __payloadBytes])
        (§ throws #_"ProtocolException")
    (§ block
        (§ super __params, __payloadBytes)
        this
    )

    #_public
    (§ constructor #_"NotFoundMessage" [#_"NetworkParameters" __params, #_"List<InventoryItem>" __items])
    (§ block
        (§ super __params)
        (§ ass (§ name (.. this items)) (§ new #_"ArrayList<>" __items))
        this
    )
)

#_(ns org.bitcoinj.core #_"PartialMerkleTree"
    (:import [java.io IOException OutputStream]
             [java.util ArrayList Arrays List])
    (:import [com.google.common.base Objects])
    (:import [org.bitcoinj.core Utils]))

;;;
 ; <p>A data structure that contains proofs of block inclusion for one or more transactions, in an efficient manner.</p>
 ;
 ; <p>The encoding works as follows: we traverse the tree in depth-first order, storing a bit for each traversed node,
 ; signifying whether the node is the parent of at least one matched leaf txid (or a matched txid itself).  In case we
 ; are at the leaf level, or this bit is 0, its merkle node hash is stored, and its children are not explored further.
 ; Otherwise, no hash is stored, but we recurse into both (or the only) child branch.  During decoding, the same
 ; depth-first traversal is performed, consuming bits and hashes as they were written during encoding.</p>
 ;
 ; <p>The serialization is fixed and provides a hard guarantee about the encoded size,
 ; <tt>SIZE &lt;= 10 + ceil(32.25 * N)</tt> where N represents the number of leaf nodes of the partial tree.
 ; N itself is bounded by:</p>
 ;
 ; N &lt;= total_transactions
 ; N &lt;= 1 + matched_transactions * tree_height
 ;
 ; <p><pre>The serialization format:
 ;  - uint32     total_transactions (4 bytes)
 ;  - varint     number of hashes (1-3 bytes)
 ;  - uint256[]  hashes in depth-first order (&lt;= 32*N bytes)
 ;  - varint     number of bytes of flag bits (1-3 bytes)
 ;  - byte[]     flag bits, packed per 8 in a byte, least significant bit first (&lt;= 2*N-1 bits)
 ; The size constraints follow from this.</pre></p>
 ;
 ; <p>Instances of this class are not safe for use by multiple threads.</p>
 ;;
#_public
(§ class #_"PartialMerkleTree" (§ extends #_"Message")
    ;; the total number of transactions in the block
    #_private
    (§ field- #_"int" (§ name transactionCount))

    ;; node-is-parent-of-matched-txid bits
    #_private
    (§ field- #_"byte[]" (§ name matchedChildBits))

    ;; txids and internal hashes
    #_private
    (§ field- #_"List<Sha256Hash>" (§ name hashes))

    #_public
    (§ constructor #_"PartialMerkleTree" [#_"NetworkParameters" __params, #_"byte[]" __payloadBytes, #_"int" __offset])
        (§ throws #_"ProtocolException")
    (§ block
        (§ super __params, __payloadBytes, __offset)
        this
    )

    ;;;
     ; Constructs a new PMT with the given bit set (little endian) and the raw list of hashes including internal hashes,
     ; taking ownership of the list.
     ;;
    #_public
    (§ constructor #_"PartialMerkleTree" [#_"NetworkParameters" __params, #_"byte[]" __bits, #_"List<Sha256Hash>" __hashes, #_"int" __origTxCount])
    (§ block
        (§ super __params)

        (§ ass (§ name (.. this matchedChildBits)) __bits)
        (§ ass (§ name (.. this hashes)) __hashes)
        (§ ass (§ name (.. this transactionCount)) __origTxCount)
        this
    )

    ;;;
     ; Calculates a PMT given the list of leaf hashes and which leaves need to be included.  The relevant interior hashes
     ; are calculated and a new PMT returned.
     ;;
    #_public
    #_static
    (§ defn #_"PartialMerkleTree" (§ fn PartialMerkleTree/buildFromLeaves) [#_"NetworkParameters" __params, #_"byte[]" __includeBits, #_"List<Sha256Hash>" __allLeafHashes])
    (§ block
        ;; Calculate height of the tree.
        (§ let #_"int" __height 0)
        (§ block
            (§ while (< 1 (PartialMerkleTree/getTreeWidth (.. __allLeafHashes (size)), __height)))
            (§ block
                (§ ass __height (inc __height))
            )
            (§ let #_"List<Boolean>" __bitList (§ new #_"ArrayList<>"))
            (§ block
                (§ let #_"List<Sha256Hash>" __hashes (§ new #_"ArrayList<>"))
                (§ block
                    (PartialMerkleTree/traverseAndBuild __height, 0, __allLeafHashes, __includeBits, __bitList, __hashes)
                    (§ let #_"byte[]" __bits (§ new #_"byte[]" (§ count (§ cast #_"int" (Math/ceil (/ (.. __bitList (size)) 8.0))))))
                    (§ block
                        (§ for (§ var #_"int" __i 0) :for (< __i (.. __bitList (size))) :for (§ recur (inc __i)))
                        (§ block
                            (§ if (.. __bitList (get __i)))
                            (§ block
                                (Utils/setBitLE __bits, __i)
                            )
                        )
                        (§ new #_"PartialMerkleTree" __params, __bits, __hashes, (.. __allLeafHashes (size)))
                    )
                )
            )
        )
    )

    #_override
    #_public
    (§ method #_"void" (§ fn bitcoinSerializeToStream) [#_"OutputStream" __stream])
        (§ throws #_"IOException")
    (§ block
        (Utils/uint32ToByteStreamLE transactionCount, __stream)

        (.. __stream (write (.. (§ new #_"VarInt" (.. hashes (size))) (encode))))
        (§ for (§ var #_"Sha256Hash" __hash) :for hashes)
        (§ block
            (.. __stream (write (.. __hash (getReversedBytes))))
        )

        (.. __stream (write (.. (§ new #_"VarInt" (.. matchedChildBits length)) (encode))))
        (.. __stream (write matchedChildBits))
        nil
    )

    #_override
    #_protected
    (§ method #_"void" (§ fn parse) [])
        (§ throws #_"ProtocolException")
    (§ block
        (§ ass (§ name transactionCount) (§ cast #_"int" (.. this (readUint32))))

        (§ let #_"int" __nHashes (§ cast #_"int" (.. this (readVarInt))))
        (§ block
            (§ ass (§ name hashes) (§ new #_"ArrayList<>" __nHashes))
            (§ for (§ var #_"int" __i 0) :for (< __i __nHashes) :for (§ recur (inc __i)))
            (§ block
                (.. hashes (add (.. this (readHash))))
            )

            (§ let #_"int" __nFlagBytes (§ cast #_"int" (.. this (readVarInt))))
            (§ block
                (§ ass (§ name matchedChildBits) (.. this (readBytes __nFlagBytes)))

                (§ ass (§ name length) (- cursor offset))
                nil
            )
        )
    )

    ;; Based on CPartialMerkleTree::TraverseAndBuild in Bitcoin Core.
    #_private
    #_static
    (§ defn- #_"void" (§ fn PartialMerkleTree/traverseAndBuild) [#_"int" __height, #_"int" __pos, #_"List<Sha256Hash>" __allLeafHashes, #_"byte[]" __includeBits, #_"List<Boolean>" __matchedChildBits, #_"List<Sha256Hash>" __resultHashes])
    (§ block
        (§ let #_"boolean" __parentOfMatch false)
        (§ block
            ;; Is this node a parent of at least one matched hash?
            (§ for (§ var #_"int" __p (<< __pos __height)) :for (and (< __p (<< (inc __pos) __height)) (< __p (.. __allLeafHashes (size)))) :for (§ recur (inc __p)))
            (§ block
                (§ if (Utils/checkBitLE __includeBits, __p))
                (§ block
                    (§ ass __parentOfMatch true)
                    (§ break )
                )
            )
            ;; Store as a flag bit.
            (.. __matchedChildBits (add __parentOfMatch))
            (§ if (or (== __height 0) (not __parentOfMatch)))
            (§ block
                ;; If at height 0, or nothing interesting below, store hash and stop.
                (.. __resultHashes (add (PartialMerkleTree/calcHash __height, __pos, __allLeafHashes)))
            )
            (§ else )
            (§ block
                ;; Otherwise descend into the subtrees.
                (§ let #_"int" __h (dec __height))
                (§ block
                    (§ let #_"int" __p (* __pos 2))
                    (§ block
                        (PartialMerkleTree/traverseAndBuild __h, __p, __allLeafHashes, __includeBits, __matchedChildBits, __resultHashes)
                        (§ if (< (inc __p) (PartialMerkleTree/getTreeWidth (.. __allLeafHashes (size)), __h)))
                        (§ block
                            (PartialMerkleTree/traverseAndBuild __h, (inc __p), __allLeafHashes, __includeBits, __matchedChildBits, __resultHashes)
                        )
                    )
                )
            )
            nil
        )
    )

    #_private
    #_static
    (§ defn- #_"Sha256Hash" (§ fn PartialMerkleTree/calcHash) [#_"int" __height, #_"int" __pos, #_"List<Sha256Hash>" __hashes])
    (§ block
        ;; Hash at height 0 is just the regular tx hash itself.
        (§ if (== __height 0))
        (§ block
            (§ return (.. __hashes (get __pos)))
        )

        (§ let #_"int" __h (dec __height))
        (§ block
            (§ let #_"int" __p (* __pos 2))
            (§ block
                (§ let #_"Sha256Hash" __left (PartialMerkleTree/calcHash __h, __p, __hashes))
                (§ block
                    ;; Calculate right hash if not beyond the end of the array - copy left hash otherwise.
                    (§ let #_"Sha256Hash" __right (if (< (inc __p) (PartialMerkleTree/getTreeWidth (.. __hashes (size)), __h)) (PartialMerkleTree/calcHash __h, (inc __p), __hashes) __left))
                    (§ block

                        (PartialMerkleTree/combineLeftRight (.. __left (getBytes)), (.. __right (getBytes)))
                    )
                )
            )
        )
    )

    ;; Helper function to efficiently calculate the number of nodes at given height in the merkle tree.
    #_private
    #_static
    (§ defn- #_"int" (§ fn PartialMerkleTree/getTreeWidth) [#_"int" __transactionCount, #_"int" __height])
    (§ block
        (>> (- (+ __transactionCount (<< 1 __height)) 1) __height)
    )

    #_private
    #_static
    (§ class #_"ValuesUsed"
        #_public
        (§ field #_"int" (§ name bitsUsed) 0)
        (§ field #_"int" (§ name hashesUsed) 0)
    )

    ;; Recursive function that traverses tree nodes, consuming the bits and hashes produced by TraverseAndBuild.
    ;; It returns the hash of the respective node.
    #_private
    (§ method- #_"Sha256Hash" (§ fn recursiveExtractHashes) [#_"int" __height, #_"int" __pos, #_"ValuesUsed" __used, #_"List<Sha256Hash>" __matchedHashes])
        (§ throws #_"VerificationException")
    (§ block
        ;; overflowed bits array - failure
        (§ if (<= (* (.. matchedChildBits length) 8) (.. __used bitsUsed)))
        (§ block
            (§ throw (§ new #_"VerificationException" "PartialMerkleTree overflowed its bits array"))
        )

        (§ let #_"boolean" __parentOfMatch (Utils/checkBitLE matchedChildBits, (.. __used bitsUsed)))
        (§ block
            (§ ass (§ name (.. __used bitsUsed)) (inc (.. __used bitsUsed)))

            ;; if at height 0, or nothing interesting below, use stored hash and do not descend
            (§ if (or (== __height 0) (not __parentOfMatch)))
            (§ block
                ;; overflowed hash array - failure
                (§ if (<= (.. hashes (size)) (.. __used hashesUsed)))
                (§ block
                    (§ throw (§ new #_"VerificationException" "PartialMerkleTree overflowed its hash array"))
                )

                (§ let #_"Sha256Hash" __hash (.. hashes (get (.. __used hashesUsed))))
                (§ block
                    (§ ass (§ name (.. __used hashesUsed)) (inc (.. __used hashesUsed)))
                    ;; in case of height 0, we have a matched txid
                    (§ if (and (== __height 0) __parentOfMatch))
                    (§ block
                        (.. __matchedHashes (add __hash))
                    )

                    (§ return __hash)
                )
            )
            (§ else )
            (§ block
                ;; otherwise, descend into the subtrees to extract matched txids and hashes
                (§ let #_"byte[]" __left (.. this (recursiveExtractHashes (dec __height), (* __pos 2), __used, __matchedHashes) (getBytes)))
                (§ block
                    (§ let #_"byte[]" __right)
                    (§ block
                        (§ if (< (inc (* __pos 2)) (PartialMerkleTree/getTreeWidth transactionCount, (dec __height))))
                        (§ block
                            (§ ass __right (.. this (recursiveExtractHashes (dec __height), (inc (* __pos 2)), __used, __matchedHashes) (getBytes)))
                            (§ if (Arrays/equals __right, __left))
                            (§ block
                                (§ throw (§ new #_"VerificationException" "Invalid merkle tree with duplicated left/right branches"))
                            )
                        )
                        (§ else )
                        (§ block
                            (§ ass __right __left)
                        )
                        ;; and combine them before returning
                        (§ return (PartialMerkleTree/combineLeftRight __left, __right))
                    )
                )
            )
        )
    )

    #_private
    #_static
    (§ defn- #_"Sha256Hash" (§ fn PartialMerkleTree/combineLeftRight) [#_"byte[]" __left, #_"byte[]" __right])
    (§ block
        (Sha256Hash/wrapReversed (Sha256Hash/hashTwice (Utils/reverseBytes __left), 0, 32, (Utils/reverseBytes __right), 0, 32))
    )

    ;;;
     ; Extracts tx hashes that are in this merkle tree and returns the merkle root of this tree.
     ;
     ; The returned root should be checked against the merkle root contained in the block header for security.
     ;
     ; @param matchedHashesOut A list which will contain the matched txn (will be cleared).
     ; @return the merkle root of this merkle tree.
     ; @throws ProtocolException if this partial merkle tree is invalid.
     ;;
    #_public
    (§ method #_"Sha256Hash" (§ fn getTxnHashAndMerkleRoot) [#_"List<Sha256Hash>" __matchedHashesOut])
        (§ throws #_"VerificationException")
    (§ block
        (.. __matchedHashesOut (clear))

        ;; an empty set will not work
        (§ if (== transactionCount 0))
        (§ block
            (§ throw (§ new #_"VerificationException" "Got a CPartialMerkleTree with 0 transactions"))
        )
        ;; check for excessively high numbers of transactions
        (§ if (< (/ Block/MAX_BLOCK_SIZE 60) transactionCount)) ;; 60 is the lower bound for the size of a serialized CTransaction
        (§ block
            (§ throw (§ new #_"VerificationException" "Got a CPartialMerkleTree with more transactions than is possible"))
        )
        ;; there can never be more hashes provided than one for every txid
        (§ if (< transactionCount (.. hashes (size))))
        (§ block
            (§ throw (§ new #_"VerificationException" "Got a CPartialMerkleTree with more hashes than transactions"))
        )
        ;; there must be at least one bit per node in the partial tree, and at least one node per hash
        (§ if (< (* (.. matchedChildBits length) 8) (.. hashes (size))))
        (§ block
            (§ throw (§ new #_"VerificationException" "Got a CPartialMerkleTree with fewer matched bits than hashes"))
        )

        ;; calculate height of tree
        (§ let #_"int" __height 0)
        (§ block
            (§ while (< 1 (PartialMerkleTree/getTreeWidth transactionCount, __height)))
            (§ block
                (§ ass __height (inc __height))
            )
            ;; traverse the partial tree
            (§ let #_"ValuesUsed" __used (§ new #_"ValuesUsed"))
            (§ block
                (§ let #_"Sha256Hash" __merkleRoot (.. this (recursiveExtractHashes __height, 0, __used, __matchedHashesOut)))
                (§ block
                    ;; verify that all bits were consumed (except for the padding caused by serializing it as a byte sequence)
                    ;; verify that all hashes were consumed
                    (§ if (or (!= (/ (+ (.. __used bitsUsed) 7) 8) (.. matchedChildBits length)) (!= (.. __used hashesUsed) (.. hashes (size)))))
                    (§ block
                        (§ throw (§ new #_"VerificationException" "Got a CPartialMerkleTree that didn't need all the data it provided"))
                    )

                    __merkleRoot
                )
            )
        )
    )

    #_public
    (§ method #_"int" (§ fn getTransactionCount) [])
    (§ block
        transactionCount
    )

    #_override
    #_public
    (§ method #_"boolean" (§ fn equals) [#_"Object" __o])
    (§ block
        (§ if (== this __o))
        (§ block
            (§ return true)
        )
        (§ if (or (nil? __o) (!= (getClass) (.. __o (getClass)))))
        (§ block
            (§ return false)
        )
        (§ let #_"PartialMerkleTree" __other (§ cast #_"PartialMerkleTree" __o))
        (§ block
            (and (== transactionCount (.. __other transactionCount)) (.. hashes (equals (.. __other hashes))) (Arrays/equals matchedChildBits, (.. __other matchedChildBits)))
        )
    )

    #_override
    #_public
    (§ method #_"int" (§ fn hashCode) [])
    (§ block
        (Objects/hashCode transactionCount, hashes, (Arrays/hashCode matchedChildBits))
    )

    #_override
    #_public
    (§ method #_"String" (§ fn toString) [])
    (§ block
        (str "PartialMerkleTree{" "transactionCount=" transactionCount ", matchedChildBits=" (Arrays/toString matchedChildBits) ", hashes=" hashes "}")
    )
)

#_(ns org.bitcoinj.core #_"Peer"
    (:import [java.util *]
             [java.util.concurrent CopyOnWriteArrayList CopyOnWriteArraySet Executor]
             [java.util.concurrent.atomic AtomicInteger]
             [java.util.concurrent.locks ReentrantLock])
    (:import [com.google.common.base * Objects]
             [com.google.common.collect Lists]
             [com.google.common.util.concurrent FutureCallback Futures ListenableFuture SettableFuture]
             [net.jcip.annotations GuardedBy]
             [org.slf4j Logger LoggerFactory])
   (:require [org.bitcoinj.core.listeners *]
             [org.bitcoinj.net StreamConnection]
             [org.bitcoinj.store BlockStore BlockStoreException]
             [org.bitcoinj.utils ListenerRegistration Threading]
             [org.bitcoinj.wallet Wallet]))

;;;
 ; <p>A Peer handles the high level communication with a Bitcoin node, extending a {@link PeerSocketHandler} which
 ; handles low-level message (de)serialization.</p>
 ;
 ; <p>Note that timeouts are handled by the extended
 ; {@link org.bitcoinj.net.AbstractTimeoutHandler} and timeout is automatically disabled (using
 ; {@link org.bitcoinj.net.AbstractTimeoutHandler#setTimeoutEnabled(boolean)}) once the version
 ; handshake completes.</p>
 ;;
#_public
(§ class #_"Peer" (§ extends #_"PeerSocketHandler")
    #_private
    #_static
    (§ def- #_"Logger" (§ name Peer/log) (LoggerFactory/getLogger (§ klass #_"Peer")))

    #_protected
    (§ field #_"ReentrantLock" (§ name lock) (Threading/lock "peer"))

    #_private
    (§ field- #_"NetworkParameters" (§ name params))
    #_private
    (§ field- #_"AbstractBlockChain" (§ name blockChain))
    #_private
    (§ field- #_"Context" (§ name context))

    #_private
    (§ field- #_"CopyOnWriteArrayList<ListenerRegistration<BlocksDownloadedEventListener>>" (§ name blocksDownloadedEventListeners) (§ new #_"CopyOnWriteArrayList<>"))
    #_private
    (§ field- #_"CopyOnWriteArrayList<ListenerRegistration<ChainDownloadStartedEventListener>>" (§ name chainDownloadStartedEventListeners) (§ new #_"CopyOnWriteArrayList<>"))
    #_private
    (§ field- #_"CopyOnWriteArrayList<ListenerRegistration<PeerConnectedEventListener>>" (§ name connectedEventListeners) (§ new #_"CopyOnWriteArrayList<>"))
    #_private
    (§ field- #_"CopyOnWriteArrayList<ListenerRegistration<PeerDisconnectedEventListener>>" (§ name disconnectedEventListeners) (§ new #_"CopyOnWriteArrayList<>"))
    #_private
    (§ field- #_"CopyOnWriteArrayList<ListenerRegistration<GetDataEventListener>>" (§ name getDataEventListeners) (§ new #_"CopyOnWriteArrayList<>"))
    #_private
    (§ field- #_"CopyOnWriteArrayList<ListenerRegistration<PreMessageReceivedEventListener>>" (§ name preMessageReceivedEventListeners) (§ new #_"CopyOnWriteArrayList<>"))
    #_private
    (§ field- #_"CopyOnWriteArrayList<ListenerRegistration<OnTransactionBroadcastListener>>" (§ name onTransactionEventListeners) (§ new #_"CopyOnWriteArrayList<>"))

    ;; Whether to try and download blocks and transactions from this peer.  Set to false by PeerGroup if not the
    ;; primary peer.  This is to avoid redundant work and concurrency problems with downloading the same chain
    ;; in parallel.
    #_private
    #_volatile
    (§ field- #_"boolean" (§ name vDownloadData))
    ;; The version data to announce to the other side of the connections we make: useful for setting our "user agent"
    ;; equivalent and other things.
    #_private
    (§ field- #_"VersionMessage" (§ name versionMessage))
    ;; Maximum depth up to which pending transaction dependencies are downloaded, or 0 for disabled.
    #_private
    #_volatile
    (§ field- #_"int" (§ name vDownloadTxDependencyDepth))
    ;; How many block messages the peer has announced to us.  Peers only announce blocks that attach to their best chain
    ;; so we can use this to calculate the height of the peers chain, by adding it to the initial height in the version
    ;; message.  This method can go wrong if the peer re-orgs onto a shorter (but harder) chain, however, this is rare.
    #_private
    (§ field- #_"AtomicInteger" (§ name blocksAnnounced) (§ new #_"AtomicInteger"))
    ;; Each wallet added to the peer will be notified of downloaded transaction data.
    #_private
    (§ field- #_"CopyOnWriteArrayList<Wallet>" (§ name wallets))
    ;; A time before which we only download block headers, after that point we download block bodies.
    #_guarded-by(§ opt "lock")
    #_private
    (§ field- #_"long" (§ name fastCatchupTimeSecs))
    ;; Whether we are currently downloading headers only or block bodies.  Starts at true.  If the fast catchup time is
    ;; set AND our best block is before that date, switch to false until block headers beyond that point have been
    ;; received at which point it gets set to true again.  This isn't relevant unless vDownloadData is true.
    #_guarded-by(§ opt "lock")
    #_private
    (§ field- #_"boolean" (§ name downloadBlockBodies) true)
    ;; Whether to request filtered blocks instead of full blocks if the protocol version allows for them.
    #_guarded-by(§ opt "lock")
    #_private
    (§ field- #_"boolean" (§ name useFilteredBlocks) false)
    ;; The current Bloom filter set on the connection, used to tell the remote peer what transactions to send us.
    #_private
    #_volatile
    (§ field- #_"BloomFilter" (§ name vBloomFilter))
    ;; The last filtered block we received, we're waiting to fill it out with transactions.
    #_private
    (§ field- #_"FilteredBlock" (§ name currentFilteredBlock) nil)
    ;; How many filtered blocks have been received during the lifetime of this connection.  Used to decide when to
    ;; refresh the server-side side filter by sending a new one (it degrades over time as false positives are added
    ;; on the remote side, see BIP 37 for a discussion of this).
    ;; TODO: Is this still needed?  It should not be since the auto FP tracking logic was added.
    #_private
    (§ field- #_"int" (§ name filteredBlocksReceived))
    ;; If non-null, we should discard incoming filtered blocks because we ran out of keys and are awaiting a new filter
    ;; to be calculated by the PeerGroup.  The discarded block hashes should be added here so we can re-request them
    ;; once we've recalculated and resent a new filter.
    #_guarded-by(§ opt "lock")
    #_nilable
    #_private
    (§ field- #_"List<Sha256Hash>" (§ name awaitingFreshFilter))
    ;; How frequently to refresh the filter.  This should become dynamic in future and calculated depending on the
    ;; actual false positive rate.  For now a good value was determined empirically around January 2013.
    #_private
    #_static
    (§ def- #_"int" (§ name Peer/RESEND_BLOOM_FILTER_BLOCK_COUNT) 25000)
    ;; Keeps track of things we requested internally with getdata but didn't receive yet, so we can avoid re-requests.
    ;; It's not quite the same as getDataFutures, as this is used only for getdatas done as part of downloading
    ;; the chain and so is lighter weight (we just keep a bunch of hashes not futures).
    ;;
    ;; It is important to avoid a nasty edge case where we can end up with parallel chain downloads proceeding
    ;; simultaneously if we were to receive a newly solved block whilst parts of the chain are streaming to us.
    #_private
    (§ field- #_"HashSet<Sha256Hash>" (§ name pendingBlockDownloads) (§ new #_"HashSet<>"))
    ;; Keep references to TransactionConfidence objects for transactions that were announced by a remote peer, but
    ;; which we haven't downloaded yet.  These objects are de-duplicated by the TxConfidenceTable class.
    ;; Once the tx is downloaded (by some peer), the Transaction object that is created will have a reference to
    ;; the confidence object held inside it, and it's then up to the event listeners that receive the Transaction
    ;; to keep it pinned to the root set if they care about this data.
    #_suppress(§ opt "MismatchedQueryAndUpdateOfCollection")
    #_private
    (§ field- #_"HashSet<TransactionConfidence>" (§ name pendingTxDownloads) (§ new #_"HashSet<>"))
    ;; The lowest version number we're willing to accept.  Lower than this will result in an immediate disconnect.
    #_private
    #_volatile
    (§ field- #_"int" (§ name vMinProtocolVersion))
    ;; When an API user explicitly requests a block or transaction from a peer, the InventoryItem is put here
    ;; whilst waiting for the response.  Is not used for downloads Peer generates itself.
    #_private
    #_static
    (§ class #_"GetDataRequest"
        (§ field #_"Sha256Hash" (§ name hash))
        (§ field #_"SettableFuture" (§ name future))

        #_public
        (§ constructor #_"GetDataRequest" [#_"Sha256Hash" __hash, #_"SettableFuture" __future])
        (§ block
            (§ ass (§ name (.. this hash)) __hash)
            (§ ass (§ name (.. this future)) __future)
            this
        )
    )
    ;; TODO: The types/locking should be rationalised a bit.
    #_private
    (§ field- #_"CopyOnWriteArrayList<GetDataRequest>" (§ name getDataFutures))
    #_guarded-by(§ opt "getAddrFutures")
    #_private
    (§ field- #_"LinkedList<SettableFuture<AddressMessage>>" (§ name getAddrFutures))

    #_private
    #_static
    (§ def- #_"int" (§ name Peer/PING_MOVING_AVERAGE_WINDOW) 20)

    ;; Outstanding pings against this peer and how long the last one took to complete.
    #_private
    (§ field- #_"ReentrantLock" (§ name lastPingTimesLock) (§ new #_"ReentrantLock"))
    #_guarded-by(§ opt "lastPingTimesLock")
    #_private
    (§ field- #_"long[]" (§ name lastPingTimes))
    #_private
    (§ field- #_"CopyOnWriteArrayList<PendingPing>" (§ name pendingPings))

    #_private
    #_volatile
    (§ field- #_"VersionMessage" (§ name vPeerVersionMessage))

    ;; A settable future which completes (with this) when the connection is open.
    #_private
    (§ field- #_"SettableFuture<Peer>" (§ name connectionOpenFuture) (SettableFuture/create))
    #_private
    (§ field- #_"SettableFuture<Peer>" (§ name outgoingVersionHandshakeFuture) (SettableFuture/create))
    #_private
    (§ field- #_"SettableFuture<Peer>" (§ name incomingVersionHandshakeFuture) (SettableFuture/create))
    #_private
    (§ field- #_"ListenableFuture<Peer>" (§ name versionHandshakeFuture) (Futures/transform (Futures/allAsList outgoingVersionHandshakeFuture, incomingVersionHandshakeFuture), (§ new #_"Function<List<Peer>, Peer>")
        (§ anon
            #_override
            #_nilable
            #_public
            (§ method #_"Peer" (§ fn apply) [#_nilable #_"List<Peer>" __peers])
            (§ block
                (Preconditions/checkNotNull __peers)
                (Preconditions/checkState (and (== (.. __peers (size)) 2) (== (.. __peers (get 0)) (.. __peers (get 1)))))
                (.. __peers (get 0))
            )
        )))

    ;;;
     ; <p>Construct a peer that reads/writes from the given block chain.</p>
     ;
     ; <p>Note that this does <b>NOT</b> make a connection to the given remoteAddress, it only creates a handler
     ; for a connection.  If you want to create a one-off connection, create a Peer and pass it to
     ; {@link org.bitcoinj.net.NioClientManager#openConnection(java.net.SocketAddress, StreamConnection)}
     ; or
     ; {@link org.bitcoinj.net.NioClient#NioClient(java.net.SocketAddress, StreamConnection, int)}.</p>
     ;
     ; <p>The remoteAddress provided should match the remote address of the peer which is being connected to,
     ; and is used to keep track of which peers relayed transactions and offer more descriptive logging.</p>
     ;;
    #_public
    (§ constructor #_"Peer" [#_"NetworkParameters" __params, #_"VersionMessage" __ver, #_nilable #_"AbstractBlockChain" __chain, #_"PeerAddress" __remoteAddress])
    (§ block
