            (§ block
                (§ ass __m (.. __registration listener (onPreMessageReceived this, __m)))
                (§ if (nil? __m))
                (§ block
                    (§ break )
                )
            )
        )
        (§ if (nil? __m))
        (§ block
            (§ return nil)
        )

        ;; If we are in the middle of receiving transactions as part of a filtered block push from the remote node,
        ;; and we receive something that's not a transaction, then we're done.
        (§ if (and (some? currentFilteredBlock) (not (§ insta __m #_"Transaction"))))
