            (.. this (endFilteredBlock currentFilteredBlock))
            (§ ass (§ name currentFilteredBlock) nil)
        )

        ;; No further communication is possible until version handshake is complete.
        (§ if (not (or (§ insta __m #_"VersionMessage") (§ insta __m #_"VersionAck") (and (.. versionHandshakeFuture (isDone)) (not (.. versionHandshakeFuture (isCancelled)))))))
        (§ block
            (§ throw (§ new #_"ProtocolException" (str "Received " (.. __m (getClass) (getSimpleName)) " before version handshake is complete.")))
        )

        (§ if (§ insta __m #_"Ping"))
        (§ block
            (.. this (processPing (§ cast #_"Ping" __m)))
        )
        (§ elseif (§ insta __m #_"Pong"))
        (§ block
            (.. this (processPong (§ cast #_"Pong" __m)))
        )
        (§ elseif (§ insta __m #_"NotFoundMessage"))
        (§ block
            ;; This is sent to us when we did a getdata on some transactions that aren't in the peers memory pool.
            ;; Because NotFoundMessage is a subclass of InventoryMessage, the test for it must come before the next.
            (.. this (processNotFoundMessage (§ cast #_"NotFoundMessage" __m)))
        )
        (§ elseif (§ insta __m #_"InventoryMessage"))
        (§ block
            (.. this (processInv (§ cast #_"InventoryMessage" __m)))
        )
        (§ elseif (§ insta __m #_"Block"))
        (§ block
            (.. this (processBlock (§ cast #_"Block" __m)))
        )
        (§ elseif (§ insta __m #_"FilteredBlock"))
        (§ block
            (.. this (startFilteredBlock (§ cast #_"FilteredBlock" __m)))
        )
        (§ elseif (§ insta __m #_"Transaction"))
        (§ block
            (.. this (processTransaction (§ cast #_"Transaction" __m)))
        )
        (§ elseif (§ insta __m #_"GetDataMessage"))
        (§ block
            (.. this (processGetData (§ cast #_"GetDataMessage" __m)))
        )
        (§ elseif (§ insta __m #_"AddressMessage"))
        (§ block
            ;; We don't care about addresses of the network right now.  But in future,
            ;; we should save them in the wallet so we don't put too much load on the seed nodes and can
            ;; properly explore the network.
            (.. this (processAddressMessage (§ cast #_"AddressMessage" __m)))
        )
        (§ elseif (§ insta __m #_"HeadersMessage"))
        (§ block
            (.. this (processHeaders (§ cast #_"HeadersMessage" __m)))
        )
        (§ elseif (§ insta __m #_"AlertMessage"))
        (§ block
            (.. this (processAlert (§ cast #_"AlertMessage" __m)))
        )
        (§ elseif (§ insta __m #_"VersionMessage"))
        (§ block
            (.. this (processVersionMessage (§ cast #_"VersionMessage" __m)))
        )
        (§ elseif (§ insta __m #_"VersionAck"))
        (§ block
            (.. this (processVersionAck (§ cast #_"VersionAck" __m)))
        )
        (§ elseif (§ insta __m #_"RejectMessage"))
        (§ block
            (.. Peer/log (error "{} {}: Received {}", this, (.. this (getPeerVersionMessage) subVer), __m))
        )
        (§ else )
        (§ block
            (.. Peer/log (warn "{}: Received unhandled message: {}", this, __m))
        )
        nil
    )

    #_private
    (§ method- #_"void" (§ fn processAddressMessage) [#_"AddressMessage" __m])
    (§ block
        (§ let #_"SettableFuture<AddressMessage>" __future)
        (§ block
            (§ sync getAddrFutures)
            (§ block
                (§ ass __future (.. getAddrFutures (poll)))
                (§ if (nil? __future)) ;; Not an addr message we are waiting for.
                (§ block
                    (§ return nil)
                )
            )
            (.. __future (set __m))
            nil
        )
    )

    #_private
    (§ method- #_"void" (§ fn processVersionMessage) [#_"VersionMessage" __m])
        (§ throws #_"ProtocolException")
    (§ block
        (§ if (some? vPeerVersionMessage))
        (§ block
            (§ throw (§ new #_"ProtocolException" "Got two version messages from peer"))
        )

        (§ ass (§ name vPeerVersionMessage) __m)
        ;; Switch to the new protocol version.
        (§ let #_"long" __peerTime (* (.. vPeerVersionMessage time) 1000))
        (§ block
            (.. Peer/log (info "{}: Got version={}, subVer='{}', services=0x{}, time={}, blocks={}", this, (.. vPeerVersionMessage clientVersion), (.. vPeerVersionMessage subVer), (.. vPeerVersionMessage localServices), (String/format Locale/US, "%tF %tT", __peerTime, __peerTime), (.. vPeerVersionMessage bestHeight)))
            ;; bitcoinj is a client mode implementation.  That means there's not much point in us talking to other client
            ;; mode nodes because we can't download the data from them we need to find/verify transactions.  Some bogus
            ;; implementations claim to have a block chain in their services field but then report a height of zero, filter
            ;; them out here.
            (§ if (or (not (.. vPeerVersionMessage (hasBlockChain))) (and (not (.. params (allowEmptyPeerChain))) (== (.. vPeerVersionMessage bestHeight) 0))))
            (§ block
                ;; Shut down the channel gracefully.
                (.. Peer/log (info "{}: Peer does not have a copy of the block chain.", this))
                (.. this (close))
                (§ return nil)
            )

            (§ if (< (.. vPeerVersionMessage bestHeight) 0))
            (§ block
                ;; In this case, it's a protocol violation.
                (§ throw (§ new #_"ProtocolException" (str "Peer reports invalid best height: " (.. vPeerVersionMessage bestHeight))))
            )
            ;; Now it's our turn ...
            ;; Send an ACK message stating we accept the peers protocol version.
            (.. this (sendMessage (§ new #_"VersionAck")))
            (.. Peer/log (debug "{}: Incoming version handshake complete.", this))
            (.. incomingVersionHandshakeFuture (set this))
            nil
        )
    )

    #_private
    (§ method- #_"void" (§ fn processVersionAck) [#_"VersionAck" __m])
        (§ throws #_"ProtocolException")
    (§ block
        (§ if (nil? vPeerVersionMessage))
        (§ block
            (§ throw (§ new #_"ProtocolException" "got a version ack before version"))
        )

        (§ if (.. outgoingVersionHandshakeFuture (isDone)))
        (§ block
            (§ throw (§ new #_"ProtocolException" "got more than one version ack"))
        )

        (.. Peer/log (debug "{}: Outgoing version handshake complete.", this))
        (.. outgoingVersionHandshakeFuture (set this))
        nil
    )

    #_private
    (§ method- #_"void" (§ fn versionHandshakeComplete) [])
