    (§ block
        ;; Scans the wallets and memory pool for transactions in the getdata message and returns them.
        ;; Runs on peer threads.
        (.. lock (lock))
        (§ try )
        (§ block
            (§ let #_"LinkedList<Message>" __transactions (§ new #_"LinkedList<>"))
            (§ block
                (§ let #_"LinkedList<InventoryItem>" __items (§ new #_"LinkedList<>" (.. __m (getItems))))
                (§ block
                    (§ let #_"Iterator<InventoryItem>" __it (.. __items (iterator)))
                    (§ block
