    ;; Updates the relayTxesBeforeFilter flag of ver.
    #_private
    (§ method- #_"void" (§ fn updateVersionMessageRelayTxesBeforeFilter) [#_"VersionMessage" __ver])
    (§ block
        ;; We will provide the remote node with a bloom filter (i.e. they shouldn't relay yet)
        ;; if chain == null || !chain.shouldVerifyTransactions() and a wallet is added and bloom filters are enabled.
        ;; Note that the default here means that no tx invs will be received if no wallet is ever added.
        (.. lock (lock))
        (§ try )
        (§ block
            (§ let #_"boolean" __spvMode (and (some? chain) (not (.. chain (shouldVerifyTransactions)))))
            (§ block
                (§ let #_"boolean" __willSendFilter (and __spvMode (< 0 (.. peerFilterProviders (size))) vBloomFilteringEnabled))
                (§ block
                    (§ ass (§ name (.. __ver relayTxesBeforeFilter)) (not __willSendFilter))
                )
            )
        )
        (§ finally )
        (§ block
            (.. lock (unlock))
        )
        nil
    )

