    ;;; Returns number of discovered peers. ;;
    #_protected
    (§ method #_"int" (§ fn discoverPeers) [])
        (§ throws #_"PeerDiscoveryException")
    (§ block
        ;; Don't hold the lock whilst doing peer discovery: it can take a long time and cause high API latency.
        (Preconditions/checkState (not (.. lock (isHeldByCurrentThread))))

        (§ let #_"int" __maxPeersToDiscoverCount (.. this vMaxPeersToDiscoverCount))
        (§ block
            (§ let #_"long" __peerDiscoveryTimeoutMillis (.. this vPeerDiscoveryTimeoutMillis))
            (§ block
                (§ let #_"Stopwatch" __watch (Stopwatch/createStarted))
                (§ block
                    (§ let #_"List<PeerAddress>" __addressList (Lists/newLinkedList))
                    (§ block

                        (§ for (§ var #_"PeerDiscovery" __peerDiscovery) :for peerDiscoverers) ;; COW
                        (§ block
                            (§ let #_"InetSocketAddress[]" __addresses (.. __peerDiscovery (getPeers requiredServices, __peerDiscoveryTimeoutMillis, TimeUnit/MILLISECONDS)))
                            (§ block
                                (§ for (§ var #_"InetSocketAddress" __address) :for __addresses)
                                (§ block
                                    (.. __addressList (add (§ new #_"PeerAddress" params, __address)))
                                )
                                (§ if (<= __maxPeersToDiscoverCount (.. __addressList (size))))
                                (§ block
                                    (§ break )
                                )
                            )
                        )

                        (§ if (not (.. __addressList (isEmpty))))
                        (§ block
                            (§ for (§ var #_"PeerAddress" __address) :for __addressList)
                            (§ block
                                (.. this (addInactive __address))
                            )

                            (§ let #_"ImmutableSet<PeerAddress>" __peersDiscoveredSet (ImmutableSet/copyOf __addressList))
                            (§ block
                                (§ for (§ var ListenerRegistration<PeerDiscoveredEventListener> registration) :for peerDiscoveredEventListeners) ;; COW
                                (§ block
                                    (.. registration executor (execute (§ new #_"Runnable")
                                    (§ anon
                                        #_override
                                        #_public
                                        (§ method #_"void" (§ fn run) [])
                                        (§ block
                                            (.. registration listener (onPeersDiscovered __peersDiscoveredSet))
                                            nil
                                        )
                                    )))
                                )
                            )
                        )
                        (.. __watch (stop))
                        (.. PeerGroup/log (info "Peer discovery took {} and returned {} items", __watch, (.. __addressList (size))))
                        (.. __addressList (size))
                    )
                )
            )
        )
    )

