        (§ try )
        (§ block
            (.. bloomFilterMerger (setBloomFilterFPRate __bloomFilterFPRate))
            (.. this (recalculateFastCatchupAndFilter FilterRecalculateMode/SEND_IF_CHANGED))
        )
        (§ finally )
        (§ block
            (.. lock (unlock))
