    (§ method #_"Peer" (§ fn connectTo) [#_"PeerAddress" __address, #_"boolean" __incrementMaxConnections, #_"int" __connectTimeoutMillis])
    (§ block
        (Preconditions/checkState (.. lock (isHeldByCurrentThread)))
        (§ let #_"VersionMessage" __ver (.. this (getVersionMessage) (duplicate)))
        (§ block
            (§ ass (§ name (.. __ver bestHeight)) (if (some? chain) (.. chain (getBestChainHeight)) 0))
            (§ ass (§ name (.. __ver time)) (Utils/currentTimeSeconds))

            (§ let #_"Peer" __peer (.. this (createPeer __address, __ver)))
            (§ block
                (.. __peer (addConnectedEventListener Threading/SAME_THREAD, startupListener))
                (.. __peer (addDisconnectedEventListener Threading/SAME_THREAD, startupListener))
                (.. __peer (setMinProtocolVersion vMinRequiredProtocolVersion))
                (.. pendingPeers (add __peer))

                (§ try )
                (§ block
                    (.. PeerGroup/log (info "Attempting connection to {}     ({} connected, {} pending, {} max)", __address, (.. peers (size)), (.. pendingPeers (size)), maxConnections))
                    (§ let #_"ListenableFuture<SocketAddress>" __future (.. channels (openConnection (.. __address (toSocketAddress)), __peer)))
                    (§ block
                        (§ if (.. __future (isDone)))
                        (§ block
                            (Uninterruptibles/getUninterruptibly __future)
                        )
                    )
                )
                (§ catch #_"ExecutionException" __e)
                (§ block
                    (§ let #_"Throwable" __cause (Throwables/getRootCause __e))
                    (§ block
                        (.. PeerGroup/log (warn (str "Failed to connect to " __address ": " (.. __cause (getMessage)))))
                        (.. this (handlePeerDeath __peer, __cause))
                        (§ return nil)
                    )
                )
                (.. __peer (setSocketTimeout __connectTimeoutMillis))
                ;; When the channel has connected and version negotiated successfully, handleNewPeer will end up being called
                ;; on a worker thread.
                (§ if __incrementMaxConnections)
                (§ block
                    ;; We don't use setMaxConnections here as that would trigger a recursive attempt to establish a new
                    ;; outbound connection.
                    (§ ass (§ name maxConnections) (inc maxConnections))
                )
                __peer
            )
        )
    )

    ;;; You can override this to customise the creation of {@link Peer} objects. ;;
    #_guarded-by(§ opt "lock")
    #_protected
    (§ method #_"Peer" (§ fn createPeer) [#_"PeerAddress" __address, #_"VersionMessage" __ver])
    (§ block
        (§ new #_"Peer" params, __ver, __address, chain, downloadTxDependencyDepth)
    )

    ;;;
     ; Sets the timeout between when a connection attempt to a peer begins and when the version message exchange completes.
     ; This does not apply to currently pending peers.
     ;;
    #_public
    (§ method #_"void" (§ fn setConnectTimeoutMillis) [#_"int" __connectTimeoutMillis])
    (§ block
        (§ ass (§ name (.. this vConnectTimeoutMillis)) __connectTimeoutMillis)
        nil
    )

    ;;;
     ; <p>Start downloading the blockchain from the first available peer.</p>
     ;
     ; <p>If no peers are currently connected, the download will be started once a peer starts.  If the peer dies,
     ; the download will resume with another peer.</p>
     ;
     ; @param listener A listener for chain download events, may not be null.
     ;;
    #_public
    (§ method #_"void" (§ fn startBlockChainDownload) [#_"PeerDataEventListener" __listener])
    (§ block
        (.. lock (lock))
        (§ try )
        (§ block
            (§ if (some? downloadPeer))
            (§ block
                (§ if (!= (.. this downloadListener) nil))
                (§ block
                    (PeerGroup/removeDataEventListenerFromPeer downloadPeer, (.. this downloadListener))
                )
                (§ if (some? __listener))
                (§ block
                    (PeerGroup/addDataEventListenerToPeer Threading/USER_THREAD, downloadPeer, __listener)
                )
            )
            (§ ass (§ name (.. this downloadListener)) __listener)
            ;; TODO: Be more nuanced about which peer to download from.  We can also try
            ;; downloading from multiple peers and handle the case when a new peer comes along
            ;; with a longer chain after we thought we were done.
            (§ if (not (.. peers (isEmpty))))
            (§ block
                (.. this (startBlockChainDownloadFromPeer (.. peers (iterator) (next)))) ;; Will add the new download listener.
            )
        )
        (§ finally )
        (§ block
            (.. lock (unlock))
        )
        nil
    )

    ;;;
     ; Register a data event listener against a single peer (i.e. for blockchain download).
     ; Handling registration/deregistration on peer death/add is outside the scope of these methods.
     ;;
    #_private
    #_static
    (§ defn- #_"void" (§ fn PeerGroup/addDataEventListenerToPeer) [#_"Executor" __executor, #_"Peer" __peer, #_"PeerDataEventListener" __downloadListener])
    (§ block
        (.. __peer (addBlocksDownloadedEventListener __executor, __downloadListener))
        (.. __peer (addChainDownloadStartedEventListener __executor, __downloadListener))
        (.. __peer (addGetDataEventListener __executor, __downloadListener))
        (.. __peer (addPreMessageReceivedEventListener __executor, __downloadListener))
        nil
    )

    ;;;
     ; Remove a registered data event listener against a single peer (i.e. for blockchain download).
     ; Handling registration/deregistration on peer death/add is outside the scope of these methods.
     ;;
    #_private
    #_static
    (§ defn- #_"void" (§ fn PeerGroup/removeDataEventListenerFromPeer) [#_"Peer" __peer, #_"PeerDataEventListener" __listener])
    (§ block
        (.. __peer (removeBlocksDownloadedEventListener __listener))
        (.. __peer (removeChainDownloadStartedEventListener __listener))
        (.. __peer (removeGetDataEventListener __listener))
        (.. __peer (removePreMessageReceivedEventListener __listener))
        nil
    )

    ;;;
     ; Download the blockchain from peers.  Convenience that uses a {@link DownloadProgressTracker} for you.
     ;
     ; This method waits until the download is complete.  "Complete" is defined as downloading
     ; from at least one peer all the blocks that are in that peer's inventory.
     ;;
    #_public
    (§ method #_"void" (§ fn downloadBlockChain) [])
    (§ block
        (§ let #_"DownloadProgressTracker" __listener (§ new #_"DownloadProgressTracker"))
        (§ block
            (.. this (startBlockChainDownload __listener))
            (§ try )
            (§ block
                (.. __listener (await))
            )
            (§ catch #_"InterruptedException" __e)
            (§ block
                (§ throw (§ new #_"RuntimeException" __e))
            )
            nil
        )
    )

    #_protected
    (§ method #_"void" (§ fn handleNewPeer) [#_"Peer" __peer])
    (§ block
        (§ let #_"int" __newSize -1)
        (§ block
            (.. lock (lock))
            (§ try )
            (§ block
                (.. groupBackoff (trackSuccess))
                (.. backoffMap (get (.. __peer (getAddress))) (trackSuccess))

                ;; Sets up the newly connected peer so it can do everything it needs to.
                (.. pendingPeers (remove __peer))
                (.. peers (add __peer))
                (§ ass __newSize (.. peers (size)))
                (.. PeerGroup/log (info "{}: New peer      ({} connected, {} pending, {} max)", __peer, __newSize, (.. pendingPeers (size)), maxConnections))
                ;; Give the peer a filter that can be used to probabilistically drop transactions that
                ;; aren't relevant to our wallet.  We may still receive some false positives, which is
                ;; OK because it helps improve wallet privacy.  Old nodes will just ignore the message.
                (§ if (!= (.. bloomFilterMerger (getLastFilter)) nil))
                (§ block
                    (.. __peer (setBloomFilter (.. bloomFilterMerger (getLastFilter))))
                )
                (.. __peer (setDownloadData false))
                ;; TODO: The peer should calculate the fast catchup time from the added wallets here.
                (§ for (§ var #_"Wallet" __wallet) :for wallets)
                (§ block
                    (.. __peer (addWallet __wallet))
                )
                (§ if (nil? downloadPeer))
                (§ block
                    ;; Kick off chain download if we aren't already doing it.
                    (.. this (setDownloadPeer (.. this (selectDownloadPeer peers))))
                    (§ let #_"boolean" __shouldDownloadChain (and (some? downloadListener) (some? chain)))
                    (§ block
                        (§ if __shouldDownloadChain)
                        (§ block
                            (.. this (startBlockChainDownloadFromPeer downloadPeer))
                        )
                    )
                )
                ;; Make sure the peer knows how to upload transactions that are requested from us.
                (.. __peer (addBlocksDownloadedEventListener Threading/SAME_THREAD, peerListener))
                (.. __peer (addGetDataEventListener Threading/SAME_THREAD, peerListener))

                ;; And set up event listeners for clients.  This will allow them to find out about new transactions and blocks.
                (§ for (§ var #_"ListenerRegistration<BlocksDownloadedEventListener>" __registration) :for peersBlocksDownloadedEventListeners)
                (§ block
                    (.. __peer (addBlocksDownloadedEventListener (.. __registration executor), (.. __registration listener)))
                )
                (§ for (§ var #_"ListenerRegistration<ChainDownloadStartedEventListener>" __registration) :for peersChainDownloadStartedEventListeners)
                (§ block
                    (.. __peer (addChainDownloadStartedEventListener (.. __registration executor), (.. __registration listener)))
                )
                (§ for (§ var #_"ListenerRegistration<PeerConnectedEventListener>" __registration) :for peerConnectedEventListeners)
                (§ block
                    (.. __peer (addConnectedEventListener (.. __registration executor), (.. __registration listener)))
                )

                ;; We intentionally do not add disconnect listeners to peers.
                (§ for (§ var #_"ListenerRegistration<GetDataEventListener>" __registration) :for peerGetDataEventListeners)
                (§ block
                    (.. __peer (addGetDataEventListener (.. __registration executor), (.. __registration listener)))
                )
                (§ for (§ var #_"ListenerRegistration<OnTransactionBroadcastListener>" __registration) :for peersTransactionBroadastEventListeners)
                (§ block
                    (.. __peer (addOnTransactionBroadcastListener (.. __registration executor), (.. __registration listener)))
                )
                (§ for (§ var #_"ListenerRegistration<PreMessageReceivedEventListener>" __registration) :for peersPreMessageReceivedEventListeners)
                (§ block
                    (.. __peer (addPreMessageReceivedEventListener (.. __registration executor), (.. __registration listener)))
                )
            )
            (§ finally )
            (§ block
                (.. lock (unlock))
            )

            (§ let #_"int" __fNewSize __newSize)
            (§ block
                (§ for (§ var ListenerRegistration<PeerConnectedEventListener> registration) :for peerConnectedEventListeners)
                (§ block
                    (.. registration executor (execute (§ new #_"Runnable")
                    (§ anon
                        #_override
                        #_public
                        (§ method #_"void" (§ fn run) [])
                        (§ block
                            (.. registration listener (onPeerConnected __peer, __fNewSize))
                            nil
                        )
                    )))
                )
                nil
            )
        )
    )

    #_nilable
    #_private
    #_volatile
    (§ field- #_"ListenableScheduledFuture<?>" (§ name vPingTask))

    #_suppress(§ opt "NonAtomicOperationOnVolatileField")
    #_private
    (§ method- #_"void" (§ fn setupPinging) [])
    (§ block
        (§ if (<= (.. this (getPingIntervalMsec)) 0))
        (§ block
            (§ return nil) ;; Disabled.
        )

        (§ ass (§ name vPingTask) (.. executor (scheduleAtFixedRate (§ new #_"Runnable")
        (§ anon
            #_override
            #_public
            (§ method #_"void" (§ fn run) [])
            (§ block
                (§ try )
                (§ block
                    (§ if (<= (.. this (getPingIntervalMsec)) 0))
                    (§ block
                        (§ let #_"ListenableScheduledFuture<?>" __task vPingTask)
                        (§ block
                            (§ if (some? __task))
                            (§ block
                                (.. __task (cancel false))
                                (§ ass (§ name vPingTask) nil)
                            )
                            (§ return nil) ;; Disabled.
                        )
                    )
                    (§ for (§ var #_"Peer" __peer) :for (.. this (getConnectedPeers)))
                    (§ block
                        (§ if (< (.. __peer (getPeerVersionMessage) clientVersion) (.. params (getProtocolVersionNum NetworkParameters/ProtocolVersion/PONG))))
                        (§ block
                            (§ continue )
                        )
                        (.. __peer (ping))
                    )
                )
                (§ catch #_"Throwable" __e)
                (§ block
                    (.. PeerGroup/log (error "Exception in ping loop", __e)) ;; The executor swallows exceptions :( ;; )
                )
                nil
            )
        ), (.. this (getPingIntervalMsec)), (.. this (getPingIntervalMsec)), TimeUnit/MILLISECONDS)))
        nil
    )

    #_private
    (§ method- #_"void" (§ fn setDownloadPeer) [#_nilable #_"Peer" __peer])
    (§ block
        (.. lock (lock))
        (§ try )
        (§ block
            (§ if (== downloadPeer __peer))
            (§ block
                (§ return nil)
            )
            (§ if (some? downloadPeer))
            (§ block
                (.. PeerGroup/log (info "Unsetting download peer: {}", downloadPeer))
                (§ if (some? downloadListener))
                (§ block
                    (PeerGroup/removeDataEventListenerFromPeer downloadPeer, downloadListener)
                )
                (.. downloadPeer (setDownloadData false))
            )
            (§ ass (§ name downloadPeer) __peer)
            (§ if (some? downloadPeer))
            (§ block
                (.. PeerGroup/log (info "Setting download peer: {}", downloadPeer))
                (§ if (some? downloadListener))
                (§ block
                    (PeerGroup/addDataEventListenerToPeer Threading/SAME_THREAD, __peer, downloadListener)
                )
                (.. downloadPeer (setDownloadData true))
                (§ if (some? chain))
                (§ block
                    (.. downloadPeer (setDownloadParameters fastCatchupTimeSecs, (some? (.. bloomFilterMerger (getLastFilter)))))
                )
            )
        )
        (§ finally )
        (§ block
            (.. lock (unlock))
        )
        nil
    )

    ;;;
     ; Tells the PeerGroup to download only block headers before a certain time and bodies after that.
     ; Call this before starting block chain download.
     ; Do not use a time > NOW - 1 block, as it will break some block download logic.
     ;;
    #_public
    (§ method #_"void" (§ fn setFastCatchupTimeSecs) [#_"long" __secondsSinceEpoch])
    (§ block
        (.. lock (lock))
        (§ try )
        (§ block
            (Preconditions/checkState (or (nil? chain) (not (.. chain (shouldVerifyTransactions)))), "Fast catchup is incompatible with fully verifying")

            (§ ass (§ name fastCatchupTimeSecs) __secondsSinceEpoch)
            (§ if (some? downloadPeer))
            (§ block
                (.. downloadPeer (setDownloadParameters __secondsSinceEpoch, (some? (.. bloomFilterMerger (getLastFilter)))))
            )
        )
        (§ finally )
        (§ block
            (.. lock (unlock))
        )
        nil
    )

    ;;;
     ; Returns the current fast catchup time.  The contents of blocks before this time won't be downloaded as they
     ; cannot contain any interesting transactions.  If you use {@link PeerGroup#addWallet(Wallet)} this just returns
     ; the min of the wallets earliest key times.
     ; @return a time in seconds since the epoch.
     ;;
    #_public
    (§ method #_"long" (§ fn getFastCatchupTimeSecs) [])
    (§ block
        (.. lock (lock))
        (§ try )
        (§ block
            (§ return fastCatchupTimeSecs)
        )
        (§ finally )
        (§ block
            (.. lock (unlock))
        )
    )

    #_protected
    (§ method #_"void" (§ fn handlePeerDeath) [#_"Peer" __peer, #_nilable #_"Throwable" __exception])
    (§ block
        ;; Peer deaths can occur during startup if a connect attempt after peer discovery aborts immediately.
        (§ if (not (.. this (isRunning))))
        (§ block
            (§ return nil)
        )

        (§ let #_"int" __numPeers)
        (§ block
            (§ let #_"int" __numConnectedPeers 0)
            (§ block
                (.. lock (lock))
                (§ try )
                (§ block
                    (.. pendingPeers (remove __peer))
                    (.. peers (remove __peer))

                    (§ let #_"PeerAddress" __address (.. __peer (getAddress)))
                    (§ block

                        (.. PeerGroup/log (info "{}: Peer died      ({} connected, {} pending, {} max)", __address, (.. peers (size)), (.. pendingPeers (size)), maxConnections))
                        (§ if (== __peer downloadPeer))
                        (§ block
                            (.. PeerGroup/log (info "Download peer died. Picking a new one."))
                            (.. this (setDownloadPeer nil))
                            ;; Pick a new one and possibly tell it to download the chain.
                            (§ let #_"Peer" __newDownloadPeer (.. this (selectDownloadPeer peers)))
                            (§ block
                                (§ if (some? __newDownloadPeer))
                                (§ block
                                    (.. this (setDownloadPeer __newDownloadPeer))
                                    (§ if (some? downloadListener))
                                    (§ block
                                        (.. this (startBlockChainDownloadFromPeer __newDownloadPeer))
                                    )
                                )
                            )
                        )
                        (§ ass __numPeers (+ (.. peers (size)) (.. pendingPeers (size))))
                        (§ ass __numConnectedPeers (.. peers (size)))

                        (.. groupBackoff (trackFailure))

                        (§ if (§ insta __exception #_"NoRouteToHostException"))
                        (§ block
                            (§ if (and (§ insta (.. __address (getAddr)) #_"Inet6Address") (not ipv6Unreachable)))
                            (§ block
                                (§ ass (§ name ipv6Unreachable) true)
                                (.. PeerGroup/log (warn "IPv6 peer connect failed due to routing failure, ignoring IPv6 addresses from now on"))
                            )
                        )
                        (§ else )
                        (§ block
                            (.. backoffMap (get __address) (trackFailure))
                            ;; Put back on inactive list.
                            (.. inactives (offer __address))
                        )

                        (§ if (< __numPeers (.. this (getMaxConnections))))
                        (§ block
                            (.. this (triggerConnections))
                        )
                    )
                )
                (§ finally )
                (§ block
                    (.. lock (unlock))
                )

                (.. __peer (removeBlocksDownloadedEventListener peerListener))
                (.. __peer (removeGetDataEventListener peerListener))
                (§ for (§ var #_"Wallet" __wallet) :for wallets)
                (§ block
                    (.. __peer (removeWallet __wallet))
                )

                (§ let #_"int" __fNumConnectedPeers __numConnectedPeers)
                (§ block

                    (§ for (§ var #_"ListenerRegistration<BlocksDownloadedEventListener>" __registration) :for peersBlocksDownloadedEventListeners)
                    (§ block
                        (.. __peer (removeBlocksDownloadedEventListener (.. __registration listener)))
                    )
                    (§ for (§ var #_"ListenerRegistration<ChainDownloadStartedEventListener>" __registration) :for peersChainDownloadStartedEventListeners)
                    (§ block
                        (.. __peer (removeChainDownloadStartedEventListener (.. __registration listener)))
                    )
                    (§ for (§ var #_"ListenerRegistration<GetDataEventListener>" __registration) :for peerGetDataEventListeners)
                    (§ block
                        (.. __peer (removeGetDataEventListener (.. __registration listener)))
                    )
                    (§ for (§ var #_"ListenerRegistration<PreMessageReceivedEventListener>" __registration) :for peersPreMessageReceivedEventListeners)
                    (§ block
                        (.. __peer (removePreMessageReceivedEventListener (.. __registration listener)))
                    )
                    (§ for (§ var #_"ListenerRegistration<OnTransactionBroadcastListener>" __registration) :for peersTransactionBroadastEventListeners)
                    (§ block
                        (.. __peer (removeOnTransactionBroadcastListener (.. __registration listener)))
                    )

                    (§ for (§ var ListenerRegistration<PeerDisconnectedEventListener> registration) :for peerDisconnectedEventListeners)
                    (§ block
                        (.. registration executor (execute (§ new #_"Runnable")
                        (§ anon
                            #_override
                            #_public
                            (§ method #_"void" (§ fn run) [])
                            (§ block
                                (.. registration listener (onPeerDisconnected __peer, __fNumConnectedPeers))
                                nil
                            )
                        )))
                        (.. __peer (removeDisconnectedEventListener (.. registration listener)))
                    )
                    nil
                )
            )
        )
    )

    #_guarded-by(§ opt "lock")
    #_private
    (§ field- #_"int" (§ name stallPeriodSeconds) 10)
    #_guarded-by(§ opt "lock")
    #_private
    (§ field- #_"int" (§ name stallMinSpeedBytesSec) (* Block/HEADER_SIZE 20))

    ;;;
     ; Configures the stall speed: the speed at which a peer is considered to be serving us the block chain
     ; unacceptably slowly.  Once a peer has served us data slower than the given data rate for the given
     ; number of seconds, it is considered stalled and will be disconnected, forcing the chain download to continue
     ; from a different peer.  The defaults are chosen conservatively, but if you are running on a platform that is
     ; CPU constrained or on a very slow network e.g. EDGE, the default settings may need adjustment to
     ; avoid false stalls.
     ;
     ; @param periodSecs How many seconds the download speed must be below blocksPerSec, defaults to 10.
     ; @param bytesPerSecond Download speed (only blocks/txns count) must be consistently below this for a stall, defaults to the bandwidth required for 20 block headers per second.
     ;;
    #_public
    (§ method #_"void" (§ fn setStallThreshold) [#_"int" __periodSecs, #_"int" __bytesPerSecond])
    (§ block
        (.. lock (lock))
        (§ try )
        (§ block
            (§ ass (§ name stallPeriodSeconds) __periodSecs)
            (§ ass (§ name stallMinSpeedBytesSec) __bytesPerSecond)
        )
        (§ finally )
        (§ block
            (.. lock (unlock))
        )
        nil
    )

    #_private
    (§ class #_"ChainDownloadSpeedCalculator" (§ implements #_"BlocksDownloadedEventListener", #_"Runnable")
        #_private
        (§ field- #_"int" (§ name blocksInLastSecond))
        #_private
        (§ field- #_"int" (§ name txnsInLastSecond))
        #_private
        (§ field- #_"int" (§ name origTxnsInLastSecond))
        #_private
        (§ field- #_"long" (§ name bytesInLastSecond))

        ;; If we take more stalls than this, we assume we're on some kind of terminally slow network and the
        ;; stall threshold just isn't set properly.  We give up on stall disconnects after that.
        #_private
        (§ field- #_"int" (§ name maxStalls) 3)

        ;; How many seconds the peer has until we start measuring its speed.
        #_private
        (§ field- #_"int" (§ name warmupSeconds) -1)

        ;; Used to calculate a moving average.
        #_private
        (§ field- #_"long[]" (§ name samples))
        #_private
        (§ field- #_"int" (§ name cursor))

        #_private
        (§ field- #_"boolean" (§ name syncDone))

        #_override
        #_public
        #_synchronized
        (§ method #_"void" (§ fn onBlocksDownloaded) [#_"Peer" __peer, #_"Block" __block, #_nilable #_"FilteredBlock" __filteredBlock, #_"int" __blocksLeft])
        (§ block
            (§ ass (§ name blocksInLastSecond) (inc blocksInLastSecond))
            (§ ass (§ name bytesInLastSecond) (+ bytesInLastSecond Block/HEADER_SIZE))
            (§ let #_"List<Transaction>" __blockTransactions (.. __block (getTransactions)))
            (§ block
                ;; This whole area of the type hierarchy is a mess.
                (§ let #_"int" __txCount (+ (if (some? __blockTransactions) (.. this (countAndMeasureSize __blockTransactions)) 0) (if (some? __filteredBlock) (.. this (countAndMeasureSize (.. __filteredBlock (getAssociatedTransactions) (values)))) 0)))
                (§ block
                    (§ ass (§ name txnsInLastSecond) (+ txnsInLastSecond __txCount))
                    (§ if (some? __filteredBlock))
                    (§ block
                        (§ ass (§ name origTxnsInLastSecond) (+ origTxnsInLastSecond (.. __filteredBlock (getTransactionCount))))
                    )
                    nil
                )
            )
        )

        #_private
        (§ method- #_"int" (§ fn countAndMeasureSize) [#_"Collection<Transaction>" __transactions])
        (§ block
            (§ for (§ var #_"Transaction" __transaction) :for __transactions)
            (§ block
                (§ ass (§ name bytesInLastSecond) (+ bytesInLastSecond (.. __transaction (getMessageSize))))
            )
            (.. __transactions (size))
        )

        #_override
        #_public
        (§ method #_"void" (§ fn run) [])
        (§ block
            (§ try )
            (§ block
                (.. this (calculate))
            )
            (§ catch #_"Throwable" __e)
            (§ block
                (.. PeerGroup/log (error "Error in speed calculator", __e))
            )
            nil
        )

        #_private
        (§ method- #_"void" (§ fn calculate) [])
        (§ block
            (§ let #_"int" __minSpeedBytesPerSec)
            (§ block
                (§ let #_"int" __period)
                (§ block

                    (.. lock (lock))
                    (§ try )
                    (§ block
                        (§ ass __minSpeedBytesPerSec stallMinSpeedBytesSec)
                        (§ ass __period stallPeriodSeconds)
                    )
                    (§ finally )
                    (§ block
                        (.. lock (unlock))
                    )

                    (§ sync this)
                    (§ block
                        (§ if (or (nil? samples) (!= (.. samples length) __period)))
                        (§ block
                            (§ ass (§ name samples) (§ new #_"long[]" (§ count __period)))
                            ;; *2 because otherwise a single low sample could cause an immediate disconnect which is too harsh.
                            (Arrays/fill samples, (* __minSpeedBytesPerSec 2))
                            (§ ass (§ name warmupSeconds) 15)
                        )

                        (§ let #_"boolean" __behindPeers (and (some? chain) (< (.. chain (getBestChainHeight)) (PeerGroup/getMostCommonChainHeight))))
                        (§ block
                            (§ if (not __behindPeers))
                            (§ block
                                (§ ass (§ name syncDone) true)
                            )
                            (§ if (not syncDone))
                            (§ block
                                (§ if (< warmupSeconds 0))
                                (§ block
                                    ;; Calculate the moving average.
                                    (§ ass (§ name (§ ai samples cursor)) bytesInLastSecond)
                                    (§ ass (§ name cursor) (inc cursor))
                                    (§ if (== cursor (.. samples length)))
                                    (§ block
                                        (§ ass (§ name cursor) 0)
                                    )
                                    (§ let #_"long" __average 0)
                                    (§ block
                                        (§ for (§ var #_"long" __sample) :for samples)
                                        (§ block
                                            (§ ass __average (+ __average __sample))
                                        )
                                        (§ ass __average (/ __average (.. samples length)))

                                        (.. PeerGroup/log (info (String/format Locale/US, "%d blocks/sec, %d tx/sec, %d pre-filtered tx/sec, avg/last %.2f/%.2f kilobytes per sec (stall threshold <%.2f KB/sec for %d seconds)", blocksInLastSecond, txnsInLastSecond, origTxnsInLastSecond, (/ __average 1024.0), (/ bytesInLastSecond 1024.0), (/ __minSpeedBytesPerSec 1024.0), (.. samples length))))

                                        (§ if (and (< __average __minSpeedBytesPerSec) (< 0 maxStalls)))
                                        (§ block
                                            (§ ass (§ name maxStalls) (dec maxStalls))
                                            (§ if (== maxStalls 0))
                                            (§ block
                                                ;; We could consider starting to drop the Bloom filtering FP rate at this point, because
                                                ;; we tried a bunch of peers and no matter what we don't seem to be able to go any faster.
                                                ;; This implies we're bandwidth bottlenecked and might want to start using bandwidth
                                                ;; more effectively.  Of course if there's a MITM that is deliberately throttling us,
                                                ;; this is a good way to make us take away all the FPs from our Bloom filters ... but
                                                ;; as they don't give us a whole lot of privacy either way that's not inherently a big
                                                ;; deal.
                                                (.. PeerGroup/log (warn "This network seems to be slower than the requested stall threshold - won't do stall disconnects any more."))
                                            )
                                            (§ else )
                                            (§ block
                                                (§ let #_"Peer" __peer (.. this (getDownloadPeer)))
                                                (§ block
                                                    (.. PeerGroup/log (warn (String/format Locale/US, "Chain download stalled: received %.2f KB/sec for %d seconds, require average of %.2f KB/sec, disconnecting %s", (/ __average 1024.0), (.. samples length), (/ __minSpeedBytesPerSec 1024.0), __peer)))
                                                    (.. __peer (close))
                                                    ;; Reset the sample buffer and give the next peer time to get going.
                                                    (§ ass (§ name samples) nil)
                                                    (§ ass (§ name warmupSeconds) __period)
                                                )
                                            )
                                        )
                                    )
                                )
                                (§ else )
                                (§ block
                                    (§ ass (§ name warmupSeconds) (dec warmupSeconds))
                                    (§ if (< 0 bytesInLastSecond))
                                    (§ block
                                        (.. PeerGroup/log (info (String/format Locale/US, "%d blocks/sec, %d tx/sec, %d pre-filtered tx/sec, last %.2f kilobytes per sec", blocksInLastSecond, txnsInLastSecond, origTxnsInLastSecond, (/ bytesInLastSecond 1024.0))))
                                    )
                                )
