    #_nilable
    #_private
    (§ field- #_"ChainDownloadSpeedCalculator" (§ name chainDownloadSpeedCalculator))

    #_private
    (§ method- #_"void" (§ fn startBlockChainDownloadFromPeer) [#_"Peer" __peer])
