        (.. lock (lock))
        (§ try )
        (§ block
            (§ let #_"ArrayList<Peer>" __results (§ new #_"ArrayList<Peer>" (.. peers (size))))
            (§ block
                (§ for (§ var #_"Peer" __peer) :for peers)
                (§ block
                    (§ if (<= __protocolVersion (.. __peer (getPeerVersionMessage) clientVersion)))
                    (§ block
                        (.. __results (add __peer))
                    )
                )
                (§ return __results)
            )
        )
        (§ finally )
        (§ block
            (.. lock (unlock))
        )
    )

    ;;;
     ; Returns a future that is triggered when there are at least the requested number of connected peers that support
