        (§ try )
        (§ block
            (§ let #_"List<Peer>" __foundPeers (.. this (findPeersWithServiceMask __mask)))
            (§ block
                (§ if (<= __numPeers (.. __foundPeers (size))))
                (§ block
                    (§ return (Futures/immediateFuture __foundPeers))
