                (§ let #_"SettableFuture<List<Peer>>" __future (SettableFuture/create))
                (§ block
                    (.. this (addConnectedEventListener (§ new #_"PeerConnectedEventListener")
                    (§ anon
                        #_override
                        #_public
                        (§ method #_"void" (§ fn onPeerConnected) [#_"Peer" __peer, #_"int" __peerCount])
                        (§ block
                            (§ let #_"List<Peer>" __peers (.. this (findPeersWithServiceMask __mask)))
                            (§ block
                                (§ if (<= __numPeers (.. __peers (size))))
                                (§ block
                                    (.. __future (set __peers))
                                    (.. this (removeConnectedEventListener this))
                                )
                                nil
                            )
                        )
                    )))
                    (§ return __future)
