        (.. lock (lock))
        (§ try )
        (§ block
            (§ if (== minBroadcastConnections 0))
            (§ block
                (§ let #_"int" __max (.. this (getMaxConnections)))
                (§ block
                    (§ if (<= __max 1))
                    (§ block
                        (§ return __max)
                    )
                    (§ else )
                    (§ block
                        (§ return (§ cast #_"int" (Math/round (* (.. this (getMaxConnections)) 0.8))))
                    )
                )
            )
            (§ return minBroadcastConnections)
        )
        (§ finally )
        (§ block
            (.. lock (unlock))
