        ;; TODO: Some round-tripping could be avoided here.
        (§ let #_"ByteArrayOutputStream" __out (§ new #_"ByteArrayOutputStream"))
        (§ block
            (§ try )
            (§ block
                (.. serializer (serialize __message, __out))
                (.. writeTarget (writeBytes (.. __out (toByteArray))))
            )
            (§ catch #_"IOException" __e)
            (§ block
                (.. this (exceptionCaught __e))
            )
            nil
        )
    )

    ;;;
     ; Closes the connection to the peer if one exists, or immediately closes the connection as soon as it opens.
     ;;
    #_public
    (§ method #_"void" (§ fn close) [])
    (§ block
        (.. lock (lock))
        (§ try )
        (§ block
            (§ if (nil? writeTarget))
            (§ block
                (§ ass (§ name closePending) true)
                (§ return nil)
            )
        )
        (§ finally )
        (§ block
            (.. lock (unlock))
        )
        (.. writeTarget (closeConnection))
        nil
    )

    #_override
    #_protected
    (§ method #_"void" (§ fn timeoutOccurred) [])
    (§ block
        (.. PeerSocketHandler/log (info "{}: Timed out", (.. this (getAddress))))
        (.. this (close))
        nil
    )

    ;;;
     ; Called every time a message is received from the network.
     ;;
    #_protected
    #_abstract
    (§ method #_"void" (§ fn processMessage) [#_"Message" __m])
        (§ throws #_"Exception")

    #_override
    #_public
    (§ method #_"int" (§ fn receiveBytes) [#_"ByteBuffer" __buff])
    (§ block
        (Preconditions/checkArgument (and (== (.. __buff (position)) 0) (<= (+ BitcoinSerializer/BitcoinPacketHeader/HEADER_LENGTH 4) (.. __buff (capacity)))))
        (§ try )
        (§ block
            ;; Repeatedly try to deserialize messages until we hit a BufferUnderflowException.
            (§ let #_"boolean" __firstMessage true)
            (§ block
                (§ while true)
                (§ block
                    ;; If we are in the middle of reading a message, try to fill that one first, before we expect another.
                    (§ if (some? largeReadBuffer))
                    (§ block
                        ;; This can only happen in the first iteration.
                        (Preconditions/checkState __firstMessage)
                        ;; Read new bytes into the largeReadBuffer.
                        (§ let #_"int" __bytesToGet (Math/min (.. __buff (remaining)), (- (.. largeReadBuffer length) largeReadBufferPos)))
                        (§ block
                            (.. __buff (get largeReadBuffer, largeReadBufferPos, __bytesToGet))
                            (§ ass (§ name largeReadBufferPos) (+ largeReadBufferPos __bytesToGet))
                            ;; Check the largeReadBuffer's status.
                            (§ if (== largeReadBufferPos (.. largeReadBuffer length)))
                            (§ block
                                ;; ...processing a message if one is available.
                                (.. this (processMessage (.. serializer (deserializePayload header, (ByteBuffer/wrap largeReadBuffer)))))
                                (§ ass (§ name largeReadBuffer) nil)
                                (§ ass (§ name header) nil)
                                (§ ass __firstMessage false)
                            )
                            (§ else ) ;; ...or just returning if we don't have enough bytes yet.
                            (§ block
                                (§ return (.. __buff (position)))
                            )
                        )
                    )
                    ;; Now try to deserialize any messages left in buff.
                    (§ let #_"Message" __message)
                    (§ block
                        (§ let #_"int" __preSerializePosition (.. __buff (position)))
                        (§ block
                            (§ try )
                            (§ block
                                (§ ass __message (.. serializer (deserialize __buff)))
                            )
                            (§ catch #_"BufferUnderflowException" __e)
                            (§ block
                                ;; If we went through the whole buffer without a full message, we need to use the largeReadBuffer.
                                (§ if (and __firstMessage (== (.. __buff (limit)) (.. __buff (capacity)))))
                                (§ block
                                    ;; ...so reposition the buffer to 0 and read the next message header.
                                    (.. __buff (position 0))
                                    (§ try )
                                    (§ block
                                        (.. serializer (seekPastMagicBytes __buff))
                                        (§ ass (§ name header) (.. serializer (deserializeHeader __buff)))
                                        ;; Initialize the largeReadBuffer with the next message's size and fill it with any bytes left in buff.
                                        (§ ass (§ name largeReadBuffer) (§ new #_"byte[]" (§ count (.. header size))))
                                        (§ ass (§ name largeReadBufferPos) (.. __buff (remaining)))
                                        (.. __buff (get largeReadBuffer, 0, largeReadBufferPos))
                                    )
                                    (§ catch #_"BufferUnderflowException" __e1)
                                    (§ block
                                        ;; If we went through a whole buffer's worth of bytes without getting a header, give up.
                                        ;; In cases where the buff is just really small, we could create a second largeReadBuffer
                                        ;; that we use to deserialize the magic+header, but that is rather complicated when the buff
                                        ;; should probably be at least that big anyway (for efficiency).
                                        (§ throw (§ new #_"ProtocolException" (str "No magic bytes+header after reading " (.. __buff (capacity)) " bytes")))
                                    )
                                )
                                (§ else )
                                (§ block
                                    ;; Reposition the buffer to its original position, which saves us from skipping messages by
                                    ;; seeking past part of the magic bytes before all of them are in the buffer.
                                    (.. __buff (position __preSerializePosition))
                                )
                                (§ return (.. __buff (position)))
                            )
                            ;; Process our freshly deserialized message.
                            (.. this (processMessage __message))
                            (§ ass __firstMessage false)
                        )
                    )
                )
            )
        )
        (§ catch #_"Exception" __e)
        (§ block
            (.. this (exceptionCaught __e))
            ;; Returning -1 also throws an IllegalStateException upstream and kills the connection.
            (§ return -1)
        )
    )

    ;;;
     ; Sets the {@link MessageWriteTarget} used to write messages to the peer.  This should almost never be called,
     ; it is called automatically by {@link org.bitcoinj.net.NioClient} or
     ; {@link org.bitcoinj.net.NioClientManager} once the socket finishes initialization.
     ;;
    #_override
    #_public
    (§ method #_"void" (§ fn setWriteTarget) [#_"MessageWriteTarget" __writeTarget])
    (§ block
        (Preconditions/checkArgument (some? __writeTarget))

        (.. lock (lock))
        (§ let #_"boolean" __closeNow false)
        (§ block
            (§ try )
            (§ block
                (Preconditions/checkArgument (== (.. this writeTarget) nil))

                (§ ass __closeNow closePending)
                (§ ass (§ name (.. this writeTarget)) __writeTarget)
            )
            (§ finally )
            (§ block
                (.. lock (unlock))
            )
            (§ if __closeNow)
            (§ block
                (.. __writeTarget (closeConnection))
            )
            nil
        )
    )

    #_override
    #_public
    (§ method #_"int" (§ fn getMaxMessageSize) [])
    (§ block
        Message/MAX_SIZE
    )

    ;;;
     ; @return the IP address and port of peer.
     ;;
    #_public
    (§ method #_"PeerAddress" (§ fn getAddress) [])
    (§ block
        peerAddress
    )

    ;;; Catch any exceptions, logging them and then closing the channel. ;;
    #_private
    (§ method- #_"void" (§ fn exceptionCaught) [#_"Exception" __e])
    (§ block
        (§ let #_"PeerAddress" __addr (.. this (getAddress)))
        (§ block
            (§ let #_"String" __s (if (some? __addr) (.. __addr (toString)) "?"))
            (§ block
                (§ if (or (§ insta __e #_"ConnectException") (§ insta __e #_"IOException")))
                (§ block
                    ;; Short message for network errors
                    (.. PeerSocketHandler/log (info (str __s " - " (.. __e (getMessage)))))
                )
                (§ else )
                (§ block
                    (.. PeerSocketHandler/log (warn (str __s " - "), __e))
                    (§ let #_"Thread.UncaughtExceptionHandler" __handler Threading/UNCAUGHT_EXCEPTION_HANDLER)
                    (§ block
                        (§ if (some? __handler))
                        (§ block
                            (.. __handler (uncaughtException (Thread/currentThread), __e))
                        )
                    )
                )

                (.. this (close))
                nil
            )
        )
    )
)

