#_(ns org.bitcoinj.core #_"Sha256Hash"
    (:import [java.io File FileInputStream IOException Serializable]
             [java.math BigInteger]
             [java.security MessageDigest NoSuchAlgorithmException]
             [java.util Arrays])
    (:import [com.google.common.base Preconditions]
             [com.google.common.io ByteStreams]
             [com.google.common.primitives *]))

;;;
 ; A Sha256Hash just wraps a byte[] so that equals and hashcode work correctly,
 ; allowing it to be used as keys in a map.
 ; It also checks that the length is correct and provides a bit more type safety.
 ;;
#_public
(§ class #_"Sha256Hash" (§ implements #_"Serializable", #_"Comparable<Sha256Hash>")
    #_public
    #_static
    (§ def #_"int" (§ name Sha256Hash/LENGTH) 32) ;; bytes
    #_public
    #_static
    (§ def #_"Sha256Hash" (§ name Sha256Hash/ZERO_HASH) (Sha256Hash/wrap (§ new #_"byte[]" (§ count Sha256Hash/LENGTH))))

    #_private
    (§ field- #_"byte[]" (§ name bytes))

    ;;;
     ; Use {@link #wrap(byte[])} instead.
     ;;
    #_protected
    (§ constructor #_"Sha256Hash" [#_"byte[]" __rawHashBytes])
    (§ block
        (Preconditions/checkArgument (== (.. __rawHashBytes length) Sha256Hash/LENGTH))
        (§ ass (§ name (.. this bytes)) __rawHashBytes)
        this
    )

    ;;;
     ; Creates a new instance that wraps the given hash value.
     ;
     ; @param rawHashBytes The raw hash bytes to wrap.
     ; @return a new instance.
     ; @throws IllegalArgumentException if the given array length is not exactly 32.
     ;;
    #_public
    #_static
    (§ defn #_"Sha256Hash" (§ fn Sha256Hash/wrap) [#_"byte[]" __rawHashBytes])
    (§ block
        (§ new #_"Sha256Hash" __rawHashBytes)
    )

    ;;;
     ; Creates a new instance that wraps the given hash value (represented as a hex string).
     ;
     ; @param hexString A hash value represented as a hex string.
     ; @return a new instance.
     ; @throws IllegalArgumentException if the given string is not a valid hex string, or if it does not represent exactly 32 bytes.
     ;;
    #_public
    #_static
    (§ defn #_"Sha256Hash" (§ fn Sha256Hash/wrap) [#_"String" __hexString])
    (§ block
        (Sha256Hash/wrap (.. Utils/HEX (decode __hexString)))
    )

    ;;;
     ; Creates a new instance that wraps the given hash value, but with byte order reversed.
     ;
     ; @param rawHashBytes The raw hash bytes to wrap.
     ; @return a new instance.
     ; @throws IllegalArgumentException if the given array length is not exactly 32.
     ;;
    #_public
    #_static
    (§ defn #_"Sha256Hash" (§ fn Sha256Hash/wrapReversed) [#_"byte[]" __rawHashBytes])
    (§ block
        (Sha256Hash/wrap (Utils/reverseBytes __rawHashBytes))
    )

    ;;;
     ; Creates a new instance containing the calculated (one-time) hash of the given bytes.
     ;
     ; @param contents The bytes on which the hash value is calculated.
     ; @return a new instance containing the calculated (one-time) hash.
     ;;
    #_public
    #_static
    (§ defn #_"Sha256Hash" (§ fn Sha256Hash/of) [#_"byte[]" __contents])
    (§ block
        (Sha256Hash/wrap (Sha256Hash/hash __contents))
    )

    ;;;
     ; Creates a new instance containing the hash of the calculated hash of the given bytes.
     ;
     ; @param contents The bytes on which the hash value is calculated.
     ; @return a new instance containing the calculated (two-time) hash.
     ;;
    #_public
    #_static
    (§ defn #_"Sha256Hash" (§ fn Sha256Hash/twiceOf) [#_"byte[]" __contents])
    (§ block
        (Sha256Hash/wrap (Sha256Hash/hashTwice __contents))
    )

    ;;;
     ; Creates a new instance containing the calculated (one-time) hash of the given file's contents.
     ;
     ; The file contents are read fully into memory, so this method should only be used with small files.
     ;
     ; @param file The file on which the hash value is calculated.
     ; @return a new instance containing the calculated (one-time) hash.
     ; @throws IOException if an error occurs while reading the file.
     ;;
    #_public
    #_static
    (§ defn #_"Sha256Hash" (§ fn Sha256Hash/of) [#_"File" __file])
        (§ throws #_"IOException")
    (§ block
        (§ let #_"FileInputStream" __in (§ new #_"FileInputStream" __file))
        (§ block
            (§ try )
            (§ block
                (§ return (Sha256Hash/of (ByteStreams/toByteArray __in)))
            )
            (§ finally )
            (§ block
                (.. __in (close))
            )
        )
    )

    ;;;
     ; Returns a new SHA-256 MessageDigest instance.
     ;
     ; This is a convenience method which wraps the checked
     ; exception that can never occur with a RuntimeException.
     ;
     ; @return a new SHA-256 MessageDigest instance.
     ;;
    #_public
    #_static
    (§ defn #_"MessageDigest" (§ fn Sha256Hash/newDigest) [])
    (§ block
        (§ try )
        (§ block
            (§ return (MessageDigest/getInstance "SHA-256"))
        )
        (§ catch #_"NoSuchAlgorithmException" __e)
        (§ block
            (§ throw (§ new #_"RuntimeException" __e)) ;; Can't happen.
        )
    )

    ;;;
     ; Calculates the SHA-256 hash of the given bytes.
     ;
     ; @param input The bytes to hash.
     ; @return the hash (in big-endian order).
     ;;
    #_public
    #_static
    (§ defn #_"byte[]" (§ fn Sha256Hash/hash) [#_"byte[]" __input])
    (§ block
        (Sha256Hash/hash __input, 0, (.. __input length))
    )

    ;;;
     ; Calculates the SHA-256 hash of the given byte range.
     ;
     ; @param input The array containing the bytes to hash.
     ; @param offset The offset within the array of the bytes to hash.
     ; @param length The number of bytes to hash.
     ; @return the hash (in big-endian order).
     ;;
    #_public
    #_static
    (§ defn #_"byte[]" (§ fn Sha256Hash/hash) [#_"byte[]" __input, #_"int" __offset, #_"int" __length])
    (§ block
        (§ let #_"MessageDigest" __digest (Sha256Hash/newDigest))
        (§ block
            (.. __digest (update __input, __offset, __length))
            (.. __digest (digest))
        )
    )

    ;;;
     ; Calculates the SHA-256 hash of the given bytes,
     ; and then hashes the resulting hash again.
     ;
     ; @param input The bytes to hash.
     ; @return the double-hash (in big-endian order).
     ;;
    #_public
    #_static
    (§ defn #_"byte[]" (§ fn Sha256Hash/hashTwice) [#_"byte[]" __input])
    (§ block
        (Sha256Hash/hashTwice __input, 0, (.. __input length))
    )

    ;;;
     ; Calculates the SHA-256 hash of the given byte range,
     ; and then hashes the resulting hash again.
     ;
     ; @param input The array containing the bytes to hash.
     ; @param offset The offset within the array of the bytes to hash.
     ; @param length The number of bytes to hash.
     ; @return the double-hash (in big-endian order).
     ;;
    #_public
    #_static
    (§ defn #_"byte[]" (§ fn Sha256Hash/hashTwice) [#_"byte[]" __input, #_"int" __offset, #_"int" __length])
    (§ block
        (§ let #_"MessageDigest" __digest (Sha256Hash/newDigest))
        (§ block
            (.. __digest (update __input, __offset, __length))
            (.. __digest (digest (.. __digest (digest))))
        )
    )

    ;;;
     ; Calculates the hash of hash on the given byte ranges.  This is equivalent to
     ; concatenating the two ranges and then passing the result to {@link #hashTwice(byte[])}.
     ;;
    #_public
    #_static
    (§ defn #_"byte[]" (§ fn Sha256Hash/hashTwice) [#_"byte[]" __input1, #_"int" __offset1, #_"int" __length1, #_"byte[]" __input2, #_"int" __offset2, #_"int" __length2])
    (§ block
        (§ let #_"MessageDigest" __digest (Sha256Hash/newDigest))
        (§ block
            (.. __digest (update __input1, __offset1, __length1))
            (.. __digest (update __input2, __offset2, __length2))
            (.. __digest (digest (.. __digest (digest))))
        )
    )

    #_override
    #_public
    (§ method #_"boolean" (§ fn equals) [#_"Object" __o])
    (§ block
        (§ if (== this __o))
        (§ block
            (§ return true)
        )
        (§ if (or (nil? __o) (!= (getClass) (.. __o (getClass)))))
        (§ block
            (§ return false)
        )
        (Arrays/equals bytes, (.. (§ cast #_"Sha256Hash" __o) bytes))
    )

    ;;;
     ; Returns the last four bytes of the wrapped hash.  This should be unique enough to be a suitable hash code
     ; even for blocks, where the goal is to try and get the first bytes to be zeros (i.e. the value as a big integer
     ; lower than the target value).
     ;;
    #_override
    #_public
    (§ method #_"int" (§ fn hashCode) [])
    (§ block
        ;; Use the last 4 bytes, not the first 4 which are often zeros in Bitcoin.
        (Ints/fromBytes (§ ai bytes (- Sha256Hash/LENGTH 4)), (§ ai bytes (- Sha256Hash/LENGTH 3)), (§ ai bytes (- Sha256Hash/LENGTH 2)), (§ ai bytes (dec Sha256Hash/LENGTH)))
    )

    #_override
    #_public
    (§ method #_"String" (§ fn toString) [])
    (§ block
        (.. Utils/HEX (encode bytes))
    )

    ;;;
     ; Returns the bytes interpreted as a positive integer.
     ;;
    #_public
    (§ method #_"BigInteger" (§ fn toBigInteger) [])
    (§ block
        (§ new #_"BigInteger" 1, bytes)
    )

    ;;;
     ; Returns the internal byte array, without defensively copying.  Therefore do NOT modify the returned array.
     ;;
    #_public
    (§ method #_"byte[]" (§ fn getBytes) [])
    (§ block
        bytes
    )

    ;;;
     ; Returns a reversed copy of the internal byte array.
     ;;
    #_public
    (§ method #_"byte[]" (§ fn getReversedBytes) [])
    (§ block
        (Utils/reverseBytes bytes)
    )

    #_override
    #_public
    (§ method #_"int" (§ fn compareTo) [#_"Sha256Hash" __other])
    (§ block
        (§ for (§ var #_"int" __i (dec Sha256Hash/LENGTH)) :for (<= 0 __i) :for (§ recur (dec __i)))
        (§ block
            (§ let #_"int" __thisByte (& 0xff (§ ai (.. this bytes) __i)))
            (§ block
                (§ let #_"int" __otherByte (& 0xff (§ ai (.. __other bytes) __i)))
                (§ block
                    (§ if (> __thisByte __otherByte))
                    (§ block
                        (§ return 1)
                    )
                    (§ if (< __thisByte __otherByte))
                    (§ block
                        (§ return -1)
                    )
                )
            )
        )
        0
    )
)

#_(ns org.bitcoinj.core #_"StoredBlock"
    (:import [java.math BigInteger]
             [java.nio ByteBuffer]
             [java.util Locale])
    (:import [com.google.common.base Objects Preconditions])
   (:require [org.bitcoinj.store BlockStore BlockStoreException]))

;;;
 ; Wraps a {@link Block} object with extra data that can be derived from the block chain but is slow or inconvenient
 ; to calculate.  By storing it alongside the block header we reduce the amount of work required significantly.
 ; Recalculation is slow because the fields are cumulative - to find the chainWork you have to iterate over every
 ; block in the chain back to the genesis block, which involves lots of seeking/loading etc.  So we just keep a
 ; running total: it's a disk space vs cpu/io tradeoff.
 ;
 ; StoredBlocks are put inside a {@link BlockStore} which saves them to memory or disk.
 ;;
#_public
(§ class #_"StoredBlock"
    ;; A BigInteger representing the total amount of work done so far on this chain.  As of May 2011 it takes
    ;; 8 bytes to represent this field, so 12 bytes should be plenty for now.
    #_public
    #_static
    (§ def #_"int" (§ name StoredBlock/CHAIN_WORK_BYTES) 12)
    #_public
    #_static
    (§ def #_"byte[]" (§ name StoredBlock/EMPTY_BYTES) (§ new #_"byte[]" (§ count StoredBlock/CHAIN_WORK_BYTES)))
    #_public
    #_static
    (§ def #_"int" (§ name StoredBlock/COMPACT_SERIALIZED_SIZE) (+ Block/HEADER_SIZE StoredBlock/CHAIN_WORK_BYTES 4)) ;; for height

    #_private
    (§ field- #_"Block" (§ name header))
    #_private
    (§ field- #_"BigInteger" (§ name chainWork))
    #_private
    (§ field- #_"int" (§ name height))

    #_public
    (§ constructor #_"StoredBlock" [#_"Block" __header, #_"BigInteger" __chainWork, #_"int" __height])
    (§ block
        (§ ass (§ name (.. this header)) __header)
        (§ ass (§ name (.. this chainWork)) __chainWork)
        (§ ass (§ name (.. this height)) __height)
        this
    )

    ;;;
     ; The block header this object wraps.  The referenced block object must not have any transactions in it.
     ;;
    #_public
    (§ method #_"Block" (§ fn getHeader) [])
    (§ block
        header
    )

    ;;;
     ; The total sum of work done in this block, and all the blocks below it in the chain.  Work is a measure of
     ; how many tries are needed to solve a block.  If the target is set to cover 10% of the total hash value space,
     ; then the work represented by a block is 10.
     ;;
    #_public
    (§ method #_"BigInteger" (§ fn getChainWork) [])
    (§ block
        chainWork
    )

    ;;;
     ; Position in the chain for this block.  The genesis block has a height of zero.
     ;;
    #_public
    (§ method #_"int" (§ fn getHeight) [])
    (§ block
        height
    )

    ;;; Returns true if this objects chainWork is higher than the others. ;;
    #_public
    (§ method #_"boolean" (§ fn moreWorkThan) [#_"StoredBlock" __other])
    (§ block
        (< 0 (.. chainWork (compareTo (.. __other chainWork))))
    )

    #_override
    #_public
    (§ method #_"boolean" (§ fn equals) [#_"Object" __o])
    (§ block
        (§ if (== this __o))
        (§ block
            (§ return true)
        )
        (§ if (or (nil? __o) (!= (getClass) (.. __o (getClass)))))
        (§ block
            (§ return false)
        )
        (§ let #_"StoredBlock" __other (§ cast #_"StoredBlock" __o))
        (§ block
            (and (.. header (equals (.. __other header))) (.. chainWork (equals (.. __other chainWork))) (== height (.. __other height)))
        )
    )

    #_override
    #_public
    (§ method #_"int" (§ fn hashCode) [])
    (§ block
        (Objects/hashCode header, chainWork, height)
    )

    ;;;
     ; Creates a new StoredBlock, calculating the additional fields by adding to the values in this block.
     ;;
    #_public
    (§ method #_"StoredBlock" (§ fn build) [#_"Block" __block])
        (§ throws #_"VerificationException")
    (§ block
        ;; Stored blocks track total work done in this chain, because the canonical chain is the one that represents
        ;; the largest amount of work done not the tallest.
        (§ let #_"BigInteger" __chainWork (.. this chainWork (add (.. __block (getWork)))))
        (§ block
            (§ let #_"int" __height (inc (.. this height)))
            (§ block
                (§ new #_"StoredBlock" __block, __chainWork, __height)
            )
        )
    )

    ;;;
     ; Given a block store, looks up the previous block in this chain.  Convenience method for doing
     ; <tt>store.get(this.getHeader().getPrevBlockHash())</tt>.
     ;
     ; @return the previous block in the chain or null if it was not found in the store.
     ;;
    #_public
    (§ method #_"StoredBlock" (§ fn getPrev) [#_"BlockStore" __store])
        (§ throws #_"BlockStoreException")
    (§ block
        (.. __store (get (.. this (getHeader) (getPrevBlockHash))))
    )

    ;;; Serializes the stored block to a custom packed format.  Used by {@link CheckpointManager}. ;;
    #_public
    (§ method #_"void" (§ fn serializeCompact) [#_"ByteBuffer" __buffer])
    (§ block
        (§ let #_"byte[]" __chainWorkBytes (.. this (getChainWork) (toByteArray)))
        (§ block
            (Preconditions/checkState (<= (.. __chainWorkBytes length) StoredBlock/CHAIN_WORK_BYTES), "Ran out of space to store chain work!")
            (§ if (< (.. __chainWorkBytes length) StoredBlock/CHAIN_WORK_BYTES))
            (§ block
                ;; Pad to the right size.
                (.. __buffer (put StoredBlock/EMPTY_BYTES, 0, (- StoredBlock/CHAIN_WORK_BYTES (.. __chainWorkBytes length))))
            )
            (.. __buffer (put __chainWorkBytes))
            (.. __buffer (putInt (.. this (getHeight))))
            ;; Using unsafeBitcoinSerialize here can give us direct access to the same bytes we read off the wire,
            ;; avoiding serialization round-trips.
            (§ let #_"byte[]" __bytes (.. this (getHeader) (unsafeBitcoinSerialize)))
            (§ block
                (.. __buffer (put __bytes, 0, Block/HEADER_SIZE)) ;; Trim the trailing 00 byte (zero transactions).
                nil
            )
        )
    )

    ;;; De-serializes the stored block from a custom packed format.  Used by {@link CheckpointManager}. ;;
    #_public
    #_static
    (§ defn #_"StoredBlock" (§ fn StoredBlock/deserializeCompact) [#_"NetworkParameters" __params, #_"ByteBuffer" __buffer])
        (§ throws #_"ProtocolException")
    (§ block
        (§ let #_"byte[]" __chainWorkBytes (§ new #_"byte[]" (§ count StoredBlock/CHAIN_WORK_BYTES)))
        (§ block
            (.. __buffer (get __chainWorkBytes))
            (§ let #_"BigInteger" __chainWork (§ new #_"BigInteger" 1, __chainWorkBytes))
            (§ block
                (§ let #_"int" __height (.. __buffer (getInt))) ;; +4 bytes
                (§ block
                    (§ let #_"byte[]" __header (§ new #_"byte[]" (§ count Block/HEADER_SIZE + 1))) ;; Extra byte for the 00 transactions length.
                    (§ block
                        (.. __buffer (get __header, 0, Block/HEADER_SIZE))
                        (§ new #_"StoredBlock" (.. __params (getDefaultSerializer) (makeBlock __header)), __chainWork, __height)
                    )
                )
            )
        )
    )

    #_override
    #_public
    (§ method #_"String" (§ fn toString) [])
    (§ block
        (String/format Locale/US, "Block %s at height %d: %s", (.. this (getHeader) (getHashAsString)), (.. this (getHeight)), (.. this (getHeader) (toString)))
    )
)

#_(ns org.bitcoinj.core #_"StoredUndoableBlock"
    (:import [java.util List]))

;;;
 ; Contains minimal data neccessary to disconnect/connect the transactions
 ; in the stored block at will.  Can either store the full set of
 ; transactions (if the inputs for the block have not been tested to work)
 ; or the set of transaction outputs created/destroyed when the block is
 ; connected.
 ;;
#_public
(§ class #_"StoredUndoableBlock"
    (§ field #_"Sha256Hash" (§ name blockHash))

    ;; Only one of either txOutChanges or transactions will be set.
    #_private
    (§ field- #_"TransactionOutputChanges" (§ name txOutChanges))
    #_private
    (§ field- #_"List<Transaction>" (§ name transactions))

    #_public
    (§ constructor #_"StoredUndoableBlock" [#_"Sha256Hash" __hash, #_"TransactionOutputChanges" __txOutChanges])
    (§ block
        (§ ass (§ name (.. this blockHash)) __hash)
        (§ ass (§ name (.. this transactions)) nil)
        (§ ass (§ name (.. this txOutChanges)) __txOutChanges)
        this
    )

    #_public
    (§ constructor #_"StoredUndoableBlock" [#_"Sha256Hash" __hash, #_"List<Transaction>" __transactions])
    (§ block
        (§ ass (§ name (.. this blockHash)) __hash)
        (§ ass (§ name (.. this txOutChanges)) nil)
        (§ ass (§ name (.. this transactions)) __transactions)
        this
    )

    ;;;
     ; Get the transaction output changes if they have been calculated, otherwise null.
     ; Only one of this and getTransactions() will return a non-null value.
     ;;
    #_public
    (§ method #_"TransactionOutputChanges" (§ fn getTxOutChanges) [])
    (§ block
        txOutChanges
    )

    ;;;
     ; Get the full list of transactions if it is stored, otherwise null.
     ; Only one of this and getTxOutChanges() will return a non-null value.
     ;;
    #_public
    (§ method #_"List<Transaction>" (§ fn getTransactions) [])
    (§ block
        transactions
    )

    ;;;
     ; Get the hash of the represented block.
     ;;
    #_public
    (§ method #_"Sha256Hash" (§ fn getHash) [])
    (§ block
        blockHash
    )

    #_override
    #_public
    (§ method #_"int" (§ fn hashCode) [])
    (§ block
        (.. blockHash (hashCode))
    )

    #_override
    #_public
    (§ method #_"boolean" (§ fn equals) [#_"Object" __o])
    (§ block
        (§ if (== this __o))
        (§ block
            (§ return true)
        )
        (§ if (or (nil? __o) (!= (getClass) (.. __o (getClass)))))
        (§ block
            (§ return false)
        )
        (.. this (getHash) (equals (.. (§ cast #_"StoredUndoableBlock" __o) (getHash))))
    )

    #_override
    #_public
    (§ method #_"String" (§ fn toString) [])
    (§ block
        (str "Undoable Block " blockHash)
    )
)

#_(ns org.bitcoinj.core #_"Transaction"
    (:import [java.io *]
             [java.math BigInteger]
             [java.util *])
    (:import [com.google.common.base Preconditions Strings]
             [com.google.common.collect ImmutableMap]
             [com.google.common.primitives Ints Longs]
             [org.slf4j Logger LoggerFactory]
             [org.spongycastle.crypto.params KeyParameter])
   (:require [org.bitcoinj.core Utils]
             [org.bitcoinj.core.TransactionConfidence ConfidenceType]
             [org.bitcoinj.crypto TransactionSignature]
             [org.bitcoinj.script Script ScriptBuilder ScriptError ScriptOpCodes]
             [org.bitcoinj.signers TransactionSigner]
             [org.bitcoinj.utils ExchangeRate]
             [org.bitcoinj.wallet Wallet]
             [org.bitcoinj.wallet.WalletTransaction Pool]))

;;;
 ; <p>A transaction represents the movement of coins from some addresses to some other addresses.  It can also represent
 ; the minting of new coins.  A Transaction object corresponds to the equivalent in the Bitcoin C++ implementation.</p>
 ;
 ; <p>Transactions are the fundamental atoms of Bitcoin and have many powerful features.  Read
 ; <a href="https://bitcoinj.github.io/working-with-transactions">"Working with transactions"</a> in the
 ; documentation to learn more about how to use this class.</p>
 ;
 ; <p>All Bitcoin transactions are at risk of being reversed, though the risk is much less than with traditional payment
 ; systems.  Transactions have <i>confidence levels</i>, which help you decide whether to trust a transaction or not.
 ; Whether to trust a transaction is something that needs to be decided on a case by case basis - a rule that makes
 ; sense for selling MP3s might not make sense for selling cars, or accepting payments from a family member.  If you
 ; are building a wallet, how to present confidence to your users is something to consider carefully.</p>
 ;
 ; <p>Instances of this class are not safe for use by multiple threads.</p>
 ;;
#_public
(§ class #_"Transaction" (§ extends #_"ChildMessage")
    ;;;
     ; A comparator that can be used to sort transactions by their updateTime field.
     ; The ordering goes from most recent into the past.
     ;;
    #_public
    #_static
    (§ def #_"Comparator<Transaction>" (§ name Transaction/SORT_TX_BY_UPDATE_TIME) (§ new #_"Comparator<Transaction>"
        (§ anon
            #_override
            #_public
            (§ method #_"int" (§ fn compare) [#_"Transaction" __tx1, #_"Transaction" __tx2])
            (§ block
                (§ let #_"long" __time1 (.. __tx1 (getUpdateTime) (getTime)))
                (§ block
                    (§ let #_"long" __time2 (.. __tx2 (getUpdateTime) (getTime)))
                    (§ block
                        (§ let #_"int" __updateTimeComparison (- (Longs/compare __time1, __time2)))
                        (§ block
                            ;; If time1 == time2, compare by tx hash to make comparator consistent with equals.
                            (if (!= __updateTimeComparison 0) __updateTimeComparison (.. __tx1 (getHash) (compareTo (.. __tx2 (getHash)))))
                        )
                    )
                )
            )
        )))

    ;;; A comparator that can be used to sort transactions by their chain height. ;;
    #_public
    #_static
    (§ def #_"Comparator<Transaction>" (§ name Transaction/SORT_TX_BY_HEIGHT) (§ new #_"Comparator<Transaction>"
        (§ anon
            #_override
            #_public
            (§ method #_"int" (§ fn compare) [#_"Transaction" __tx1, #_"Transaction" __tx2])
            (§ block
                (§ let #_"TransactionConfidence" __confidence1 (.. __tx1 (getConfidence)))
                (§ block
                    (§ let #_"int" __height1 (if (== (.. __confidence1 (getConfidenceType)) ConfidenceType/BUILDING) (.. __confidence1 (getAppearedAtChainHeight)) Block/BLOCK_HEIGHT_UNKNOWN))
                    (§ block
                        (§ let #_"TransactionConfidence" __confidence2 (.. __tx2 (getConfidence)))
                        (§ block
                            (§ let #_"int" __height2 (if (== (.. __confidence2 (getConfidenceType)) ConfidenceType/BUILDING) (.. __confidence2 (getAppearedAtChainHeight)) Block/BLOCK_HEIGHT_UNKNOWN))
                            (§ block
                                (§ let #_"int" __heightComparison (- (Ints/compare __height1, __height2)))
                                (§ block
                                    ;; If height1 == height2, compare by tx hash to make comparator consistent with equals.
                                    (if (!= __heightComparison 0) __heightComparison (.. __tx1 (getHash) (compareTo (.. __tx2 (getHash)))))
                                )
                            )
                        )
                    )
                )
            )
        )))

    #_private
    #_static
    (§ def- #_"Logger" (§ name Transaction/log) (LoggerFactory/getLogger (§ klass #_"Transaction")))

    ;;; Threshold for lockTime: below this value it is interpreted as block number, otherwise as timestamp. ;;
    #_public
    #_static
    (§ def #_"int" (§ name Transaction/LOCKTIME_THRESHOLD) 500000000) ;; Tue Nov  5 00:53:20 1985 UTC
    ;;; Same, but as a BigInteger for CHECKLOCKTIMEVERIFY. ;;
    #_public
    #_static
    (§ def #_"BigInteger" (§ name Transaction/LOCKTIME_THRESHOLD_BIG) (BigInteger/valueOf Transaction/LOCKTIME_THRESHOLD))

    ;;; How many bytes a transaction can be before it won't be relayed anymore.  Currently 100kb. ;;
    #_public
    #_static
    (§ def #_"int" (§ name Transaction/MAX_STANDARD_TX_SIZE) 100000)

    ;;;
     ; If feePerKb is lower than this, Bitcoin Core will treat it as if there were no fee.
     ;;
    #_public
    #_static
    (§ def #_"Coin" (§ name Transaction/REFERENCE_DEFAULT_MIN_TX_FEE) (Coin/valueOf 5000)) ;; 0.05 mBTC

    ;;;
     ; If using this feePerKb, transactions will get confirmed within the next couple of blocks.
     ; This should be adjusted from time to time.  Last adjustment: February 2017.
     ;;
    #_public
    #_static
    (§ def #_"Coin" (§ name Transaction/DEFAULT_TX_FEE) (Coin/valueOf 100000)) ;; 1 mBTC

    ;;;
     ; Any standard (i.e. pay-to-address) output smaller than this value (in satoshis) will most likely be rejected by the network.
     ; This is calculated by assuming a standard output will be 34 bytes, and then using the formula used in
     ; {@link TransactionOutput#getMinNonDustValue(Coin)}.
     ;;
    #_public
    #_static
    (§ def #_"Coin" (§ name Transaction/MIN_NONDUST_OUTPUT) (Coin/valueOf 2730)) ;; satoshis

    ;; These are bitcoin serialized.
    #_private
    (§ field- #_"long" (§ name version))
    #_private
    (§ field- #_"ArrayList<TransactionInput>" (§ name inputs))
    #_private
    (§ field- #_"ArrayList<TransactionOutput>" (§ name outputs))

    #_private
    (§ field- #_"long" (§ name lockTime))

    ;; This is either the time the transaction was broadcast as measured from the local clock, or the time from the
    ;; block in which it was included.  Note that this can be changed by re-orgs so the wallet may update this field.
    ;; Old serialized transactions don't have this field, thus null is valid.  It is used for returning an ordered
    ;; list of transactions from a wallet, which is helpful for presenting to users.
    #_private
    (§ field- #_"Date" (§ name updatedAt))

    ;; This is an in memory helper only.
    #_private
    (§ field- #_"Sha256Hash" (§ name hash))

    ;; Data about how confirmed this tx is.  Serialized, may be null.
    #_nilable
    #_private
    (§ field- #_"TransactionConfidence" (§ name confidence))

    ;; Records a map of which blocks the transaction has appeared in (keys) to an index within that block (values).
    ;; The "index" is not a real index, instead the values are only meaningful relative to each other.  For example,
    ;; consider two transactions that appear in the same block, t1 and t2, where t2 spends an output of t1.  Both
    ;; will have the same block hash as a key in their appearsInHashes, but the counter would be 1 and 2 respectively
    ;; regardless of where they actually appeared in the block.
    ;;
    ;; If this transaction is not stored in the wallet, appearsInHashes is null.
    #_private
    (§ field- #_"Map<Sha256Hash, Integer>" (§ name appearsInHashes))

    ;; Transactions can be encoded in a way that will use more bytes than is optimal
    ;; (due to VarInts having multiple encodings).
    ;; MAX_BLOCK_SIZE must be compared to the optimal encoding, not the actual encoding, so when parsing, we keep track
    ;; of the size of the ideal encoding in addition to the actual message size (which Message needs) so that Blocks
    ;; can properly keep track of optimal encoded size.
    #_private
    (§ field- #_"int" (§ name optimalEncodingMessageSize))

    ;;;
