    (§ block
        (.. this (getHash) (toString))
    )

    ;;;
     ; Gets the sum of the inputs, regardless of who owns them.
     ;;
    #_public
    (§ method #_"Coin" (§ fn getInputSum) [])
    (§ block
        (§ let #_"Coin" __inputTotal Coin/ZERO)
        (§ block

            (§ for (§ var #_"TransactionInput" __input) :for inputs)
            (§ block
                (§ let #_"Coin" __inputValue (.. __input (getValue)))
                (§ block
                    (§ if (some? __inputValue))
                    (§ block
                        (§ ass __inputTotal (.. __inputTotal (add __inputValue)))
                    )
                )
            )

            __inputTotal
        )
    )

    ;;;
     ; Calculates the sum of the outputs that are sending coins to a key in the wallet.
     ;;
    #_public
    (§ method #_"Coin" (§ fn getValueSentToMe) [#_"TransactionBag" __transactionBag])
    (§ block
        ;; This is tested in WalletTest.
        (§ let #_"Coin" __v Coin/ZERO)
        (§ block
            (§ for (§ var #_"TransactionOutput" __o) :for outputs)
            (§ block
                (§ if (.. __o (isMine __transactionBag)))
                (§ block
                    (§ ass __v (.. __v (add (.. __o (getValue)))))
                )
            )
            __v
        )
    )

    ;;;
     ; Returns a map of block [hashes] which contain the transaction mapped to relativity counters, or null if this
     ; transaction doesn't have that data because it's not stored in the wallet or because it has never appeared in a
     ; block.
     ;;
    #_nilable
    #_public
    (§ method #_"Map<Sha256Hash, Integer>" (§ fn getAppearsInHashes) [])
    (§ block
        (when (some? appearsInHashes) (ImmutableMap/copyOf appearsInHashes))
    )

    ;;;
     ; Convenience wrapper around getConfidence().getConfidenceType().
     ; @return true if this transaction hasn't been seen in any block yet.
     ;;
    #_public
    (§ method #_"boolean" (§ fn isPending) [])
    (§ block
        (== (.. this (getConfidence) (getConfidenceType)) TransactionConfidence/ConfidenceType/PENDING)
    )

    ;;;
     ; <p>Puts the given block in the internal set of blocks in which this transaction appears.  This is
     ; used by the wallet to ensure transactions that appear on side chains are recorded properly even though
     ; the block stores do not save the transaction data at all.</p>
     ;
     ; <p>If there is a re-org this will be called once for each block that was previously seen, to update which block
     ; is the best chain.  The best chain block is guaranteed to be called last. So this must be idempotent.</p>
     ;
     ; <p>Sets updatedAt to be the earliest valid block time where this tx was seen.</p>
     ;
     ; @param block     The {@link StoredBlock} in which the transaction has appeared.
     ; @param bestChain Whether to set the updatedAt timestamp from the block header (only if not already set).
     ; @param relativityOffset A number that disambiguates the order of transactions within a block.
     ;;
    #_public
    (§ method #_"void" (§ fn setBlockAppearance) [#_"StoredBlock" __block, #_"boolean" __bestChain, #_"int" __relativityOffset])
    (§ block
        (§ let #_"long" __blockTime (* (.. __block (getHeader) (getTimeSeconds)) 1000))
        (§ block
            (§ if (and __bestChain (or (nil? updatedAt) (== (.. updatedAt (getTime)) 0) (< __blockTime (.. updatedAt (getTime))))))
            (§ block
                (§ ass (§ name updatedAt) (§ new #_"Date" __blockTime))
            )

            (.. this (addBlockAppearance (.. __block (getHeader) (getHash)), __relativityOffset))

            (§ if __bestChain)
            (§ block
                (§ let #_"TransactionConfidence" __transactionConfidence (.. this (getConfidence)))
                (§ block
                    ;; This sets type to BUILDING and depth to one.
                    (.. __transactionConfidence (setAppearedAtChainHeight (.. __block (getHeight))))
                )
            )
            nil
        )
    )

    #_public
    (§ method #_"void" (§ fn addBlockAppearance) [#_"Sha256Hash" __blockHash, #_"int" __relativityOffset])
    (§ block
        ;; TODO: This could be a lot more memory efficient as we'll typically only store one element.
        (§ if (nil? appearsInHashes))
        (§ block
            (§ ass (§ name appearsInHashes) (§ new #_"TreeMap<>"))
        )

        (.. appearsInHashes (put __blockHash, __relativityOffset))
        nil
    )

    ;;;
     ; Calculates the sum of the inputs that are spending coins with keys in the wallet.  This requires the
     ; transactions sending coins to those keys to be in the wallet.  This method will not attempt to download
     ; the blocks containing the input transactions if the key is in the wallet but the transactions are not.
     ;
     ; @return sum of the inputs that are spending coins with keys in the wallet.
     ;;
    #_public
    (§ method #_"Coin" (§ fn getValueSentFromMe) [#_"TransactionBag" __wallet])
        (§ throws #_"ScriptException")
    (§ block
        ;; This is tested in WalletTest.
        (§ let #_"Coin" __v Coin/ZERO)
        (§ block
            (§ for (§ var #_"TransactionInput" __input) :for inputs)
            (§ block
                ;; This input is taking value from a transaction in our wallet.  To discover the value,
                ;; we must find the connected transaction.
                (§ let #_"TransactionOutput" __connected (.. __input (getConnectedOutput (.. __wallet (getTransactionPool Pool/UNSPENT)))))
                (§ block
                    (§ if (nil? __connected))
                    (§ block
                        (§ ass __connected (.. __input (getConnectedOutput (.. __wallet (getTransactionPool Pool/SPENT)))))
                    )
                    (§ if (nil? __connected))
                    (§ block
                        (§ ass __connected (.. __input (getConnectedOutput (.. __wallet (getTransactionPool Pool/PENDING)))))
                    )

                    ;; The connected output may be the change to the sender of a previous input sent to this wallet.
                    ;; In this case we ignore it.
                    (§ if (and (some? __connected) (.. __connected (isMine __wallet))))
                    (§ block
                        (§ ass __v (.. __v (add (.. __connected (getValue)))))
                    )
                )
            )
            __v
        )
    )

    ;;;
     ; Gets the sum of the outputs of the transaction.  If the outputs are less than the inputs, it does not count the fee.
     ; @return the sum of the outputs regardless of who owns them.
     ;;
    #_public
    (§ method #_"Coin" (§ fn getOutputSum) [])
    (§ block
        (§ let #_"Coin" __totalOut Coin/ZERO)
        (§ block

            (§ for (§ var #_"TransactionOutput" __output) :for outputs)
            (§ block
                (§ ass __totalOut (.. __totalOut (add (.. __output (getValue)))))
            )

            __totalOut
        )
    )

    #_nilable
    #_private
    (§ field- #_"Coin" (§ name cachedValue))
    #_nilable
    #_private
    (§ field- #_"TransactionBag" (§ name cachedForBag))

    ;;;
     ; Returns the difference of {@link Transaction#getValueSentToMe(TransactionBag)} and {@link Transaction#getValueSentFromMe(TransactionBag)}.
     ;;
    #_public
    (§ method #_"Coin" (§ fn getValue) [#_"TransactionBag" __wallet])
        (§ throws #_"ScriptException")
    (§ block
        ;; FIXME: TEMP PERF HACK FOR ANDROID - this crap can go away once we have a real payments API.
        (§ let #_"boolean" __isAndroid (Utils/isAndroidRuntime))
        (§ block
            (§ if (and __isAndroid (some? cachedValue) (== cachedForBag __wallet)))
            (§ block
                (§ return cachedValue)
            )

            (§ let #_"Coin" __result (.. this (getValueSentToMe __wallet) (subtract (.. this (getValueSentFromMe __wallet)))))
            (§ block
                (§ if __isAndroid)
                (§ block
                    (§ ass (§ name cachedValue) __result)
                    (§ ass (§ name cachedForBag) __wallet)
                )
                __result
            )
        )
    )

    ;;;
     ; The transaction fee is the difference of the value of all inputs and the value of all outputs.
     ; Currently, the fee can only be determined for transactions created by us.
     ;
     ; @return fee, or null if it cannot be determined.
     ;;
    #_public
    (§ method #_"Coin" (§ fn getFee) [])
    (§ block
        (§ let #_"Coin" __fee Coin/ZERO)
        (§ block
            (§ if (or (.. inputs (isEmpty)) (.. outputs (isEmpty)))) ;; Incomplete transaction.
            (§ block
                (§ return nil)
            )

            (§ for (§ var #_"TransactionInput" __input) :for inputs)
            (§ block
                (§ if (== (.. __input (getValue)) nil))
                (§ block
                    (§ return nil)
                )
                (§ ass __fee (.. __fee (add (.. __input (getValue)))))
            )
            (§ for (§ var #_"TransactionOutput" __output) :for outputs)
            (§ block
                (§ ass __fee (.. __fee (subtract (.. __output (getValue)))))
            )
            __fee
        )
    )

    ;;;
     ; Returns true if any of the outputs is marked as spent.
     ;;
    #_public
    (§ method #_"boolean" (§ fn isAnyOutputSpent) [])
    (§ block
        (§ for (§ var #_"TransactionOutput" __output) :for outputs)
        (§ block
            (§ if (not (.. __output (isAvailableForSpending))))
            (§ block
                (§ return true)
            )
        )

        false
    )

    ;;;
     ; Returns false if this transaction has at least one output that is owned by the given wallet and unspent,
     ; true otherwise.
     ;;
    #_public
    (§ method #_"boolean" (§ fn isEveryOwnedOutputSpent) [#_"TransactionBag" __transactionBag])
    (§ block
        (§ for (§ var #_"TransactionOutput" __output) :for outputs)
        (§ block
            (§ if (and (.. __output (isAvailableForSpending)) (.. __output (isMine __transactionBag))))
            (§ block
                (§ return false)
            )
        )

        true
    )

    ;;;
     ; Returns the earliest time at which the transaction was seen (broadcast or included into the chain),
     ; or the epoch if that information isn't available.
     ;;
    #_public
    (§ method #_"Date" (§ fn getUpdateTime) [])
    (§ block
        ;; Older wallets did not store this field.  Set to the epoch.
        (§ if (nil? updatedAt))
        (§ block
            (§ ass (§ name updatedAt) (§ new #_"Date" 0))
        )
        updatedAt
    )

    #_public
    (§ method #_"void" (§ fn setUpdateTime) [#_"Date" __updatedAt])
    (§ block
        (§ ass (§ name (.. this updatedAt)) __updatedAt)
        nil
    )

    ;;;
     ; These constants are a part of a scriptSig signature on the inputs.  They define the details of how a
     ; transaction can be redeemed, specifically, they control how the hash of the transaction is calculated.
     ;;
    #_public
    (§ enum #_"SigHash"
        (§ item (ALL 1))
        (§ item (NONE 2))
        (§ item (SINGLE 3))
        (§ item (ANYONECANPAY 0x80)) ;; Caution: Using this type in isolation is non-standard.  Treated similar to ANYONECANPAY_ALL.
        (§ item (ANYONECANPAY_ALL 0x81))
        (§ item (ANYONECANPAY_NONE 0x82))
        (§ item (ANYONECANPAY_SINGLE 0x83))
        (§ item (UNSET 0)) ;; Caution: Using this type in isolation is non-standard.  Treated similar to ALL.

        #_public
        (§ field #_"int" (§ name value))

        ;;;
         ; @param value
         ;;
        #_private
        (§ constructor- #_"SigHash" [#_"int" __value])
        (§ block
            (§ ass (§ name (.. this value)) __value)
            this
        )

        ;;;
         ; @return the value as a byte.
