        #_public
        (§ method #_"byte" (§ fn byteValue) [])
        (§ block
            (§ cast #_"byte" (.. this value))
        )
    )

    #_override
    #_protected
    (§ method #_"void" (§ fn unCache) [])
    (§ block
        (.. super (unCache))
        (§ ass (§ name hash) nil)
        nil
    )

    #_protected
    #_static
    (§ defn #_"int" (§ fn Transaction/calcLength) [#_"byte[]" __buf, #_"int" __offset])
    (§ block
        ;; jump past version (uint32)
        (§ let #_"int" __cursor (+ __offset 4))
        (§ block

            (§ let #_"VarInt" __varint (§ new #_"VarInt" __buf, __cursor))
            (§ block
                (§ let #_"long" __txInCount (.. __varint value))
                (§ block
                    (§ ass __cursor (+ __cursor (.. __varint (getOriginalSizeInBytes))))

                    (§ for (§ var #_"int" __i 0) :for (< __i __txInCount) :for (§ recur (inc __i)))
                    (§ block
                        ;; 36 = length of previous_outpoint
                        (§ ass __cursor (+ __cursor 36))
                        (§ ass __varint (§ new #_"VarInt" __buf, __cursor))
                        (§ let #_"long" __scriptLen (.. __varint value))
                        (§ block
                            ;; 4 = length of sequence field (unint32)
                            (§ ass __cursor (+ __cursor __scriptLen 4 (.. __varint (getOriginalSizeInBytes))))
                        )
                    )

                    (§ ass __varint (§ new #_"VarInt" __buf, __cursor))
                    (§ let #_"long" __txOutCount (.. __varint value))
                    (§ block
                        (§ ass __cursor (+ __cursor (.. __varint (getOriginalSizeInBytes))))

                        (§ for (§ var #_"int" __i 0) :for (< __i __txOutCount) :for (§ recur (inc __i)))
                        (§ block
                            ;; 8 = length of tx value field (uint64)
                            (§ ass __cursor (+ __cursor 8))
                            (§ ass __varint (§ new #_"VarInt" __buf, __cursor))
                            (§ let #_"long" __scriptLen (.. __varint value))
                            (§ block
                                (§ ass __cursor (+ __cursor __scriptLen (.. __varint (getOriginalSizeInBytes))))
                            )
                        )
                        ;; 4 = length of lock_time field (uint32)
                        (+ (- __cursor __offset) 4)
                    )
                )
            )
        )
    )

    #_override
    #_protected
    (§ method #_"void" (§ fn parse) [])
        (§ throws #_"ProtocolException")
    (§ block
        (§ ass (§ name cursor) offset)

        (§ ass (§ name version) (.. this (readUint32)))
        (§ ass (§ name optimalEncodingMessageSize) 4)

        ;; First come the inputs.
        (§ let #_"long" __numInputs (.. this (readVarInt)))
        (§ block
            (§ ass (§ name optimalEncodingMessageSize) (+ optimalEncodingMessageSize (VarInt/sizeOf __numInputs)))
            (§ ass (§ name inputs) (§ new #_"ArrayList<>" (§ cast #_"int" __numInputs)))
            (§ for (§ var #_"long" __i 0) :for (< __i __numInputs) :for (§ recur (inc __i)))
            (§ block
                (§ let #_"TransactionInput" __input (§ new #_"TransactionInput" params, this, payload, cursor, serializer))
                (§ block
                    (.. inputs (add __input))
                    (§ let #_"long" __scriptLen (.. this (readVarInt TransactionOutPoint/MESSAGE_LENGTH)))
                    (§ block
                        (§ ass (§ name optimalEncodingMessageSize) (+ optimalEncodingMessageSize TransactionOutPoint/MESSAGE_LENGTH (VarInt/sizeOf __scriptLen) __scriptLen 4))
                        (§ ass (§ name cursor) (+ cursor __scriptLen 4))
                    )
                )
            )
            ;; Now the outputs.
            (§ let #_"long" __numOutputs (.. this (readVarInt)))
            (§ block
                (§ ass (§ name optimalEncodingMessageSize) (+ optimalEncodingMessageSize (VarInt/sizeOf __numOutputs)))
                (§ ass (§ name outputs) (§ new #_"ArrayList<>" (§ cast #_"int" __numOutputs)))
                (§ for (§ var #_"long" __i 0) :for (< __i __numOutputs) :for (§ recur (inc __i)))
                (§ block
                    (§ let #_"TransactionOutput" __output (§ new #_"TransactionOutput" params, this, payload, cursor, serializer))
                    (§ block
                        (.. outputs (add __output))
                        (§ let #_"long" __scriptLen (.. this (readVarInt 8)))
                        (§ block
                            (§ ass (§ name optimalEncodingMessageSize) (+ optimalEncodingMessageSize 8 (VarInt/sizeOf __scriptLen) __scriptLen))
                            (§ ass (§ name cursor) (+ cursor __scriptLen))
                        )
                    )
                )
                (§ ass (§ name lockTime) (.. this (readUint32)))
                (§ ass (§ name optimalEncodingMessageSize) (+ optimalEncodingMessageSize 4))
                (§ ass (§ name length) (- cursor offset))
                nil
            )
        )
    )

    #_public
    (§ method #_"int" (§ fn getOptimalEncodingMessageSize) [])
    (§ block
        (§ if (!= optimalEncodingMessageSize 0))
        (§ block
            (§ return optimalEncodingMessageSize)
        )

        (§ ass (§ name optimalEncodingMessageSize) (.. this (getMessageSize)))
        optimalEncodingMessageSize
    )

    ;;;
     ; The priority (coin age) calculation doesn't use the regular message size, but rather one adjusted downwards
     ; for the number of inputs.  The goal is to incentivise cleaning up the UTXO set with free transactions, if one
     ; can do so.
     ;;
    #_public
    (§ method #_"int" (§ fn getMessageSizeForPriorityCalc) [])
    (§ block
        (§ let #_"int" __size (.. this (getMessageSize)))
        (§ block
            (§ for (§ var #_"TransactionInput" __input) :for inputs)
            (§ block
                ;; 41: min size of an input
                ;; 110: enough to cover a compressed pubkey p2sh redemption (somewhat arbitrary)
                (§ let #_"int" __benefit (+ 41 (Math/min 110, (.. __input (getScriptSig) (getProgram) length))))
                (§ block
                    (§ if (< __benefit __size))
                    (§ block
                        (§ ass __size (- __size __benefit))
                    )
                )
            )
            __size
        )
    )

    ;;;
     ; A coinbase transaction is one that creates a new coin.  They are the first transaction in each block and their
     ; value is determined by a formula that all implementations of Bitcoin share.  In 2011 the value of a coinbase
     ; transaction is 50 coins, but in future it will be less.  A coinbase transaction is defined not only by its
     ; position in a block but by the data in the inputs.
     ;;
    #_public
    (§ method #_"boolean" (§ fn isCoinBase) [])
    (§ block
        (and (== (.. inputs (size)) 1) (.. inputs (get 0) (isCoinBase)))
    )

    ;;;
     ; A transaction is mature if it is either a building coinbase tx that is as deep or deeper than the required coinbase depth, or a non-coinbase tx.
     ;;
    #_public
    (§ method #_"boolean" (§ fn isMature) [])
    (§ block
        (§ if (not (.. this (isCoinBase))))
        (§ block
            (§ return true)
        )

