    (§ block
        (§ let #_"int" __n (inc count))
        (§ block
            (§ if (< (.. buf length) __n))
            (§ block
                (§ ass (§ name buf) (Utils/copyOf buf, (Math/max (<< (.. buf length) 1), __n)))
            )
            (§ ass (§ name (§ ai buf count)) (§ cast #_"byte" __b))
            (§ ass (§ name count) __n)
            nil
        )
    )

