     ; Writes <code>len</code> bytes from the specified byte array
     ; starting at offset <code>off</code> to this byte array output stream.
     ;
     ; @param b   The data.
     ; @param off The start offset in the data.
     ; @param len The number of bytes to write.
     ;;
    #_override
    #_public
    (§ method #_"void" (§ fn write) [#_"byte[]" __b, #_"int" __off, #_"int" __len])
    (§ block
        (§ if (or (< __off 0) (< (.. __b length) __off) (< __len 0) (< (.. __b length) (+ __off __len)) (< (+ __off __len) 0)))
        (§ block
            (§ throw (§ new #_"IndexOutOfBoundsException"))
        )

        (§ if (!= __len 0))
        (§ block
            (§ let #_"int" __n (+ count __len))
            (§ block
                (§ if (< (.. buf length) __n))
                (§ block
                    (§ ass (§ name buf) (Utils/copyOf buf, (Math/max (<< (.. buf length) 1), __n)))
                )
                (System/arraycopy __b, __off, buf, count, __len)
                (§ ass (§ name count) __n)
