    ;;;
     ; MPI encoded numbers are produced by the OpenSSL BN_bn2mpi function.  They consist of
     ; a 4 byte big endian length field, followed by the stated number of bytes representing
     ; the number in big endian format (with a sign bit).
     ; @param hasLength can be set to false if the given array is missing the 4 byte length field.
     ;;
    #_public
    #_static
    (§ defn #_"BigInteger" (§ fn Utils/decodeMPI) [#_"byte[]" __mpi, #_"boolean" __hasLength])
    (§ block
        (§ let #_"byte[]" __buf)
        (§ block
            (§ if __hasLength)
            (§ block
                (§ let #_"int" __length (§ cast #_"int" (Utils/readUint32BE __mpi, 0)))
                (§ block
                    (§ ass __buf (§ new #_"byte[]" (§ count __length)))
                    (System/arraycopy __mpi, 4, __buf, 0, __length)
                )
            )
            (§ else )
            (§ block
                (§ ass __buf __mpi)
            )
            (§ if (== (.. __buf length) 0))
            (§ block
                (§ return BigInteger/ZERO)
            )

            (§ let #_"boolean" __isNegative (== (& (§ ai __buf 0) 0x80) 0x80))
            (§ block
                (§ if __isNegative)
                (§ block
                    (§ ass (§ name (§ ai __buf 0)) (& (§ ai __buf 0) 0x7f))
                )
                (§ let #_"BigInteger" __result (§ new #_"BigInteger" __buf))
                (§ block
                    (if __isNegative (.. __result (negate)) __result)
                )
            )
        )
    )

    ;;;
     ; MPI encoded numbers are produced by the OpenSSL BN_bn2mpi function.  They consist of
     ; a 4 byte big endian length field, followed by the stated number of bytes representing
     ; the number in big endian format (with a sign bit).
     ; @param includeLength indicates whether the 4 byte length field should be included.
     ;;
    #_public
    #_static
    (§ defn #_"byte[]" (§ fn Utils/encodeMPI) [#_"BigInteger" __value, #_"boolean" __includeLength])
    (§ block
        (§ if (.. __value (equals BigInteger/ZERO)))
        (§ block
            (§ return (if __includeLength (§ new #_"byte[]" (§ coll 0x00, 0x00, 0x00, 0x00 )) (§ new #_"byte[]")))
        )

        (§ let #_"boolean" __isNegative (< (.. __value (signum)) 0))
        (§ block
            (§ if __isNegative)
            (§ block
                (§ ass __value (.. __value (negate)))
            )
            (§ let #_"byte[]" __array (.. __value (toByteArray)))
            (§ block
                (§ let #_"int" __length (.. __array length))
                (§ block
                    (§ if (== (& (§ ai __array 0) 0x80) 0x80))
                    (§ block
                        (§ ass __length (inc __length))
                    )

                    (§ if __includeLength)
                    (§ block
                        (§ let #_"byte[]" __result (§ new #_"byte[]" (§ count __length + 4)))
                        (§ block
                            (System/arraycopy __array, 0, __result, (+ (- __length (.. __array length)) 3), (.. __array length))
                            (Utils/uint32ToByteArrayBE __length, __result, 0)
                            (§ if __isNegative)
                            (§ block
                                (§ ass (§ name (§ ai __result 4)) (| (§ ai __result 4) 0x80))
                            )
                            (§ return __result)
                        )
                    )
                    (§ else )
                    (§ block
                        (§ let #_"byte[]" __result)
                        (§ block
                            (§ if (!= __length (.. __array length)))
                            (§ block
                                (§ ass __result (§ new #_"byte[]" (§ count __length)))
                                (System/arraycopy __array, 0, __result, 1, (.. __array length))
                            )
                            (§ else )
                            (§ block
                                (§ ass __result __array)
                            )
                            (§ if __isNegative)
                            (§ block
                                (§ ass (§ name (§ ai __result 0)) (| (§ ai __result 0) 0x80))
                            )
                            (§ return __result)
                        )
                    )
                )
            )
        )
    )

    ;;;
     ; <p>The "compact" format is a representation of a whole number N using an unsigned 32 bit number similar to
     ; a floating point format.  The most significant 8 bits are the unsigned exponent of base 256.  This exponent
     ; can be thought of as "number of bytes of N".  The lower 23 bits are the mantissa.  Bit number 24 (0x800000)
     ; represents the sign of N.  Therefore, N = (-1^sign) * mantissa * 256^(exponent-3).</p>
     ;
     ; <p>Satoshi's original implementation used BN_bn2mpi() and BN_mpi2bn().  MPI uses the most significant bit of
     ; the first byte as sign.  Thus 0x1234560000 is compact 0x05123456 and 0xc0de000000 is compact 0x0600c0de.
     ; Compact 0x05c0de00 would be -0x40de000000.</p>
     ;
     ; <p>Bitcoin only uses this "compact" format for encoding difficulty targets, which are unsigned 256bit quantities.
     ; Thus, all the complexities of the sign bit and using base 256 are probably an implementation accident.</p>
     ;;
    #_public
    #_static
    (§ defn #_"BigInteger" (§ fn Utils/decodeCompactBits) [#_"long" __compact])
    (§ block
        (§ let #_"int" __size (& (§ cast #_"int" (>> __compact 24)) 0xff))
        (§ block
            (§ let #_"byte[]" __bytes (§ new #_"byte[]" (§ count 4 + __size)))
            (§ block
                (§ ass (§ name (§ ai __bytes 3)) (§ cast #_"byte" __size))
                (§ if (<= 1 __size))
                (§ block
                    (§ ass (§ name (§ ai __bytes 4)) (§ cast #_"byte" (& (>> __compact 16) 0xff)))
                )
                (§ if (<= 2 __size))
                (§ block
                    (§ ass (§ name (§ ai __bytes 5)) (§ cast #_"byte" (& (>> __compact 8) 0xff)))
                )
                (§ if (<= 3 __size))
                (§ block
                    (§ ass (§ name (§ ai __bytes 6)) (§ cast #_"byte" (& __compact 0xff)))
                )
                (Utils/decodeMPI __bytes, true)
            )
        )
    )

    ;;;
     ; @see Utils#decodeCompactBits(long)
     ;;
    #_public
    #_static
    (§ defn #_"long" (§ fn Utils/encodeCompactBits) [#_"BigInteger" __value])
    (§ block
        (§ let #_"long" __result)
        (§ block
            (§ let #_"int" __size (.. __value (toByteArray) length))
            (§ block
                (§ if (<= __size 3))
                (§ block
                    (§ ass __result (<< (.. __value (longValue)) (* 8 (- 3 __size))))
                )
                (§ else )
                (§ block
                    (§ ass __result (.. __value (shiftRight (* 8 (- __size 3))) (longValue)))
                )
                ;; The 0x00800000 bit denotes the sign.
                ;; Thus, if it is already set, divide the mantissa by 256 and increase the exponent.
                (§ if (!= (& __result 0x00800000) 0))
                (§ block
                    (§ ass __result (>> __result 8))
                    (§ ass __size (inc __size))
                )
                (§ ass __result (| __result (<< __size 24)))
                (§ ass __result (| __result (if (== (.. __value (signum)) -1) 0x00800000 0)))
                __result
            )
        )
    )

