    #_public
    #_static
    (§ defn #_"int" (§ fn Utils/maxOfMostFreq) [#_"int..." __items])
    (§ block
        ;; Java 6 sucks.
        (§ let #_"ArrayList<Integer>" __list (§ new #_"ArrayList<>" (.. __items length)))
        (§ block
            (§ for (§ var #_"int" __item) :for __items)
