    #_public
    #_static
    (§ defn #_"int" (§ fn Utils/maxOfMostFreq) [#_"List<Integer>" __items])
    (§ block
        (§ if (.. __items (isEmpty)))
        (§ block
            (§ return 0)
        )

        ;; This would be much easier in a functional language (or in Java 8).
        (§ ass __items (.. (Ordering/natural) (reverse) (sortedCopy __items)))
        (§ let #_"LinkedList<Pair>" __pairs (Lists/newLinkedList))
        (§ block
            (.. __pairs (add (§ new #_"Pair" (.. __items (get 0)), 0)))
            (§ for (§ var #_"int" __item) :for __items)
            (§ block
                (§ let #_"Pair" __pair (.. __pairs (getLast)))
                (§ block
                    (§ if (!= (.. __pair item) __item))
                    (§ block
                        (.. __pairs (add (§ ass __pair (§ new #_"Pair" __item, 0))))
                    )
                    (§ ass (§ name (.. __pair count)) (inc (.. __pair count)))
                )
            )
            ;; pairs now contains a uniqified list of the sorted inputs, with counts for how often that item appeared.
            ;; Now sort by how frequently they occur, and pick the max of the most frequent.
            (Collections/sort __pairs)
            (§ let #_"int" __maxCount (.. __pairs (getFirst) count))
            (§ block
                (§ let #_"int" __maxItem (.. __pairs (getFirst) item))
                (§ block
                    (§ for (§ var #_"Pair" __pair) :for __pairs)
                    (§ block
                        (§ if (!= (.. __pair count) __maxCount))
                        (§ block
                            (§ break )
                        )
                        (§ ass __maxItem (Math/max __maxItem, (.. __pair item)))
                    )
                    __maxItem
                )
            )
        )
    )

