    (§ field #_"long" (§ name value))
    #_private
    (§ field- #_"int" (§ name originallyEncodedSize))

    ;;;
     ; Constructs a new VarInt with the given unsigned long value.
     ;
     ; @param value the unsigned long value (beware widening conversion of negatives!)
     ;;
    #_public
    (§ constructor #_"VarInt" [#_"long" __value])
    (§ block
        (§ ass (§ name (.. this value)) __value)
        (§ ass (§ name originallyEncodedSize) (.. this (getSizeInBytes)))
        this
    )

    ;;;
     ; Constructs a new VarInt with the value parsed from the specified offset of the given buffer.
     ;
     ; @param buf The buffer containing the value.
     ; @param offset The offset of the value.
     ;;
    #_public
    (§ constructor #_"VarInt" [#_"byte[]" __buf, #_"int" __offset])
    (§ block
        (§ let #_"int" __first (& 0xff (§ ai __buf __offset)))
        (§ block
            (§ if (< __first 253))
            (§ block
                (§ ass (§ name value) __first)
                (§ ass (§ name originallyEncodedSize) 1) ;; 1 data byte (8 bits)
            )
            (§ elseif (== __first 253))
            (§ block
                (§ ass (§ name value) (| (& 0xff (§ ai __buf (inc __offset))) (<< (& 0xff (§ ai __buf (+ __offset 2))) 8)))
                (§ ass (§ name originallyEncodedSize) 3) ;; 1 marker + 2 data bytes (16 bits)
            )
            (§ elseif (== __first 254))
            (§ block
                (§ ass (§ name value) (Utils/readUint32 __buf, (inc __offset)))
                (§ ass (§ name originallyEncodedSize) 5) ;; 1 marker + 4 data bytes (32 bits)
            )
            (§ else )
            (§ block
                (§ ass (§ name value) (Utils/readInt64 __buf, (inc __offset)))
                (§ ass (§ name originallyEncodedSize) 9) ;; 1 marker + 8 data bytes (64 bits)
            )
            this
        )
    )

    ;;;
     ; Returns the original number of bytes used to encode the value if it was
     ; deserialized from a byte array, or the minimum encoded size if it was not.
     ;;
    #_public
    (§ method #_"int" (§ fn getOriginalSizeInBytes) [])
    (§ block
        originallyEncodedSize
    )

    ;;;
     ; Returns the minimum encoded size of the value.
     ;;
    #_public
    (§ method #_"int" (§ fn getSizeInBytes) [])
    (§ block
        (VarInt/sizeOf value)
    )

    ;;;
     ; Returns the minimum encoded size of the given unsigned long value.
     ;
     ; @param value the unsigned long value (beware widening conversion of negatives!)
     ;;
    #_public
    #_static
    (§ defn #_"int" (§ fn VarInt/sizeOf) [#_"long" __value])
    (§ block
        ;; if negative, it's actually a very large unsigned long value
        (§ if (< __value 0))
        (§ block
            (§ return 9) ;; 1 marker + 8 data bytes
        )
        (§ if (< __value 253))
        (§ block
            (§ return 1) ;; 1 data byte
        )
        (§ if (<= __value 0xffff))
        (§ block
            (§ return 3) ;; 1 marker + 2 data bytes
        )
        (§ if (<= __value 0xffffffff))
        (§ block
            (§ return 5) ;; 1 marker + 4 data bytes
        )
        9 ;; 1 marker + 8 data bytes
    )

    ;;;
     ; Encodes the value into its minimal representation.
     ;
     ; @return the minimal encoded bytes of the value.
     ;;
    #_public
    (§ method #_"byte[]" (§ fn encode) [])
    (§ block
        (§ let #_"byte[]" __bytes)
        (§ block
            (§ switch (VarInt/sizeOf value))
            (§ block
                (§ case 1)
                (§ block
                    (§ return (§ new #_"byte[]" (§ coll (§ cast #_"byte" value) )))
                )
                (§ case 3)
                (§ block
                    (§ return (§ new #_"byte[]" (§ coll (§ cast #_"byte" 253), (§ cast #_"byte" value), (§ cast #_"byte" (>> value 8)) )))
                )
                (§ case 5)
                (§ block
                    (§ ass __bytes (§ new #_"byte[]" (§ count 5)))
                    (§ ass (§ name (§ ai __bytes 0)) (§ cast #_"byte" 254))
                    (Utils/uint32ToByteArrayLE value, __bytes, 1)
                    (§ return __bytes)
                )
                (§ default )
                (§ block
                    (§ ass __bytes (§ new #_"byte[]" (§ count 9)))
                    (§ ass (§ name (§ ai __bytes 0)) (§ cast #_"byte" 255))
                    (Utils/uint64ToByteArrayLE value, __bytes, 1)
                    (§ return __bytes)
                )
            )
        )
    )
)

